Park.create!([
  {name: "277 North Campground", city: "f", totalsites: "18", firstcome: "17", reservable: "1", fees: "6.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/amis/planyourvisit/277-north-campground.htm", description: "277 North Campground is generally open year-round and rarely fills up, except possibly on holiday weekends. A boat launch ramp off the campground may be available depending on lake levels. 277 North offers 17 primitive sites, which are first-come, first-served only. Each site has a covered picnic table and a BBQ grill. A group site is available with reservation only. No ground fires are allowed. The sites are suitable for tents or RVs. Vault toilets are available. No drinking water or hookups are available.", latitude: 29.512373695509215, longitude: -100.90816633365614},
  {name: "Abrams Creek Campground", city: "Tallassee", totalsites: "16", firstcome: "0", reservable: "16", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Abrams Creek Campground—elevation 1,125 feet (~343 m)—is located beside beautiful Abrams Creek in a remote and secluded setting. The climate is moderate, characterized by mild winters and hot, humid summers. Flush toilets and drinking water are available. No hookups or showers are available at the campground.", latitude: 35.61074664664336, longitude: -83.93318327404131},
  {name: "Adirondack Shelters", city: "Thurmont", totalsites: "2", firstcome: "0", reservable: "2", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/233321?q=Adirondack%20Shelters", description: "Reservations Required. No Walk Ins. There are two shelters that offer the bare minimum with regard to outdoor accommodation. Available year-round, the Adirondack Shelters located near the northern most point of the park, provide a destination for true nature immersion and an alternative to drive-up camping. A moderate three-mile uphill hike over rocky and gently rolling terrain starting at Camp Round Meadow will lead you to these three-sided shelters.", latitude: 39.67775041640082, longitude: -77.48462720159597},
  {name: "Afterbay Campground", city: "Fort Smith", totalsites: "22", firstcome: "0", reservable: "22", fees: "35.00", showers: "None", toilets: "Composting Toilets - year round", phone: "No", url: "https://www.nps.gov/bica/planyourvisit/afterbay-campground.htm", description: "- Near Fort Smith, Montana - Open All Year - $35 per night for full utility sites - $18 per night for non-utility/tent camping sites - 22 RV and tent sites are located on the south shore of Afterbay lake - All sites have fire rings and picnic tables - Composting vault toilets - All sites are back in (not pull through) - All sites have electric and water - RV dump station available across the road near the Headquarters building", latitude: 45.315438210045016, longitude: -107.94165891102676},
  {name: "Akers Group Campground", city: "Eminence", totalsites: "4", firstcome: "4", reservable: "4", fees: "65.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/ozar/planyourvisit/akers-campground.htm", description: "Group campsites, located at Akers Ferry. The campground is located north of the junction of Highway KK and Highway K.", latitude: 37.3764551871, longitude: -91.5615463252},
  {name: "Alamo Canyon Campground", city: "Ajo", totalsites: "4", firstcome: "0", reservable: "4", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/orpi/planyourvisit/alamo-canyon.htm", description: "Alamo Canyon Campground is a secluded, four-site campground nestled against rocky mountains at the mouth of Alamo Canyon. This primitive campground features picnic tables, grills, a tent area at each site, and a vault toilet located near bulletin boards. It is only for tent camping or small car camping; no RVs, trailers or generators are allowed.", latitude: 32.072943, longitude: -112.728093},
  {name: "Alley Spring Campground", city: "Eminence", totalsites: "162", firstcome: "62", reservable: "102", fees: "30.00", showers: "Free - Seasonal", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/ozar/planyourvisit/alley-spring-campground.htm", description: "Campground located near Alley Spring, 6 miles west of Eminence, MO on State Highway 106.", latitude: 37.148223, longitude: -91.44976700000001},
  {name: "Alum Ford Campground", city: "Whitley City", totalsites: "6", firstcome: "6", reservable: "0", fees: "5.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/biso/planyourvisit/alumfordcampground.htm", description: "Alum Ford is a primitive campground and offers six campsites with a vault toilet facility. No drinking water is located at this area. Alum Ford also has a boat ramp. There are no fees to use the ramp.", latitude: 36.764033745775, longitude: -84.5463375728999},
  {name: "American Memorial Park", city: "Saipan", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "There are no campgrounds in the park.", latitude: 0.0, longitude: 0.0},
  {name: "Anacapa Island Campground", city: "Anacapa Island", totalsites: "7", firstcome: "0", reservable: "7", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/anacapa-island/r/campgroundDetails.do?contractCode=NRSO&parkId=70984", description: "Primitive camping is available (seven sites; $15 per night per site; reservations required). Picnic table, food storage box, and pit toilet are provided. No water is available. Distance from landing to campground is one-half mile and includes a 157-stair climb. WARNING: Western gulls nest on Anacapa Island From April through mid-August. During this time, visitors will encounter seabird rookery conditions: guano, strong odor, constant noise, bird carcasses, and birds protecting their territory.", latitude: 34.0143426535482, longitude: -119.367763068282},
  {name: "Antelope Point RV Park", city: "f", totalsites: "104", firstcome: "0", reservable: "104", fees: "f", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://antelopepointlakepowell.com/explore-lake-powell/rv-park/", description: "While the Antelope Point RV park is not physically within the boundaries of Glen Canyon National Recreation Area, it is adjacent to the Antelope Point Marina, restaurant, gift shop. This is an RV site only. 104 full hook-up spaces, 15 pull-through spaces. Maximum Length - 70ft. 2 RV dump stations", latitude: 36.956030575674106, longitude: -111.43123626708984},
  {name: "Antietam Creek Campground", city: "f", totalsites: "20", firstcome: "0", reservable: "20", fees: "20.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/252970?tab=info", description: "Mile 69.4 (Washington County, MD) - The campground has 20 single campsites each with a picnic table, fire ring and grill. Each campsite will accommodate up to eight campers. Pit toilets are located between campsite #6 and campsite #7 adjacent to the towpath. A second set of chemical toilets are available down the towpath. Potable water is along the towpath at pumps (available mid-April to mid-November). No vehicular traffic is allowed on the towpath and access to the campsites is by foot only.", latitude: 39.42110917600782, longitude: -77.74884282038619},
  {name: "Apgar Campground", city: "West Glacier", totalsites: "194", firstcome: "0", reservable: "188", fees: "23.00", showers: "Free - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Apgar campground is the largest campground in the park. It is situated in trees and provides tent and RV campers with shade and some privacy. Evening sunsets on Lake McDonald are only a short stroll, and you won't want to miss evening programs with a ranger at the Apgar Amphitheater. Many trails are located within a short drive of the campground. Advanced reservations are required for all sites. Click the reservation button below for details.", latitude: 48.5260810439, longitude: -113.981860185},
  {name: "Army Camp", city: "Prince", totalsites: "11", firstcome: "11", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - year round", phone: "Yes - year round", url: "", description: "This campground offers both tent and RV campsites along the banks of the New River at one of its many horseshoe bends. There are 11 drive-in sites for RVs, campers, and tents. Only small (less than 25 feet) and medium (22 - 31.5 feet) RVs or campers are recommended. Large RVs or campers are not recommended. Some sites offer partial shade; others are located in the open with no shade. Camp only at designated sites. RV, camper, or car camping in non-designated sites is strictly prohibited.", latitude: 37.85814480804737, longitude: -81.09902510804346},
  {name: "Aspenglen Campground", city: "Estes Park", totalsites: "52", firstcome: "0", reservable: "54", fees: "35.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/romo/planyourvisit/agcg.htm", description: "Aspenglen Campground is closed. Aspenglen Campground is a seasonal campground open during the summer season.", latitude: 40.39934770583215, longitude: -105.59305451637942},
  {name: "Atwell Mill Campground", city: "Sequoia National Park", totalsites: "21", firstcome: "21", reservable: "21", fees: "28.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/atwell.htm", description: "All sites are reservation only. 𝗢𝗽𝗲𝗻𝗶𝗻𝗴 𝗗𝗮𝘁𝗲: Not currently accepting reservations for summer 2023, due to winter storm damage. Typically, reservations would begin on April 24, 2023, on a one-month rolling basis. The campground is situated along the East Fork of the Kaweah River. There are limited services at Silver City Resort, 1.7 miles (3 km) east of the campground. More services can be found in Three Rivers, 23 miles (37 km) west of Atwell Mill Campground (approximately 1.5 hours away).", latitude: 36.46466811721671, longitude: -118.66967218821418},
  {name: "Au Sable East Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: nil, toilets: nil, phone: "", url: "", description: "Au Sable Point East Group Campground is 0.2 miles west of the Au Sable Light Station and inland from the North Country Trail. The group campsite is surrounded by a coniferous forest. There is access to Lake Superior with a view of the Grand Sable Dunes. One shared fire ring and pit toilet. Water source is Lake Superior. Lake Superior access for kayakers.", latitude: 46.670375, longitude: -86.13682},
  {name: "Au Sable East Individual Backpacking Sites", city: "f", totalsites: "6", firstcome: "0", reservable: "6", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Au Sable Point East Campground is next to the Au Sable Point East Group Campground. Due to the dense forest, you are unable to see the neighboring campground. The six campsites are scattered throughout the trees. Shared fire ring and pit toilet. Water source is Lake Superior. Lake Superior access for kayakers", latitude: 46.669327, longitude: -86.135215},
  {name: "Avalanche", city: "West Glacier", totalsites: "87", firstcome: "0", reservable: "87", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Avalanche campground in located in one of the most popular sections of Glacier National Park west of the Continental Divide. The campground accommodates tent and RV campers, however only 50 sites will accommodate vehicle lengths up to 26 feet. Bring your hiking boots and binoculars. Two popular day hikes offer spectacular scenery and glimpses of birds and wildlife that inhabit the area. Advanced reservations are required for all sites. Check Recreation.gov for details.", latitude: 48.67654586358507, longitude: -113.81852507945598},
  {name: "Azalea Campground", city: "Grant Grove", totalsites: "110", firstcome: "0", reservable: "110", fees: "28.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/seki/planyourvisit/azalea.htm", description: "Open year-round except when weather or safety conditions require a closure. All sites are reservation only except during the winter. Reservations can be made up to four months in advance. Only twenty sites are open during winter months.", latitude: 36.74133435099185, longitude: -118.96777677730648},
  {name: "Back Country Camping - Green Run", city: "Berlin", totalsites: "1", firstcome: "1", reservable: "1", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/backcountry-camping.htm", description: "Bayside back country site", latitude: 38.0799522999743, longitude: -75.2135101005224},
  {name: "Back Country Camping - Little Levels", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/backcountry-camping.htm", description: "Back Country Camping, bayside", latitude: 38.1521378003843, longitude: -75.1727157999991},
  {name: "Back Country Camping - Pine Tree", city: "Berlin", totalsites: "1", firstcome: "1", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/backcountry-camping.htm", description: "Bay side back country campsite", latitude: 38.1432381002, longitude: -75.1883865003},
  {name: "Back Country Camping - Pope Bay", city: "Berlin", totalsites: "1", firstcome: "1", reservable: "1", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/backcountry-camping.htm", description: "Bay side back country campsite", latitude: 38.0477905997415, longitude: -75.2341248002154},
  {name: "Back Country Camping - State Line", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/backcountry-camping.htm", description: "Back Country Camping; ocean side", latitude: 38.04472, longitude: -75.23139},
  {name: "Back Country Camping - Tingles Island", city: "Berlin", totalsites: "1", firstcome: "1", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/backcountry-camping.htm", description: "Bayside back country site", latitude: 38.1777120008, longitude: -75.1754320001},
  {name: "Backcountry Camping", city: "Alamogordo", totalsites: "10", firstcome: "10", reservable: "0", fees: "3.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/whsa/planyourvisit/backpackingpr.htm", description: "Due to rehabilitation of camping sites, backcountry camping is closed.", latitude: 32.809869, longitude: -106.264225},
  {name: "Backcountry Camping", city: "Torrey", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "A free backcountry permit, available at the visitor center, is required for camping outside of campgrounds. Capitol Reef offers many hiking options for serious backpackers and those who enjoy exploring remote areas. For more information on possible backpacking routes, route descriptions and maps are available on our hiking and backpacking page.", latitude: 0.0, longitude: 0.0},
  {name: "Backcountry Camping in the Otis Pike High Dune Wilderness", city: "f", totalsites: "36", firstcome: "36", reservable: "36", fees: "20.00", showers: "None", toilets: "No Toilets", phone: "No", url: "", description: "Under the stars, near the sound of the surf, waking to a chorus of song birds, camping on Fire Island within the Otis Pike Fire Island High Dune Wilderness can be a memorable experience. This wilderness camping experience is available by permit only. Permits are limited and issued for specific nights and number of persons within the group. A maximum length of stay is three consecutive nights. Beach camping may be available from March 15 through Labor Day.", latitude: 40.6961609, longitude: -72.9719285},
  {name: "Backpacking Campsites in the Dunes or Forest", city: "Mosca", totalsites: "27", firstcome: "0", reservable: "27", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/grsa/planyourvisit/backpacking.htm", description: "The most popular and unique backpacking option is camping in the 30-square-mile dunefield, beyond the major dune ridgeline (about 1.5 mile hike minimum over dunes). Enjoy wide open views of the starry sky or a bright moonlit night. Designated sites are also available in the trees of the foothills, or camping is available in higher forests of Great Sand Dunes National Preserve. Reserve national park backpacking permits for the dunes or foothills through recreation.gov", latitude: 37.77724, longitude: -105.535601},
  {name: "Baker Creek Campground", city: "f", totalsites: "37", firstcome: "37", reservable: "0", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grba/planyourvisit/campgrounds.htm", description: "Baker Creek Campground contains 37 campsites. All sites are available, when the campground is open (seasons permitting), on a first-come-first-served basis. Potable water is not available.", latitude: 38.98573, longitude: -114.24331},
  {name: "Bald Eagle Island Hiker-Biker Campsite", city: "Lander", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 50.3 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.299834, longitude: -77.557168},
  {name: "Bugbee Canyon", city: "Fritch", totalsites: "10", firstcome: "10", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "This area is located near the Bugbee community off FM 3395. Shoreline fishing is popular and there are a number of shoreline birds to view. There is no drinking water or flush toilets; vault-evaporator toilets only.", latitude: 35.7248098991, longitude: -101.5901470184},
  {name: "Balsam Mountain Campground", city: "Gatlinburg", totalsites: "43", firstcome: "0", reservable: "43", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grsm/planyourvisit/balsam-mountain.htm", description: "Balsam Mountain Campground—elevation 5,310 feet (~1,618 m)—is surrounded by mountain ranges and streams. Summer daytime temperatures rarely exceed 70°F and lows may dip into the 50s. RV and tent sites plus flush toilets and drinking water are available. No hookups or showers and no lights in the restrooms.", latitude: 35.56543624633261, longitude: -83.17429877302192},
  {name: "Bandy Creek Campground", city: "Oneida", totalsites: "149", firstcome: "149", reservable: "146", fees: "32.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/biso/planyourvisit/bandycreekcampground.htm", description: "The campground offers a total of 181 campsites: 96 trailer sites which offer water and electric hook-ups, 49 sites for tent camping, and two group camping loops with 19 sites in one loop and 16 sites in the other. All campsites include picnic tables, fire rings, and access to restrooms and showers. A dump station is located near the registration kiosk. There are no sewer hook-ups at individual trailer sites. The group campsites also have their own cook shelters and campfire circles.", latitude: 36.4886610474, longitude: -84.6971229739},
  {name: "Barry's Landing & Trail Creek Campground", city: "Lovell", totalsites: "30", firstcome: "30", reservable: "1", fees: "18.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/bica/planyourvisit/trail-creek-campground.htm", description: "Barry's Landing & Trail Creek campgrounds are located on the south district near Barry's Landing, 27 miles north of Lovell, WY on HWY 37. - Open All Year - $18 per night during the summer season (Mid April through October) - 14 RV sites, 16 tent sites - Most RV sites are small for a 28 ft RV or smaller - Vault toilets only, no running water or electricity", latitude: 45.10441282728944, longitude: -108.22557108412536},
  {name: "Bartlett Cove Campground", city: "Bartlett Cove", totalsites: "33", firstcome: "33", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - year round", phone: "No", url: "", description: "The National Park Service maintains a free walk-in campground in Bartlett Cove. The campground features bear-proof food caches, a fire-pit and warming shelter (with firewood provided) located in a very scenic setting along the shore, 1/4 mile south of the Bartlett Cove dock. Stays are limited to 14 days", latitude: 58.450780888, longitude: -135.894400288},
  {name: "Bay Campground - South Manitou Island", city: "Empire", totalsites: "28", firstcome: "25", reservable: "3", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/slbe/planyourvisit/smibaycamp.htm", description: "The Bay Campground is the closest campground to the dock. Bring water filtration equipment because there is no source of purified drinking water. There are 25 individual sites and 3 group sites.", latitude: 45.018882, longitude: -86.102772},
  {name: "Bayside Drive-in Campground", city: "Berlin", totalsites: "22", firstcome: "0", reservable: "22", fees: "40.00", showers: "Cold- Seasonal", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/bayside-drive-in-campground-reservation.htm", description: "Tents, trailers and recreational vehicles (no hookups). Sites have a picnic table and a fire ring. Sites are $40 per night. No generators allowed in Loop B. Reservations are required from March 15 through November 15, and are available for November 16 through March 14.", latitude: 38.207737100574306, longitude: -75.1593790997531},
  {name: "Bear Creek Horse Camp", city: "Strunk", totalsites: "23", firstcome: "22", reservable: "22", fees: "28.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/biso/planyourvisit/horsecamps.htm", description: "This campground contains 23 developed campsites. Each site offers water and electricity, tables, grills, tie-outs for 4 horses, access to modern restrooms with hot showers, a dump station and access miles of horse trails. Bear Creek Horse Camp is located on Bear Creek Road off Hwy. 742, west of Stearns, Kentucky. The fee rate per night is $28.00 (half price for holders of the Interagency Access/Senior Passes).", latitude: 36.6423697456121, longitude: -84.5243896745863},
  {name: "Bear Island Campground", city: "Ochopee", totalsites: "40", firstcome: "0", reservable: "40", fees: "10.00", showers: nil, toilets: nil, phone: "", url: "", description: "Primitive campground with no water. Vault toilets available. Forty designated sites. Access to the Bear Island Campground is at the end of a 20-mile secondary gravel road", latitude: 26.183239748193, longitude: -81.247475274331},
  {name: "Beaver Creek Individual Backpacking Sites", city: "f", totalsites: "6", firstcome: "0", reservable: "6", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Beaver Creek Campground is on the bluff above Lake Superior and Beaver Creek in the Beaver Basin Wilderness. The six campsites are dispersed throughout the sandy grounds, shaded by pine trees. There is one communal fire ring with a view of Lake Superior. Shared pit toilet. Water source is Lake Superior and Beaver Creek. Lake Superior access for kayakers.", latitude: 46.577012, longitude: -86.350163},
  {name: "Beaver Island Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Located in the center of Washington Harbor, near Windigo. All shelters are water facing. Boats can stay at the dock overnight. Stay Limit: 3 nights effective from 6/1 - Labor Day annually. Shelters: 3 Depth at dock (normal conditions): 2'-5' Access: Canoe/kayak/private boat", latitude: 47.90406144441624, longitude: -89.1743364694965},
  {name: "Beehives Campground", city: "Page", totalsites: "6", firstcome: "6", reservable: "0", fees: "14.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/glca/planyourvisit/camping.htm", description: "No reservations. $14 per night. 6 designated sites. Across the highway from Wahweap South Entrance. Picnic table at each site. No hookups, dump station, or restrooms. Pack it in, pack it out. Portable toilets required. No campfires or glass containers. Three night camping limit.", latitude: 36.938243, longitude: -111.496291},
  {name: "Belle Campground", city: "Twentynine Palms", totalsites: "18", firstcome: "18", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/belle-campground.htm", description: "This small (18 site) campground is at an elevation of 3,800 ft (1,158 m). First-come, first-served. Pit toilets. No water so bring plenty of your own. Belle is a great campground to see dark night skies. When you arrive, look for an open, vacated site. Move into an open site and set up camp. Then proceed as soon as possible to an entrance station to complete registration and pay. If arriving after entrance stations close, payment may be delayed until morning.", latitude: 34.00193734907302, longitude: -116.01976203151656},
  {name: "Belle Fourche River Campground", city: "Devils Tower", totalsites: "46", firstcome: "46", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/deto/planyourvisit/campgrounds.htm", description: "The first come, first served campground (no reservations) Belle Fourche River Campground is a 2-loop, 46-site campground (43 pull-through sites with room for RVs up to 35') with 4 ADA sites and 3 tent-only group sites. Drinking water is available at water spigots and in the restrooms. No hookups are available. Large cottonwood trees provide shade. A picnic shelter and tables are available north of the campground. 14-day limit on occupancy.", latitude: 44.58183403582838, longitude: -104.70634354706146},
  {name: "Belle Isle Campground", city: "Houghton", totalsites: "7", firstcome: "7", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "A campground located on Belle Isle, in Belle Harbor, on the northeast end of Isle Royale National Park. Boats can use dock for overnight stays. Stay Limit: 5 nights, effective 6/1 – Labor Day. Shelters: 6 Depth at dock (normal conditions): 13’ Access: Canoe/kayak/private boat", latitude: 48.15235177620953, longitude: -88.58598760529463},
  {name: "Burns Lake", city: "ochopee", totalsites: "0", firstcome: "0", reservable: "15", fees: "24.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "Primitive camping with no water. Vault toilets available. This site provides day use picnic area and backcountry access parking. The campground accommodates 15 designated RV/tent sites.", latitude: 25.8931188486191, longitude: -81.2297696743782},
  {name: "Benchmark Individual Backpacking Sites", city: "f", totalsites: "6", firstcome: "0", reservable: "6", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Benchmark Campground is on a sandy bluff above Lake Superior. The six campsites are sandy and open. There are social trails that lead down to Lake Superior, but it is a steep climb back to your campsite. When hiking from the west, walk through Twelvemile Beach Campground to the trailhead and day use area to connect back to the North Country Trail. Two shared fire rings and a pit toilet. Water source is Lake Superior. Lake Superior access for kayakers.", latitude: 46.647014, longitude: -86.200461},
  {name: "Bicentennial Campground", city: "Sausalito", totalsites: "3", firstcome: "0", reservable: "3", fees: "25.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/272229", description: "Bicentennial Campground is the easiest to reach campground in Golden Gate, approximately 100 yards from the parking area near Battery Wallace. The campground is a great location for those wanting to camp near the city. There is a moderate short hill to climb to/from the campground from the parking area. It is downhill to the site and uphill back to the parking area. Open Year Round. $25.00/per campsite per night", latitude: 37.824572114428165, longitude: -122.52672546997377},
  {name: "Big Creek Campground", city: "Newport", totalsites: "12", firstcome: "0", reservable: "12", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grsm/planyourvisit/camping-at-big-creek.htm", description: "Big Creek Campground—elevation 1,700 feet (~518 m)—is located beside beautiful Big Creek in a remote and secluded setting. The climate is moderate, characterized by mild winters and hot, humid summers. Tent only camping, flush toilets, and drinking water are available. No hookups or showers at the campground.", latitude: 35.75007764619811, longitude: -83.11138107344533},
  {name: "Big Meadows Campground", city: "Robertson", totalsites: "221", firstcome: "0", reservable: "220", fees: "75.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/shen/planyourvisit/big-meadows-campground.htm", description: "Big Meadows Campground (mile 51.2) is centrally-located in Shenandoah National Park, providing easy access to many of the most popular destinations in the Park, including Big Meadows, Dark Hollow Falls, and Byrd Visitor Center. All sites include a place for a tent or RV, a fire ring, and a picnic table. All sites at Big Meadows Campground are by reservation only.", latitude: 38.5279623, longitude: -78.4369404},
  {name: "Big Seboeis Campsite", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "Backcountry campsite along the scenic East Branch of the Penobscot River.", latitude: 45.932131, longitude: -68.635802},
  {name: "Big Spring Brook Campsite", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "Primitive backcountry campsite along the East Branch of the Penobscot River that is only accessible by boat.", latitude: 46.0287936106, longitude: -68.7256877275},
  {name: "Big Spring Brook Hut", city: "Patten", totalsites: "1", firstcome: "0", reservable: "1", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "A beautiful cabin tucked away in the heart of the forests of Katahdin Woods and Waters National Monument along Big Spring Brook. Available for overnight stays in the winter by reservation, the hut is reached by cross-country skiing or snowshoeing. Reservations required through Recreation.gov. A great place to keep warm, find solitude, and enjoy this winter wonderland!", latitude: 46.0341848574, longitude: -68.7742320868},
  {name: "Big Spring Campground", city: "Van Buren", totalsites: "123", firstcome: "56", reservable: "54", fees: "30.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/ozar/planyourvisit/big-spring-campground-map11.htm", description: "The Big Spring Campground is located near Big Spring, south of Van Buren, Missouri. Electric and non-electric sites are available at Big Spring year-round. Some sites are reservable and others are available on a first come first serve basis. Sites may be reserved at www.recreation.gov.", latitude: 36.96099384748342, longitude: -90.98586951446981},
  {name: "Big Woods Hiker-Biker Campsite", city: "Mercersville", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 82.7 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.492868, longitude: -77.792591},
  {name: "Birch Island Campground", city: "Houghton", totalsites: "2", firstcome: "2", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Small campground on Birch Island, near the mouth of McCargoe Cove, on the north side of Isle Royale National Park. One individual tent site and one shelter on site. Boats can stay overnight at dock. Stay Limit: 3 nights June 1 - Labor Day annually. Shelters: 1 Depth at dock (normal conditions): 5' Access: Canoe/kayak/private boat", latitude: 48.110107730247336, longitude: -88.68562291855892},
  {name: "Bird Island Basin Campground", city: "Corpus Christi", totalsites: "34", firstcome: "34", reservable: "0", fees: "8.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/pais/planyourvisit/bird_island_basin.htm", description: "Bird Island Basin lies along Laguna Madre with easy access for fishing, birding, windsurfing, and kayaking. There are 35 RV and tent sites and 10 additional tent-only sites. All sites are side by side on gravel. The tent-only sites are walk up to a sand flat. This area is also for day use activities. Vault toilets are available at the north and south ends with day use areas in between.", latitude: 27.466794, longitude: -97.313693},
  {name: "Black Canyon Campground", city: "Fort Smith", totalsites: "17", firstcome: "17", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/bica/planyourvisit/black-canyon.htm", description: "Black Canyon is a boat-in-only campground located five lake miles from the Ok-A-Beh boat ramp. Black Canyon is currently closed (as of 8/30/22) because of aggressive bear activity due to them receiving food rewards from campers. - Open All Year - Docks in seasonally (Memorial Day - Labor Day) - 17 tent sites", latitude: 45.25091802928579, longitude: -107.96260372030031},
  {name: "Black Canyon Group and Equestrian Campground", city: "Barstow", totalsites: "2", firstcome: "0", reservable: "2", fees: "25.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "While the campgrounds at Mid Hills and Hole-in-the-Wall accommodate a maximum of 8 people and 2 vehicles per site, the Black Canyon Equestrian & Group Campground (located across the road from Hole-in-the-Wall Information Center) is ideal for larger groups. The campsite is available to groups of 15-50 people and reservations are required. Call (760) 252-6100 to make a reservation up to 12 months in advance.", latitude: 35.0486255634, longitude: -115.382956823},
  {name: "Black Rock Campground", city: "f", totalsites: "99", firstcome: "0", reservable: "99", fees: "25.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/jotr/planyourvisit/black-rock-campground.htm", description: "This large (99 site) campground is located in the northwest corner of the park. Each campsite has a picnic table and fire ring with rest rooms and water nearby. Shopping facilities are only five miles away in the town of Yucca Valley. Campsites vary in size and can accommodate both tents and RVs. A day-use picnic area and a dump station are also available. For horse owners, a separate area is provided for camping or for staging a ride.", latitude: 34.07298223672509, longitude: -116.39149680312721},
  {name: "Blackwoods Campground", city: "Otter Creek", totalsites: "281", firstcome: "0", reservable: "281", fees: "60.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/acad/planyourvisit/blackwoods-campground.htm", description: "Blackwoods Campground is located on the east side of Mount Desert Island, about 5 miles (8 km) south of Bar Harbor on Route 3. All sites at Blackwoods are wooded and within a 10-minute walk of the ocean. The majority of Blackwoods' sites are for small and large tents; other sites can accommodate travelers with RVs. Flush toilets, running water, and a dump station are provided, as are picnic tables and fire rings. Roads are paved.", latitude: 44.30894404258141, longitude: -68.20988547018771},
  {name: "Blue Creek", city: "Fritch", totalsites: "50", firstcome: "50", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "This camping area is an off-road vehicle and horseback riding area on the northwest side of the lake off FM 1913. There is no drinking water or flush toilets; vault-evaporator toilet only. Off-road use is in the creek bed ONLY. This area is surrounded with plush cottonwood and soapberry trees.", latitude: 35.7230720210905, longitude: -101.663841251126},
  {name: "Blue Heron Campground", city: "Strunk", totalsites: "45", firstcome: "45", reservable: "43", fees: "20.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/biso/planyourvisit/blueheroncampground.htm", description: "Blue Heron Campground is located in the Kentucky portion of Big South Fork, 9 miles west of Stearns off Highway 742. The campground offers a total of 45 sites all with water and electric hook-ups, picnic tables, fire-rings, access to restrooms and showers and a dump station.", latitude: 36.6775354458249, longitude: -84.5185055731685},
  {name: "Blue Ridge Wilderness Campground", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/br_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. Blue Ridge Campground is surrounded by ponderosa pine and Douglas fir; wild roses and grassy areas are nearby - a beautiful location and very remote. The distance from Pine Springs Trailhead: via the Tejas & Blue Ridge Trails is 7.8 miles, via the Tejas, Bush Mountain & Blue Ridge Trails is 8.9 miles.", latitude: 31.94825872757042, longitude: -104.87174820889072},
  {name: "Blue West Campground", city: "f", totalsites: "30", firstcome: "30", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "A caprock campground, overlooking Lake Meredith. Access to shoreline is difficult. This campground is located north of the lake off FM Road 1913. These campgrounds have excellent views of the lake and offer picnic tables and shade shelters. This area is a great place to view the sunsets of the Texas Panhandle.", latitude: 35.682455887, longitude: -101.628561851},
  {name: "Bluff Campground", city: "f", totalsites: "6", firstcome: "6", reservable: "6", fees: "10.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10191039", description: "The Bluff Campground is a hike in campground and is perfect for those looking to camp in the frontcountry yet have a more backcountry experience. This campground has 6 sites that can accommodate up to 8 campers each. Each site has a firepit and a picnic table. Restrooms are available at either the visitor center or the Longleaf Campground, and water and park information is available at the visitor center. This is a pack it in/pack it out campground.", latitude: 33.83230624616456, longitude: -80.81488727320112},
  {name: "Boca Chita Campground", city: "Homestead", totalsites: "60", firstcome: "60", reservable: "0", fees: "35.00", showers: "Cold- Seasonal", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/bisc/planyourvisit/bocachita.htm", description: "Boca Chita campground is located on an island. The only access is by boat. No services are available. Boca Chita Key features an iconic lighthouse and an open, waterside, grassy camping area with picnic tables and grills. Toilets are available, but there are no sinks, showers or drinking water. The harbor entrance at low tide is approximately four feet deep. Pets, with the exception of service animals as defined by the ADA, are not permitted on the island or boats in the harbor.", latitude: 25.5231, longitude: -80.1746},
  {name: "Bonita Canyon Campground", city: "f", totalsites: "26", firstcome: "0", reservable: "26", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "", description: "Bonita Canyon Campground is located in a shady pine and oak grove near the riparian area of Bonita Canyon wash. It has 25 sites and with rest rooms (flush toilets), running water, picnic tables, and trash pickup. There is a group campground that can accommodate 9 to 24 people (8 tent max). Reserve campsites through Recreation.gov There are no hookups or showers. Vehicle length limit is 29 feet. Camping is limited to 14 days.", latitude: 32.0115507156, longitude: -109.355060216},
  {name: "Boulder Beach Campground (Lake Mead)", city: "Boulder City", totalsites: "135", firstcome: "9", reservable: "126", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10075120", description: "The Boulder Beach Campground is located just minutes from Las Vegas along the Boulder Basin of Lake Mead. It’s open year-round and features large paved sites that can accommodate tents or large RVs, along with tables, fire pits and/or grills. These sites are like an oasis, because of the lush vegetation that shades nearly every campsite. There is a mix of palm trees, oleanders, mature cottonwood trees and native vegetation that also helps provide privacy between sites.", latitude: 36.036950329698435, longitude: -114.80191660139093},
  {name: "Boulder Beach Group Campground (Lake Mead)", city: "Boulder City", totalsites: "5", firstcome: "0", reservable: "5", fees: "80.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10048964", description: "When you stay at the Boulder Beach Group Campground, you’ll have access to many recreation opportunities. Boulder Beach, Special Events Beach, Canoe/Kayak Beach and PWC Beach are 1-2 miles away. At these locations you can swim, kayak and jetski. If you prefer to explore the park on land, the River Mountains Loop Trail passes right by the campground. This paved trail is more than 30 miles long and leads to the Historic Railroad Trail.", latitude: 36.03752520072463, longitude: -114.8025731005672},
  {name: "Bow Creek Primitive Camping", city: "f", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/mnrr/planyourvisit/campgrounds.htm", description: "Primitive, Leave No Trace camping is allowed and free. Camp only in designated sites marked with brown camping signs and metal fire rings. Please call us at 605-665-0209 and leave your basic itinerary for your safety. Located along Bow Creek and the Missouri River in Cedar County about 2 miles northeast of Wynot, Nebraska. A series of gravel roads wind you to the location by car. Follow the brown Bow Creek Recreational Area directional signs to the gravel parking lot and trailhead.", latitude: 42.7776, longitude: -97.1481},
  {name: "Bowman Lake", city: "West Glacier", totalsites: "48", firstcome: "48", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Bowman Lake campground is located in the North Fork area of Glacier National Park approximately 32.5 miles from the west entrance and 30 miles from the Canadian border. The drive to Bowman Lake is a very slow, dusty, and bumpy ride on dirt roads. Trailers are not permitted. The campground is located close to the shore of Bowman Lake and camp sites are within trees for shade and some privacy. Tent campers looking for peace and quiet will enjoy Bowman Lake for its serenity and remote location.", latitude: 48.82904649538723, longitude: -114.2031303790302},
  {name: "Bullfrog RV & Campground", city: "Lake Powell", totalsites: "98", firstcome: "75", reservable: "24", fees: "46.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.lakepowell.com/rv-camping/bullfrog-rv-campground/", description: "This campground is located in the Bullfrog developed area and is operated by Lake Powell Resorts & Marinas. 78 sites, restroom, phones, dump station, potable water station, ½ mile to laundry, store, post office, launch ramp. No reservations. Fees apply. The concessioner also operates a separate RV park with 24 sites, full hook-ups, restrooms, showers, and 1/2 mile to laundry, store, post office, launch ramp. For reservations visit www.lakepowell.com or call 800-528-6154. Fees apply.", latitude: 37.5199691549143, longitude: -110.721029500387},
  {name: "Brandy Creek", city: "Whiskeytown", totalsites: "2", firstcome: "0", reservable: "2", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.recreation.gov/camping/brandy-creek-primitive-campground/r/campgroundDetails.do?contractCode=NRSO&parkId=156940", description: "This two site campground is found just off Brandy Creek Road, with Brandy Creek accessible a short walk away. The Brandy Creek trail, popular with hikers, mountain bikers, and horseback riders, runs next to the campground. This campsite is closed during the winter. Both sites are in partial shade and feature a bear-proof storage locker, picnic table, lamp pole, fire pit with swing-arm BBQ grill, and an ADA accessible vault toilet. Each site has a maximum occupancy of six people and two vehicles.", latitude: 40.6014296703, longitude: -122.582337507},
  {name: "Brandy Creek RV", city: "Whiskeytown", totalsites: "15", firstcome: "32", reservable: "32", fees: "20.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/whis/planyourvisit/brandy-creek-rv.htm", description: "A secluded hilltop RV site close to Brandy Creek Marina and within walking distance of Brandy Creek Beach, the Brandy Creek trail and the Davis Gulch trail. Campground is an asphalt parking lot with 15 parking sites. Only for self-contained units that have their own toilets, such as recreational vehicles, cab-over campers, pop-up tent trailers, and fifth wheels. There is no bathroom and no hookups at this campground. One main water and dump station shared by all campers.", latitude: 40.61705, longitude: -122.57476},
  {name: "Brickhill Bluff Wilderness Campsite", city: "f", totalsites: "4", firstcome: "0", reservable: "4", fees: "9.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/cuis/planyourvisit/brickhillbluff.htm", description: "Brickhill Bluff Wilderness Campsite is one of three campgrounds in Cumberland Island’s designated wilderness on the northern part of the island. The campsite is located 10.5 miles north of the Sea Camp dock. Wilderness sites have no amenities. Fires are prohibited, but camp stoves can be used for cooking. Campers should be prepared to hang all food and trash. Water is available from a pump well located in the site and must be treated. Permits are $9 a night, good for up to 6 people.", latitude: 30.8945, longitude: -81.444},
  {name: "Bridalveil Creek Campground", city: "f", totalsites: "115", firstcome: "0", reservable: "116", fees: "36.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yose/planyourvisit/bvcamp.htm", description: "The Bridalveil Creek Campground is located along the Glacier Point Road near Bridalveil Creek and is surrounded by a beautiful forest of red fir and lodgepole pine. At an elevation of 7,200 feet (2,200m) the campground is located 7 miles west of Glacier Point, 9 miles east of the Wawona Road turnoff, and is approximately 45 minutes from Yosemite Valley. The spectacular views from Glacier Point are nearby, and numerous hiking trails are located along the Glacier Point Road. There are no services nearby.", latitude: 37.6636576342436, longitude: -119.624293366359},
  {name: "Bridge Bay Campground", city: "Yellowstone National Park", totalsites: "432", firstcome: "0", reservable: "432", fees: "33.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/yell/planyourvisit/bridgebaycg.htm", description: "Bridge Bay Campground—elevation 7,800 feet (2377 m)—is located near Yellowstone Lake, one of the largest, high-elevation, fresh-water lakes in North America. Campers at Bridge Bay will enjoy spectacular views of the lake and the Absaroka Range rising above the lake's eastern shore. Yellowstone National Park Lodges provides reservations for this campground.", latitude: 44.534437749236, longitude: -110.436923682403},
  {name: "Brooklyn Campground", city: "Cunard", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "This tent only campground is located on the banks of the New River near the Cunard River Access point and Southside Trailhead. It offers 4 walk-in tents sites and 1 tent-only drive-in site. There are no drive-in sites for RVs or campers at this location. RV, camper, or car camping is prohibited.", latitude: 37.984096, longitude: -81.027881},
  {name: "Brooks Camp Campground", city: "King Salmon", totalsites: "60", firstcome: "0", reservable: "60", fees: "18.00", showers: "Coin-Operated - Seasonal", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/katm/planyourvisit/plyovicamp.htm", description: "Brooks Camp Campground is the only developed campground in Katmai National Park and Preserve. It is located on the shore of Naknek Lake, about .3 miles (.5 km) from the Brooks Camp Visitor Center. Facilities in the campground include a food cache, gear cache, fuel storage locker, potable water, cooking shelters, fire rings, and vault toilets. The campground is also surrounded by an electric fence. Special regulations apply at Brooks Camp and in the campground.", latitude: 58.5597862997626, longitude: -155.777534811051},
  {name: "Buckeye Flat Campground", city: "Sequoia National Park", totalsites: "27", firstcome: "0", reservable: "27", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/buckeye.htm", description: "Closed in 2023 for necessary repairs from extensive damage from winter storms during the 2022-2023 winter. All sites are reservation only. Campers can hear the rushing Middle Fork of the Kaweah River from most sites. Due to high temperatures and dry conditions, fire restrictions are often in effect here. The Paradise Creek Trail departs from the campground, and the Middle Fork Trail is located nearby.", latitude: 36.52243302377206, longitude: -118.76410649285589},
  {name: "Buckhorn Campground Loop A", city: "f", totalsites: "19", firstcome: "19", reservable: "0", fees: "16.00", showers: "Hot - Year Round", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Buckhorn Campground has four loops. Loop A is open seasonally on a first come, first served basis. There are no hookups for RVs on Loop A. Located next to Lake of the Arbuckles, Buckhorn Campground is also located near the Buckhorn Boat Launch and near the southern trailhead for the Rock Creek Multi-Use Trail.", latitude: 34.4296702499, longitude: -96.9881113658},
  {name: "Buckhorn Campground Loop B", city: "f", totalsites: "25", firstcome: "25", reservable: "0", fees: "16.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Buckhorn Campground has four loops. Loop B is open seasonally on a first come, first served basis. There are no hookups for RVs on Loop B. Located next to Lake of the Arbuckles, Buckhorn Campground is also located near the Buckhorn Boat Launch and near the southern trailhead for the Rock Creek Multi-Use Trail.", latitude: 34.4271613463, longitude: -96.9920669817},
  {name: "Buckhorn Campground Loop C", city: "f", totalsites: "41", firstcome: "0", reservable: "41", fees: "16.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Buckhorn Campground has four loops. Loop C is open year round by reservation only. Located next to Lake of the Arbuckles, Buckhorn Campground is also located near the Buckhorn Boat Launch and near the southern trailhead for the Rock Creek Multi-Use Trail.", latitude: 34.4330928671, longitude: -96.9968411343},
  {name: "Buckhorn Campground Loop D", city: "f", totalsites: "48", firstcome: "48", reservable: "0", fees: "16.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Buckhorn Campground has four loops. Loop D is open year round. Campsite on Loop D are first come, first served. Located next to Lake of the Arbuckles, Buckhorn Campground is also located near the Buckhorn Boat Launch and near the southern trailhead for the Rock Creek Multi-Use Trail.", latitude: 34.432766546, longitude: -96.9974819194},
  {name: "Buffalo Point Campground", city: "Yellville", totalsites: "108", firstcome: "45", reservable: "56", fees: "30.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/buffalo-point-campground.htm", description: "Buffalo Point Campground is the park's largest and most developed campground. It offers water and electrical hookups for RVs. Some sites at Buffalo Point are available for reservation at www.recreation.gov (1-877-444-6777) and others are first come, first serve. Flush restrooms, dump station, showers, water, and electricity are available from March 15 - November 14 when fees are charged. Fees are not charged from November 15 to March 14 when only B Loop is open with no amenities, just a vault restroom.", latitude: 36.0715554992, longitude: -92.5555762993},
  {name: "Burnwood Group Campsite", city: "Lansing", totalsites: "1", firstcome: "0", reservable: "1", fees: "75.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "", description: "Burnwood is located across Route 19 from Canyon Rim Visitor Center and offers group camping. This site requires a Special Use Permit. For a permit for the group campsites, contact the permits office at 304-465-6517. Permits require a minimum of 10 business days after the application has been received.", latitude: 38.073636, longitude: -81.077535},
  {name: "Bush Mountain Wilderness Campground", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/bm_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. A favorite of many for the exceptional vistas and western sunsets. Though the campsites at Bush Mountain are semi-protected from high winds, backpackers will find hiking on the exposed trails to reach the campground difficult during periods of high wind activity. Bush Mountain Campground is 6.2 miles from Pine Springs Trailhead via the Tejas and Bush Mountain Trails. Five designated campsites are defined by tent pads.", latitude: 31.92641602069643, longitude: -104.87668543567213},
  {name: "Butte Lake Campground", city: "Old Station", totalsites: "101", firstcome: "0", reservable: "107", fees: "22.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/butte-lake-campground.htm", description: "Butte Lake Campground is located in the NE corner of Lassen Volcanic National Park. The campground is a 45-minute drive from the Northwest Entrance and Manzanita Lake Area and is accessed via the 6-mile-long Butte Lake Road south of highway 44. This remote campground offers few amenities, but numerous recreation opportunities. Enjoy a hike up Cinder Cone or a paddle along Butte Lake's lava rock shores. Reservations are available for most sites and required for group sites and the stock corral.", latitude: 40.56480115555671, longitude: -121.30576429271787},
  {name: "Cabin Camp 1 (By Reservation Only)", city: "Dumfries", totalsites: "1", firstcome: "0", reservable: "1", fees: "670.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/prwi/planyourvisit/cabin-camping.htm", description: "Cabin Camp 1 was the first camp in the park completed by the Civilian Conservation Corps (CCC) in the 1930's. It sleeps 205 visitors. Its dining hall and craft lodges are some of the best examples of rustic architecture in the park. Stone foundations, fireplaces and whole log supports showcase the beautiful native materials used by the CCC. The sleeping cabins in this camp host 2 to 10 campers per unit and are newer construction (circa 1980s). Some cabins in the D unit are semi wheelchair accessible.", latitude: 38.59701768873082, longitude: -77.35605254877296},
  {name: "Cabin Camp 2 (By Reservation Only)", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "630.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/prwi/planyourvisit/cabin-camping.htm", description: "Cabin Camp 2 first hosted campers in 1937. The camp sleeps 149 visitors and consists of approximately 132 acres. This camp mainly contains 4 person cabins, with a few 2 and 6 person cabins mixed in here and there. The units are clustered in a circular arrangement and contain 8 cabins, a bath house, and a lodge. Each unit is in a grassy clearing that is surrounded by dense forest. The large ballfield and lake access in Camp 2 are among the best in the park. Cabin Camp 2 was listed on the National Register o", latitude: 38.581375041329196, longitude: -77.4157374023348},
  {name: "Cabin Camp 3 (By Reservation Only)", city: "Triangle", totalsites: "13", firstcome: "0", reservable: "13", fees: "475.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/prwi/planyourvisit/cabin-camping.htm", description: "Individual cabin rentals and a small group site are available in Cabin Camp 3. This forested camp was constructed by the Civilian Conservation Corps in the 1930s. It is also listed on the National Register of Historic Places. Cabin Camp 3, C Unit is made for smaller groups. C Unit has a linear layout and sleeps 76 visitors. C Unit's 10-person cabins make it one of the best in the park for group outings. The beautiful brick fireplaces in the dining hall and craft lodges give this camp a unique character.", latitude: 38.56367216064515, longitude: -77.36493032549049},
  {name: "Cabin Camp 4 (By Reservation Only)", city: "Dumfries", totalsites: "1", firstcome: "0", reservable: "1", fees: "650.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/prwi/planyourvisit/cabin-camping.htm", description: "The 199 visitors who can sleep in this Cabin Camp 4 can enjoy the new roofs and windows that help ensure that these historic structures last for future generations. Cabin Camp 4 has mostly 10-person sleeping cabins. While this camp is the only one in the park without a fireplace in its dining hall, the theater building/gymnasium is the only one in the park.", latitude: 38.59150925969603, longitude: -77.35386082375908},
  {name: "Cabin Camp 5 (By Reservation Only)", city: "Triangle", totalsites: "1", firstcome: "0", reservable: "1", fees: "790.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/prwi/planyourvisit/cabin-camping.htm", description: "Cabin Camp 5 was the last camp to be completed by the CCC and WPA. The camp offers the largest capacity (210 in summer, 104 in winter) due to the large dorm buildings and restrooms. This is also the only camp with heat, so this camp may be rented year-round. This is also the only camp to have an outdoor picnic pavilion located inside the camp. Cabin Camp 5 is set up in two units with cabins that sleeps 2 to 4 people and larger dorm buildings that sleep 24.", latitude: 38.57577906331334, longitude: -77.41256789301235},
  {name: "Cacapon Junction Hiker-Biker Campsite", city: "Woodmont", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 133.6 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.621284, longitude: -78.279305},
  {name: "Cades Cove Campground", city: "Townsend", totalsites: "164", firstcome: "0", reservable: "164", fees: "30.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/grsm/planyourvisit/cades-cove-campground.htm", description: "Cades Cove Campground—elevation 1,807 feet (~551 m)—is open year round and combines the feel of primitive camping with the modern convenience of flush toilets and drinking water.", latitude: 35.60272464725332, longitude: -83.77629277369829},
  {name: "Cades Cove Group Campground", city: "Townsend", totalsites: "3", firstcome: "0", reservable: "4", fees: "35.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "", description: "Cades Cove combines the feel of primitive camping with the modern convenience of flush toilets and drinking water. A camp store provides visitors with basic necessities and bike rentals. Events like interpretive programs in the nearby amphitheater and bicycle-only days on the Cades Cove Loop Road provide visitors with a fun and unique experience.", latitude: 35.60389164669365, longitude: -83.77130987431053},
  {name: "Calico Rocks Hiker-Biker Campsite", city: "Point of Rocks", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 47.6 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.271168, longitude: -77.535582},
  {name: "Callville Bay Campground (Lake Mead)", city: "f", totalsites: "52", firstcome: "52", reservable: "0", fees: "20.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10068459", description: "When you stay at Callville Bay Campground, you can enjoy the area on land or on water. A nearby shaded picnic area has cool grass, grills, restrooms and panels explaining the history of the area. Across the street, you’ll find the trailhead to the Callville Summit Trail. This 2.7-mile moderate hike requires a short climb, but yields a spectacular view of Lake Mead, Fortification Hill, the Hemenway Valley and the River Mountains.", latitude: 36.1378088551872, longitude: -114.72861216150869},
  {name: "Callville Bay RV Park (Lake Mead)", city: "Overton", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://callvillebay.com/lodging-rv-camp/", description: "More than 550 miles of pristine shoreline. Soaring red rock cliffs. Countless bays and beaches. Get off the grid for the vacation of a lifetime at Callville Bay Resort & Marina on Lake Mead. Conveniently located within an hour of Las Vegas, Nevada, Callville Bay offers an ideal getaway perfectly complemented by infinite opportunities for adventure and entertainment. Personal watercraft and ski boat rentals are available for a fun-filled day trip, along with multiple houseboat rental options.", latitude: 36.143263, longitude: -114.725594},
  {name: "Camp 4 Campground", city: "f", totalsites: "61", firstcome: "0", reservable: "61", fees: "10.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/yose/planyourvisit/camp4.htm", description: "Camp 4 is located in Yosemite Valley near the base of granite cliffs close to Yosemite Falls. Yosemite Valley is centrally located in the park and boasts some of Yosemite’s most iconic features. This campground is located at 4,000 feet (1,200 m) elevation and can be accessed from all park roads. Camp 4 is within biking and walking distance of many services in Yosemite Valley and is located near the free shuttle route. There are food and grocery services nearby at Yosemite Valley Lodge and Yosemite Village.", latitude: 37.7420541114337, longitude: -119.602253054678},
  {name: "Camp Greentop", city: "Thurmont", totalsites: "16", firstcome: "1", reservable: "1", fees: "1000.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/cato/planyourvisit/greentop.htm", description: "Located on the National Register of Historic Places as a Historic District, the cabin camp facility is a perfect location for organized groups to hold retreats and conferences. The camp has space for 140 people to sleep on twin beds. There are 12 cabins that sleep 10 each, one lodge that sleeps 12, and three smaller cabins.", latitude: 39.64454144059151, longitude: -77.47656473444567},
  {name: "Camp Misty Mount", city: "Thurmont", totalsites: "30", firstcome: "0", reservable: "30", fees: "50.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/233241?q=Camp%20Misty%20Mount", description: "Reservations Required. No Walk Ins. Camp Misty Mount is a cabin rental facility for individuals, families, or small groups. This historic cabin camp was completed in 1937 by the Works Progress Administration. The camp is surrounded by hardwood forests on the slopes of Catoctin Mountain Park. The region is part of the Blue Ridge Province and offers scenic mountain views and clear streams.", latitude: 39.6418851453505, longitude: -77.4484489543919},
  {name: "Camp Round Meadow", city: "Sabillasville", totalsites: "1", firstcome: "0", reservable: "1", fees: "1000.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/251264", description: "Reservations Required. No Walk-Ins. Camp Round Meadow is a group camp featuring 4 heated dorms that sleep 30 people each (total 120). Each dorm has bathrooms with flush toilets, hot showers, and electric outlets. Cots with vinyl mattresses are provided. Campers bring bedding, toiletries, toilet paper and towels, and cleaning supplies. Brooms and mops are provided. Additional facilities include a dining hall, gymnasium, classrooms, a conference room, and a small outdoor gazebo.", latitude: 39.6449169814922, longitude: -77.4876280994409},
  {name: "Camping and Huts", city: "f", totalsites: "24", firstcome: "22", reservable: "2", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "Twelve campsites, four lean-tos and 2 huts are dispersed throughout the monument. Reservations for Haskell and Big Spring Brook Hut are required and available through Recreation.gov. All other campsites and lean-tos are currently first-come, first-serve. Reservations for those sites will be available through Recreation.gov by summer 2022.", latitude: 46.1249342961593, longitude: -68.801462993981},
  {name: "Canaveral National Seashore Backcountry Island Camping", city: "New Smyrna Beach", totalsites: "14", firstcome: "0", reservable: "14", fees: "20.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/cana/planyourvisit/camping.htm", description: "Natural and spoil island campsites provide a variety of access and capacity options to those visitors who have their own boat, canoe or kayak. Please see the attached map for location and site numbers/names. Reservations and a permit are required for island campsites. Canoe rentals are available for sites one through five. Canoes are reserved by calling (386) 428-3384 extension zero, after you have made your camping reservation. Sites 6-14 may only be reached with a personal boat, canoe or kayak.", latitude: 28.92700096310608, longitude: -80.82460296672082},
  {name: "Canyon Campground", city: "Yellowstone National Park", totalsites: "273", firstcome: "0", reservable: "273", fees: "39.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/yell/planyourvisit/canyoncg.htm", description: "Canyon Campground—elevation 7,900 feet (2408 m)—lies in a lodgepole pine forest at Canyon Village, south of the Washburn range and near the breath-taking Grand Canyon of the Yellowstone River. Canyon Village offers stores, restaurants, and lodging. Nearby hikes include Cascade Lake, Mount Washburn, and the Canyon Rim trails. Yellowstone National Park Lodges provides reservations for this campground.", latitude: 44.73538374887841, longitude: -110.48791238318904},
  {name: "Canyon City", city: "Skagway", totalsites: "11", firstcome: "0", reservable: "11", fees: "54.60", showers: "None", toilets: "Composting Toilets - year round", phone: "No", url: "https://www.nps.gov/klgo/planyourvisit/canyon-city.htm", description: "The Canyon City campground provides a nice place to take a break from hiking, with several tables and two warming shelters. This backcountry campground is at mi 7.7 (km 12.5). The campground is located just half a mile from the historic Canyon City townsite where hikers can see artifacts leftover from the gold rush days including the large metal tram boiler. In order to camp overnight along the Chilkoot Trail on the U.S. side you must purchase a backcountry permit from the Chilkoot Trail Center in Skagway.", latitude: 59.6075624, longitude: -135.3246672},
  {name: "Canyon View Campground", city: "Shonto", totalsites: "13", firstcome: "10", reservable: "3", fees: "0.00", showers: nil, toilets: nil, phone: "", url: "", description: "Canyon View Campground is open during the Summer Season from May through September, with 13 sites including 3 large group camping sites. Camping is free.", latitude: 0.0, longitude: 0.0},
  {name: "Canyon View Campground", city: "Kings Canyon National Park", totalsites: "16", firstcome: "0", reservable: "16", fees: "50.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/canyon_view.htm", description: "Not currently taking reservations for summer 2023, due to winter storm damage. Typically opens end of May with reservations beginning end of January, on a four-month rolling basis. Canyon View campground is located on Highway 180, 0.25 miles (400 m) from Cedar Grove Village. This group-only campground is on the floor of the canyon along the South Fork of the Kings River. It was named for its excellent views of Kings Canyon's granite cliffs. This campground is for medium and large groups.", latitude: 36.78719293800008, longitude: -118.66544930776234},
  {name: "Cape Point Campground", city: "Buxton", totalsites: "202", firstcome: "0", reservable: "202", fees: "20.00", showers: "Cold- Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/251945", description: "Cape Point Campground with 202 sites is located southwest of the Cape Hatteras Lighthouse, and within walking distance of the Atlantic Ocean and Cape Hatteras (also known as Cape Point) itself. Open April 7-Nov. 27, 2023. Campsite reservations are limited to same-day reservations only. Campsites must be booked via recreation.gov.", latitude: 35.235775, longitude: -75.53911},
  {name: "Caribou Island Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Caribou Island Campground is located on the very western tip of West Caribou Island, in Rock Harbor Channel, on the northeast end of Isle Royale National Park. Boats can dock overnight. Stay Limit: 3 nights effective from June 1 – Labor Day. Shelters: 2 Depth at dock (normal conditions): 10’ Access: Canoe/kayak/private boat", latitude: 48.094571206998936, longitude: -88.57220404720897},
  {name: "Carver Campground", city: "Jasper", totalsites: "8", firstcome: "8", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/camping.htm", description: "Carver is a first come, first serve campground in the upper district of the park. It has 8 tent only sites with potable water, but no electrical amenities. A vault restroom is available. This is a pack in/pack out facility with no trash service.", latitude: 35.9835736001, longitude: -93.0401838004},
  {name: "Cataloochee Campground", city: "Waynesville", totalsites: "27", firstcome: "0", reservable: "27", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grsm/planyourvisit/cataloochee-campground.htm", description: "Cataloochee Campground—elevation 2,610 feet (~796 m)—located in historic Cataloochee Valley, is surrounded by mountain ranges and pristine streams characterized by mild winters and hot, humid summers. It offers traditional camping with the convenience of flush toilets and drinking water. No hookups or showers.", latitude: 35.631185945507, longitude: -83.08554127280615},
  {name: "Cave Creek Campground", city: "Cave Junction", totalsites: "17", firstcome: "17", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/orca/planyourvisit/campgrounds.htm", description: "Sleep under the boughs of an old growth forest beside Cave Creek. This campground is located 15 miles up Highway 46 from Cave Junction and just four miles from the cave entrance. The campground has fire rings, water and vault toilets. The spaces are too small for large RVs or trailers and no pull through sites. The campground offers 17 sites available on a first-come, first-serve basis. Maximum vehicle length: 20 feet.", latitude: 42.1139211881, longitude: -123.429613284},
  {name: "Cedar Canyon Campground", city: "Fritch", totalsites: "15", firstcome: "15", reservable: "0", fees: "0.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "This small cove lies between Fritch Fortress and Sanford-Yake campgrounds. The area features beach camping with no individual campsites. This campground can become soft during rainy conditions. Please drive with caution when leaving paved roads. There are restrooms with potable water and flush toilets in the parking lot.", latitude: 35.79584, longitude: -101.24451},
  {name: "Cedar Pass Campground", city: "Interior", totalsites: "96", firstcome: "0", reservable: "96", fees: "23.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Located near the Ben Reifel Visitor Center, the Cedar Pass Campground has 96 level sites with scenic views of the badlands formations. This campground offers flush toilets and coin operated showers in the summertime.", latitude: 43.744154, longitude: -101.950161},
  {name: "Central Avenue Walk-in Sites", city: "Michigan City", totalsites: "5", firstcome: "5", reservable: "5", fees: "25.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10177495", description: "The Central Beach Walk-In Sites has 5 sites, and you will walk a distance to get to your campsite. There is a primitive toilet (porta-potty) by the Central Beach parking lot for the entire season. There is no drinkable water in the area so you must bring your own water.", latitude: 41.701453274677434, longitude: -86.94854736328125},
  {name: "Central Campground", city: "f", totalsites: "0", firstcome: "0", reservable: "10", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Central Campground consists of ten group sites. It is located near downtown Sulphur.", latitude: 34.5031796294, longitude: -96.9655507482},
  {name: "Chapel Individual Backpacking Sites", city: "f", totalsites: "6", firstcome: "0", reservable: "6", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Chapel Beach Campground sits on the sand bluff above Chapel Beach and Lake Superior. Chapel Beach is a popular day use area, so the six sites here offer less isolation than other backcountry campgrounds. Shared pit toilet. Water source is Chapel Creek and Lake Superior. Lake Superior access for kayakers. NO CAMPFIRES.", latitude: 46.546925, longitude: -86.440929},
  {name: "Chimney Hollow Campground", city: "Fritch", totalsites: "15", firstcome: "0", reservable: "0", fees: "0.00", showers: nil, toilets: nil, phone: "", url: "", description: "This remote and primitive area is located near Blue West. It is tucked in the Blue Creek embankment, which provides protection from prevailing winds. There is no drinking water or flush toilets; vault-evaporator only. There are picnic tables available.", latitude: 35.6918602938, longitude: -101.642234005},
  {name: "Chippewa Harbor Campground", city: "Houghton", totalsites: "7", firstcome: "6", reservable: "1", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Chippewa Harbor Campground is located in Chippewa Harbor, on the south shore of Isle Royale National Park. Trail access is via the Indian Portage Trail. Boats can stay overnight at dock. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 4 Depth at dock (normal conditions): 7’ Access: Foot/canoe/kayak/private boat", latitude: 48.02936725450616, longitude: -88.6510379097632},
  {name: "Chisel Branch Hiker-Biker Campsite", city: "Poolesville", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 30.5 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.102158, longitude: -77.473011},
  {name: "Chisos Basin Campground", city: "f", totalsites: "56", firstcome: "0", reservable: "56", fees: "16.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/bibe/planyourvisit/basin_campground.htm", description: "The Chisos Basin Campground is nestled in an open woodland within a scenic mountain basin. Campers enjoy the iconic views of Casa Grande and Emory Peak. The sunset through the nearby \"Window\" is a Big Bend highlight. Some of the park's most popular trails begin nearby. Elevation is 5,400 feet. RESERVATIONS REQUIRED. Trailers over 20 feet and RV's over 24 feet are not recommended due to the narrow, winding road to the Basin and small campsites.", latitude: 29.275900792177, longitude: -103.302285692397},
  {name: "Chopawamsic Backcountry Area", city: "Triangle", totalsites: "8", firstcome: "0", reservable: "8", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/prwi/planyourvisit/backcountrycamping.htm", description: "Chopawamsic Backcountry Area (tents only) is an 8-site, hike-in/hike-out, backcountry campground in a remote area of the park. No pets and no campfires are allowed in this area. Four campers per site only. Campers must pitch tents within 20 feet of the site marker. Reservations can be made on recreation.gov or by calling 877-444-6777. Campers must pay the park entrance fee or possess a valid park pass. Vehicles must be parked in the gravel parking lot.", latitude: 38.558761425074024, longitude: -77.40690973222598},
  {name: "Cimarron Campground", city: "f", totalsites: "21", firstcome: "0", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cure/planyourvisit/camp_cimarron.htm", description: "Cimarron Campground is located 20 miles east of Montrose on U.S. Highway 50 at the site of a historic narrow gauge railroad town. All types of camping from tents to medium sized RVs can be accommodated at Cimarron. All sites are first-come first-served, and the campground rarely fills. Railroad exhibits are adjacent to the campground, and Crystal Reservoir near Morrow Point Dam is approximately 1.5 miles away. Elevation: 6895 feet", latitude: 38.4443044882, longitude: -107.555542845},
  {name: "Cinnamon Bay Beach and Campground", city: "f", totalsites: "121", firstcome: "0", reservable: "50", fees: "50.00", showers: "Cold- Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/viis/planyourvisit/camping.htm", description: "The campground offers bare tent sites, eco-tents, and cottages. Amenities include restrooms, showers, food and beverage service, watersports rentals, beach wheelchairs, and more.", latitude: 18.35231014634408, longitude: -64.75408127304699},
  {name: "Circle X Ranch Campground - CLOSED", city: "Malibu", totalsites: "1", firstcome: "1", reservable: "1", fees: "9.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "Circle X Ranch Group Campground is the only campground the National Park Service operates in the Santa Monica Mountains. Visit recreation.gov to make reservations for this 10 person minimum group camp.", latitude: 34.1112789977, longitude: -118.926525539},
  {name: "City of Rocks Campground", city: "Almo", totalsites: "67", firstcome: "6", reservable: "67", fees: "14.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/ciro/planyourvisit/camping.htm", description: "Camping in City of Rocks is a great way to enjoy being out in nature. Relax beneath the cool shade of aspen, juniper, mountain mahogany, or pine. Spend the night surrounded by impressive granite formations and starry skies. There are 64 standard campsites and 3 group sites to choose from with easy access to climbing, hiking trails, and vistas.", latitude: 42.067203, longitude: -113.701382},
  {name: "Cliffs Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Cliffs Group Campground is one mile east of Cliffs Campground. It lies high above Lake Superior and inland from the North Country Trail. You will not be able to see the lake from the campground in the summer. There are spots along the North Country Trail near the campground to get a glimpse of Grand Island and Lake Superior. One shared fired ring and pit toilet. Visitors must bring water. NO ACCESS TO WATER.", latitude: 46.48567, longitude: -86.556577},
  {name: "Cliffs Individual Backpacking Sites", city: "f", totalsites: "3", firstcome: "0", reservable: "3", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Cliffs Campground rests high above Lake Superior inland from the North Country Trail. The three campsites are in an open area with little cover between sites. During the summer months when the foliage is at its peak, you cannot see Lake Superior from the campground. There are places along the trail to sneak a peek of Grand Island and the lake. One shared fired ring and pit toilet. Visitors must bring water. NO ACCESS TO WATER.", latitude: 46.473431, longitude: -86.570456},
  {name: "Cloverleaf Campground", city: "Gifford", totalsites: "7", firstcome: "0", reservable: "7", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-cloverleaf.htm", description: "Cloverleaf is a small, walk-in only campground near Gifford. Tent sites are tucked into the pines with lake shore access. This campground is great for those wanting a more rugged, primitive camping experience with standard amenities like water and pit toilets. Although small, Cloverleaf Campground is not as popular as other campgrounds and doesn’t often fill.", latitude: 48.2971109735789, longitude: -118.14685853302},
  {name: "Coast Campground", city: "Point Reyes Station", totalsites: "14", firstcome: "1", reservable: "13", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/pore/planyourvisit/coast-campground.htm", description: "Coast Campground is nestled within a small coastal valley with easy access to the beach and tidepools. The beach is within 220 yards of the campground and tidepools are located ~720 yards to the southeast. One may access Coast Campground by foot, bicycle, or horse; not by boat, auto, or RV. The shortest approach on solid ground is via the 1.8-mile slightly uphill hike on the Laguna and Fire Lane Trails, starting at the Laguna Trailhead just east of the hostel. There are 12 regular sites and two group sites.", latitude: 38.01717280583828, longitude: -122.85437534114334},
  {name: "Cold Springs Campground", city: "Sequoia National Park", totalsites: "40", firstcome: "0", reservable: "40", fees: "28.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/cold_springs.htm", description: "All sites are reservation only. 𝗢𝗽𝗲𝗻𝗶𝗻𝗴 𝗗𝗮𝘁𝗲: Not currently taking reservations for summer 2023, due to winter storm damage. Typically, reservations begin on April 24, 2023, on a one-month rolling basis. Nestled among amid aspen trees and conifers, the campground is located near the Mineral King Ranger Station, 26 miles (42 km) - 1.5 hour drive time - from the Highway 198 junction in Three Rivers. There are limited services at Silver City Resort, 2.5 miles (4 km) west of the campground.", latitude: 36.45130196165861, longitude: -118.61241063028547},
  {name: "Cold Springs Campground", city: "f", totalsites: "65", firstcome: "0", reservable: "65", fees: "14.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Cold Springs Campground is open seasonally for the summer. It opens no later than Memorial Day Weekend and closes no earlier than Labor Day Weekend. Rebuilt in the 1930s to its current configuration, it does not accommodate most RVs, though small pop-up or teardrop trailers may fit in the parking spaces at some campsites.", latitude: 34.5007809022, longitude: -96.9604327557},
  {name: "Colonial Creek North Campground", city: "Sedro-Woolley", totalsites: "41", firstcome: "0", reservable: "44", fees: "24.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/246855", description: "Colonial Creek North Campground is a remote, yet bustling campground nestled in old growth forest. Located on the north side of State Route 20 near mile marker 130, there are 41 campsites that are surrounded by forest and located on Diablo Lake. Recreational opportunities include a fully accessible fishing pier and boat launch on Diablo Lake, as well as hiking opportunities for various abilities on Thunder Creek and Thunder Knob trails.", latitude: 48.691, longitude: -121.101},
  {name: "Colonial Creek South Campground", city: "Sedro-Woolley", totalsites: "96", firstcome: "0", reservable: "91", fees: "24.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/255201", description: "Colonial Creek South Campground is a remote, yet busy campground nestled in old growth forest on the south side of State Route 20 near mile marker 130.Ten walk-in, tent-only sites are available first-come, first-served in the winter (sites #64-74). Sites may be difficult to access in winter. Reservations required late May to September. Recreational opportunities include a fully accessible fishing pier and boat launch on Diablo Lake, as well as hiking opportunities on Thunder Creek and Thunder Knob trails.", latitude: 48.687, longitude: -121.094},
  {name: "Colorado River Primitive Camping Between Glen Canyon Dam and Lees Ferry", city: "Page", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/glca/planyourvisit/camping.htm", description: "No reservations. Along the fifteen mile stretch of the Colorado River between Glen Canyon Dam and Lees Ferry, there are five primitive beaches set aside for camping, marked with signs, on a first-come, first-served basis. Camping is permitted in these areas only, as they are provided with toilets and fire pits. These sites can only be accessed by small vessel. All campsites are located well above the river and require a short walk from your boat.", latitude: 36.86618866356771, longitude: -111.58668629578499},
  {name: "Colter Bay Campground", city: "Moran", totalsites: "324", firstcome: "0", reservable: "347", fees: "50.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grte/planyourvisit/colterbaycg.htm", description: "This large campground is in a lodgepole pine forest near Colter Bay Village. While not on the shores of Jackson Lake, a short stroll leads to a spectacular view of Mount Moran and the northern Teton Range. Colter Bay Village has many facilities including a visitor center, restaurants, stores, cabins, and marina. Shower and laundry services are available for additional fee. Departing from the nearby Hermitage Point Trailhead, hikers traverse forests and sagebrush meadows enjoying the Teton landscape.", latitude: 43.9074780505115, longitude: -110.641378983075},
  {name: "Colter Bay RV Park", city: "Moran", totalsites: "112", firstcome: "0", reservable: "112", fees: "112.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grte/planyourvisit/colterbayrv.htm", description: "This RV Park provides full hookups for campers in the park. It is in a lodgepole pine forest near Colter Bay Village. While not on the shores of Jackson Lake, a short stroll leads to a spectacular view of Mount Moran and the northern Teton Range. Colter Bay Village has many facilities including a visitor center, restaurants, stores, cabins, a marina. Shower and laundry services are available for additional fee.", latitude: 43.905642, longitude: -110.641324},
  {name: "Colter Bay Tent Village", city: "Moran", totalsites: "66", firstcome: "0", reservable: "66", fees: "98.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grte/planyourvisit/colter-bay-tent-village.htm", description: "The Colter Bay Tent Village at Colter Bay Village is set amidst towering lodgepole pines just a short, picturesque stroll away from Jackson Lake. The Tent Village includes 66 tent cabins, and is open from mid-May to early-September. As with all campgrounds in the park, the Colter Bay Tent Village is very popular and tends to fill up quickly. Visitors are strongly encouraged to plan and book early.", latitude: 43.904150265109244, longitude: -110.63494741916656},
  {name: "Cosby Campground", city: "Cosby", totalsites: "157", firstcome: "0", reservable: "157", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/232479?tab=info", description: "Cosby Campground—elevation 2,459 feet (~750 m)—is tucked in the mountains under a canopy of cool shade. This campground creates a peaceful and secluded environment for visitors. Mountain ranges and a pristine river are the backdrop for your camping adventure in Cosby. Cosby provides a moderate climate, characterized by mild winters and hot, humid summers. Campsites for both tents and RVs available.", latitude: 35.7545448462525, longitude: -83.20925027349114},
  {name: "Cottonwood Campground", city: "Cottonwood Springs", totalsites: "62", firstcome: "0", reservable: "62", fees: "25.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/cottonwood-campground.htm", description: "The Cottonwood Campground is reservation only and has 62 sites, potable water and flush toilets. The Campground is near the Cottonwood Visitor Center in the southeast part of the park. The closest metropolitan area is Indio about 30 miles away.", latitude: 33.744880129561544, longitude: -115.81282987420815},
  {name: "Cottonwood Campground", city: "f", totalsites: "76", firstcome: "36", reservable: "37", fees: "14.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/thro/planyourvisit/cottonwood-campground.htm", description: "Cottonwood Campground lies inside the park, about 5 miles from Medora, ND. It is the South Unit's only campground. Half the sites are by reservation at recreation.gov while all remaining sites are first come, first served. Most sites are suitable for tents and RVs (no hookups). Cottonwood Campground fills to capacity each afternoon, mid-May through mid-September.", latitude: 46.949934505, longitude: -103.531268589},
  {name: "Cottonwood Campground", city: "f", totalsites: "22", firstcome: "0", reservable: "22", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/bibe/planyourvisit/cottonwood_campground.htm", description: "Cottonwood Campground is a quiet oasis in the western corner of Big Bend National Park. Reservations are required. Conveniently located between the Castolon Historic District, the scenic Santa Elena Canyon and the tail end of the Ross Maxwell Scenic Drive, this small, 22-site campground is one of the least-known and quiet campgrounds in the park. There is one group campsite and 21 individual sites. This is a remote campground in a remote park. It is dry camping, no hook-ups, and no generators are permitted.", latitude: 29.13678040347906, longitude: -103.52251396165582},
  {name: "Cottonwood Cove Campground (Lake Mohave)", city: "f", totalsites: "45", firstcome: "0", reservable: "45", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.recreation.gov/camping/campgrounds/10068512", description: "Cottonwood Campground is located near Searchlight, Nevada, along Lake Mohave. It’s open year-round and features paved sites that can accommodate tents or RVs, along with tables, fire pits and/or grills. Restrooms and water spigots are located throughout the campsite. Temperatures usually surpass 100F (37C) degrees June-August. In May and September, daytime highs are around 90F (32C). October-April, temperatures are much cooler. Lows can dip to freezing temps December-February. The area averages 4 inch", latitude: 35.487755719796056, longitude: -114.69503347257717},
  {name: "Cottonwood Cove RV Park (Lake Mead)", city: "f", totalsites: "72", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://cottonwoodcoveresort.com/explore/lodging-rv-camp/", description: "Cottonwood Cove Resort & Marina at Lake Mohave is a premier destination for houseboat rentals, watercraft rentals, camping and includes a spacious rv park. We are conveniently located on the Colorado River and just a short drive from Laughlin, Nevada. Our resort and marina have lakeside accommodations, a full-service rv park, nearby campground facilities and a variety of boat rentals. Visit us at Lake Mohave and experience the getaway of a lifetime.", latitude: 35.49080255042127, longitude: -114.68835234952485},
  {name: "Cougar Rock Campground", city: "f", totalsites: "179", firstcome: "46", reservable: "133", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/mora/planyourvisit/campgrounds.htm", description: "Cougar Rock Campground is located on the south side of the park, on the road between Longmire and Paradise. It is in a steep valley with the Nisqually River across the main road. Thick forest extends through the whole campground providing privacy for campsites. Access to the Wonderland trail is across the road with waterfalls a moderate hike away. Less than 15 minutes of driving can take you to the trails and facilities at Longmire or Paradise.", latitude: 46.767606803, longitude: -121.792370845},
  {name: "Coves Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Coves Group Campground is three miles east of Chapel Beach. You are still up on the cliff line, but have started your descent towards Twelvemile Beach. Hike east on the North Country Trail about a quarter mile to find a trail to a small cove and beach on Lake Superior; this is where you have access to water. One shared fire ring and pit toilet. No access for kayakers due to 1/4 mile portage.", latitude: 46.56088, longitude: -86.395902},
  {name: "Coves Individual Backpacking Sites", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Coves Campground is inland from the North Country Trail, hidden behind the pine trees and ferns. There are two entry points to the campground. The campground trail leads through the forest where you will find five campsites and two designated metal communal fire rings. There is Lake Superior and beach access from the bluff along the North Country Trail. Water source is Lake Superior. Shared pit toilet. Lake Superior access for kayakers.", latitude: 46.568171, longitude: -86.372254},
  {name: "Crabtree Falls Campground", city: "Spruce Pine", totalsites: "81", firstcome: "54", reservable: "27", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/blri/planyourvisit/crabtree-falls-cg.htm", description: "A hidden parkway gem, Crabtree Falls Campground offers quiet serenity. It’s a magical place when mist hangs in the air, coyotes and deer stroll through, and barred owls call out. Crabtree Falls, a 70’ waterfall, is accessible from the campground. Named for the crabapple trees which once dotted the countryside, the meadows in Crabtree Falls Recreation Area speak of the area’s history as a farming community. Bursting with wildflowers in spring, the meadows abut hardwood forests and thickets of rhododendrons.", latitude: 35.815386045639634, longitude: -82.14556897298166},
  {name: "Crane Flat Campground", city: "f", totalsites: "151", firstcome: "0", reservable: "151", fees: "36.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/yose/planyourvisit/cranecamp.htm", description: "The Crane Flat Campground is located along the Big Oak Flat Road, just west of Crane Flat, about 30 minutes northwest of Yosemite Valley, at 6,200 ft (1,900 m) elevation. There is a gas station and minimal convenience items located nearby at Crane Flat. Trailheads for both the Merced Grove and Tuolumne Grove of Giant Sequoias are located within a short drive of the campground.", latitude: 37.7497960831966, longitude: -119.800258983341},
  {name: "Crystal Creek", city: "Whiskeytown", totalsites: "2", firstcome: "0", reservable: "2", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.recreation.gov/camping/crystal-creek-primitive-campground/r/campgroundDetails.do?contractCode=NRSO&parkId=156890", description: "The Crystal Creek campground offers two secluded sites along Crystal Creek and is a short drive away from both Crystal Creek falls and the James K. Carr trail to Whiskeytown Falls. This campsite is closed during the winter. A fairly steep dirt road accesses the campsites. Both sites are well shaded and feature a bear-proof storage locker, picnic table, lamp pole, fire pit with swing-arm BBQ grill, with a shared ADA accessible vault toilet. Each site has a maximum occupancy of six people and two vehicles.", latitude: 40.6422, longitude: -122.6710333},
  {name: "Crystal Springs Campground", city: "Grant Grove", totalsites: "50", firstcome: "0", reservable: "50", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/seki/planyourvisit/crystal_springs.htm", description: "All sites are reservation only. 𝗢𝗽𝗲𝗻𝗶𝗻𝗴 𝗗𝗮𝘁𝗲: June 23, 2023 (subject to change) Individual site reservations begin on June 7, 2023, on a two-day rolling basis. Group site reservations are on a four-month rolling basis. Crystal Springs campground is located 4 miles (6 km) from Kings Canyon Park entrance in the Grant Grove area. The campground is situated under open stands of evergreen trees at an elevation of 6,500 feet (1,980 m). Services can be found in Grant Grove Village.", latitude: 36.745327742713684, longitude: -118.9629581339243},
  {name: "Cumberland Valley Hiker-Biker Campsite", city: "Williamsport", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 95.2 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.562141, longitude: -77.853069},
  {name: "Cut Bank", city: "", totalsites: "14", firstcome: "14", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/poi/258798", description: "The Cut Bank campground is located on the east side of Glacier National Park and provides a sense of peace and quiet that may not be found in larger campgrounds within the park. The campground is accessed by a 5 mile dirt road off of highway 89. The Campground is located among trees, providing shade and privacy for tent campers. RVs are not recommended due to the nature of the road and campground layout. Trailheads are near the campground for day hiking use or extended trips in the backcountry.", latitude: 48.6059915915, longitude: -113.376949654},
  {name: "D. H. Day Campground", city: "Glen Arbor", totalsites: "87", firstcome: "0", reservable: "88", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/slbe/planyourvisit/dhdaycamp.htm", description: "The rustic D.H. Day Campground is located in the northern district of the Sleeping Bear Dunes National Lakeshore, on the lower peninsula of Michigan. This beautifully wooded campground is one of the most popular in northern Michigan, with easy access to the Dune Climb, the Pierce Stocking Scenic Drive, and museums and the historic village of Glen Haven. and is located only 2 miles from the restaurants and shops of beautiful downtown Glen Arbor.", latitude: 44.89615, longitude: -86.02006},
  {name: "D. H. Day Campground Group Campground", city: "Glen Arbor", totalsites: "4", firstcome: "0", reservable: "4", fees: "40.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "The D.H. Day Group Campground offers more rustic camping. You will find dirt roads, vault toilets, and ready access to the Lake Michigan beach. There are no electric hook-ups or showers. The campground is located in the northern part of the park mid-way between Glen Haven and Glen Arbor.", latitude: 44.89615, longitude: -86.02006},
  {name: "Daisy Farm Campground", city: "Houghton", totalsites: "25", firstcome: "22", reservable: "3", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Isle Royale's largest campground is located at Daisy Farm, in Rock Harbor Channel, off of the Rock Harbor Trail, Daisy Farm Trail, and Mount Ojibway Trail. Boats can stay overnight at dock. Ranger Station staffed seasonally. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 16 Depth at dock (normal conditions): 9’. Access: Foot/canoe/kayak/private boat", latitude: 48.09208065079491, longitude: -88.59622128173942},
  {name: "Davis Bayou Campground", city: "Ocean Springs", totalsites: "52", firstcome: "0", reservable: "52", fees: "22.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/250796", description: "Gulf Islands National Seashore's Davis Bayou Campground offers tent and recreational vehicle camping experiences in a wooded area next to Davis Bayou in Mississippi.", latitude: 30.397808, longitude: -88.795595},
  {name: "Dayboard 9 Campground", city: "Fort Smith", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/bica/planyourvisit/day-board-nine.htm", description: "This small boat in only campground is located at Dayboard 9, approximately 9 miles south of Okabeh Marina. During the summer season (Memorial Day - Labor Day) this location features a courtesy dock and floating comfort station. The primitive gravel campsites include picnic tables, bear boxes, and fire pits.", latitude: 45.2651648, longitude: -108.0028519},
  {name: "Deep Creek Campground", city: "Bryson City", totalsites: "92", firstcome: "0", reservable: "92", fees: "30.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "", description: "Deep Creek Campground—elevation 1,800 feet (~549 m)—is located next to one of the park's most popular creeks. Hookups and showers are not provided, but flush toilets and drinking water are available. Whether blanketed in bright spring wildflowers or vivid fall colors, the scenery at Deep Creek never disappoints.", latitude: 35.46031234620795, longitude: -83.43466297423731},
  {name: "Deerlodge Park Campground", city: "Dinosaur", totalsites: "7", firstcome: "7", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/dino/planyourvisit/deerlodgecampground.htm", description: "Deerlodge Park Campground is located 51 miles (82 km) east of the Canyon Visitor Center. It is located on the Yampa River at the boat ramp at the head of Yampa Canyon. It has seven shady sites suitable for tents. The sites have tables and fire pits. There is drinking water and vault toilets available seasonally, but no showers. Deerlodge Park Campground is open year-round, but winter access can be very difficult due to snow. When the Yampa River exceeds an 18,000 cfs flow rate, the campground will flood.", latitude: 40.4464807631185, longitude: -108.5133041338832},
  {name: "Desert View Campground (Reservations Required)", city: "Grand Canyon", totalsites: "49", firstcome: "0", reservable: "49", fees: "18.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grca/planyourvisit/desert-view-campground.htm", description: "At the East Entrance to the park, Desert View Campground offers a peaceful setting with 49 campsites. Sites accommodate tents or smaller RV’s, or vehicles with small travel trailers (30 ft. total length). Campsites available by reservation only at www.recreation.gov, and can be made up to 6 months in advance between Saturday April 15, through the night of Sunday October 15, 2023. Make reservations online or by phone (877-444-6777). It is possible to make same day call-in and/or online reservations.", latitude: 36.03893681571232, longitude: -111.82260877399585},
  {name: "Elk Prairie Campground", city: "Crescent City", totalsites: "75", firstcome: "0", reservable: "75", fees: "35.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/redw/planyourvisit/elk-prairie-campground.htm", description: "Enjoy ancient coast redwoods, grazing Roosevelt elk and black-tailed deer in Elk Prairie, easy access to over 70 miles of hiking and biking trails, and seasonal ranger-led programs. Cabins available. Located in the heart of Prairie Creek Redwoods State Park, 6 miles north of Orick, CA.", latitude: 41.35982485324314, longitude: -124.02943276711778},
  {name: "Devils Alley Hiker-Biker Campsite", city: "Little Orleans", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 144.5 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.622156, longitude: -78.411328},
  {name: "Devils Garden Campground", city: "Moab", totalsites: "51", firstcome: "0", reservable: "51", fees: "25.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/arch/planyourvisit/camping.htm", description: "Camp among slickrock outcroppings at Devils Garden Campground, 18 miles from the park entrance. You can reserve standard campsites up to 6 months in advance for stays March 1-October 31. All sites are usually reserved months in advance. Between November 1 and February 28, sites are first-come, first-served. Facilities include drinking water, picnic tables, grills, and both pit-style and flush toilets.", latitude: 38.7773329174, longitude: -109.588799042},
  {name: "Dingmans Campground", city: "Dingmans Ferry", totalsites: "136", firstcome: "136", reservable: "136", fees: "f", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.dingmanscampground.com/", description: "Dingmans Campground is a 136-site rustic campground located in Dingmans Ferry, Pennsylvania. Dingmans Campground offers a variety of settings with campsites along the river, forest campsites, as well as RV, water, and electric campsites. Group campsites for 20 to 40 people available. Camping is available from April until October most years. Telephone 570-828-1551 for details.", latitude: 41.2110633, longitude: -74.8736983995},
  {name: "Dog Canyon Campground", city: "Salt Flat", totalsites: "15", firstcome: "0", reservable: "15", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/dog_canyon_campground.htm", description: "Dog Canyon is a two-hour drive from the Pine Springs area. The Dog Canyon Campground sits at 6,300 feet in elevation and has 9 tent and 4 RV sites. There is one group site at Dog Canyon that can be reserved for a group of 10-20 individuals. Campground amenities include drinking water and flush toilets (there are NO hookups, dump stations or showers). Only cooking stoves are permitted; charcoal, wood fires, and propane firepits are not permitted.", latitude: 31.9948983481, longitude: -104.832847248},
  {name: "Dorst Creek Campground", city: "Sequoia National Park", totalsites: "222", firstcome: "0", reservable: "202", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/dorst.htm", description: "Not currently taking reservations for summer 2023, due to winter storm damage. All sites are reservation only. Ten miles (16 km) from the Giant Forest, this Campground rests under open stands of evergreen trees at an elevation of 6,800 feet (2,073 m). This centrally located campground is ideal for those exploring both parks. The trail to the Muir Grove of giant sequoias begins here.", latitude: 36.636171213229, longitude: -118.80850264793725},
  {name: "Dosewallips Campground (Walk-In Only)", city: "Port Angeles", totalsites: "14", firstcome: "14", reservable: "0", fees: "15.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/dosewallips-area-brochure.htm", description: "Perfect for secluded tent camping. The access road is washed out 6.5 miles from the campground and it is not vehicle accessible.", latitude: 47.7373029, longitude: -123.1647009},
  {name: "Doughton Park Campground", city: "Laurel Springs", totalsites: "108", firstcome: "77", reservable: "31", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/blri/planyourvisit/doughton-park-cg.htm", description: "Doughton Park Campground is nestled in a landscape of mountains and open meadows. Explore the history of early settlers, enjoy spectacular shows of flame azalea and rhododendron, stretch your legs on area hiking trails, and view wildlife such as white-tailed deer, raccoons, red and gray foxes, and bobcats. Originally known as The Bluffs, this area is now part of the 7000-acre Doughton Park, named in honor of Congressman Robert Doughton, a long-time advocate and supporter of the Blue Ridge Parkway.", latitude: 36.42949264609475, longitude: -81.15577587281024},
  {name: "Douglas Spring", city: "Tucson", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "- 4,800 feet elevation - 3 campsites (6 people max per site)", latitude: 32.227981, longitude: -110.606875},
  {name: "Dry Creek Group Campground", city: "f", totalsites: "2", firstcome: "0", reservable: "2", fees: "80.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/whis/planyourvisit/dry-creek.htm", description: "Dry Creek Group Campground includes two large campsites. Each site is primitive and may be reserved for groups of up to 50 people (20 people minimum). The campsites are located within oak woodland on a peninsula surrounded on three sides by Whiskeytown Lake. The primitive campground is located near Brandy Creek Marina.", latitude: 40.6253167, longitude: -122.5807},
  {name: "Dry Gulch Campground", city: "f", totalsites: "9", firstcome: "0", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cure/planyourvisit/camp_dry_gulch.htm", description: "Dry Gulch Campground is located 17 miles west of Gunnison just north of U.S. Highway 50. The campground is shaded by large cottonwood trees and can accommodate tents or medium sized RVs. All sites are first-come first-served. A horse corral is available. Elevation: 7560 feet", latitude: 38.4841144836276, longitude: -107.188437346818},
  {name: "Duck Harbor Campground", city: "Bar Harbor", totalsites: "5", firstcome: "0", reservable: "5", fees: "20.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/acad/planyourvisit/duckharbor.htm", description: "Duck Harbor Campground is located on Isle au Haut, a rugged island off the coast of Stonington, Maine. This campground has five sites with lean-to shelters and is a great launch point for paddling excursions. Remote and inaccessible to automobiles, Isle au Haut is linked to the mainland by mailboat.", latitude: 44.0277948996318, longitude: -68.6535293990644},
  {name: "Dump Station", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "10.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "", description: "No overnight camping allowed. Dump Station is for dumping garbage and RV sewage.", latitude: 39.006544, longitude: -114.215989},
  {name: "Dunbar Group Site", city: "Porter", totalsites: "1", firstcome: "0", reservable: "1", fees: "100.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "", description: "The Dunbar Group Site holds up to 30 people and is located less than 500 feet from the beach. The site overlooks the Great Marsh and Lake Michigan. Parking and restrooms are located in the Dunbar Beach parking lot, 400 feet from the site.", latitude: 41.68266002966044, longitude: -87.00171411037445},
  {name: "Duncan Bay Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Duncan Bay Campground is located on the west end of Duncan Bay, on the northeast end of Isle Royale National Park. Boats can stay overnight at dock. Stay Limit: 3 nights from June 2 – Labor Day annually. Shelters: 2 Depth at dock (normal conditions): 6’ Access: Canoe/kayak/private boat", latitude: 48.150633110830206, longitude: -88.52247086940037},
  {name: "Duncan Narrows Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Duncan Narrows Campground is located in the narrows of Duncan Bay on the northeast end of Isle Royale National Park. Boats can stay overnight at dock. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 2 Depth at dock (normal conditions): 6’. Access: Canoe/kayak/private boat", latitude: 48.169312119814265, longitude: -88.47796476335344},
  {name: "Dundo Group Campground", city: "Crozet", totalsites: "3", firstcome: "0", reservable: "3", fees: "75.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/shen/planyourvisit/dundo-group-campground.htm", description: "Dundo Group Campground (mile 83.7) is a small, beautiful campground located in the southern part of Shenandoah National Park that offers group campsites, only. The Appalachian Trail passes through the campground. All sites include fire rings and picnic tables.", latitude: 38.2341, longitude: -78.7216},
  {name: "Dunewood Campground", city: "Beverly Shores", totalsites: "66", firstcome: "0", reservable: "66", fees: "25.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/indu/planyourvisit/campgrounds.htm", description: "Campground consists of two loops containing 66 camp-sites (53 conventional drive-in sites and 13 walk/carry-in sites). Four sites are wheelchair accessible (numbers 15, 30, 41 and 55). Each loop has modern restrooms and hot/cold showers. Some sites have limited recreational vehicle length. Convenience stores and a gas station are located 1/4 mile north of the campground at the intersection of U.S. Highway 12 and Broadway Ave. in the town of Beverly Shores. Campground closed during winter, Nov 2 - Mar 31.", latitude: 41.671966000578976, longitude: -86.9837859988566},
  {name: "Dyea Campground", city: "f", totalsites: "22", firstcome: "22", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/klgo/planyourvisit/campgrounds.htm", description: "The Dyea Campground is located 9 miles from Skagway. Nestled in the trees along the banks of the Taiya River with limited amenities, the Dyea Campground is a restful place from busy town. The campground is within a short drive or bike ride of the historic townsite of Dyea and a half mile from the Chilkoot Trailhead. The campground is monitored by National Park Service rangers and is open when free of snow.", latitude: 59.5072499, longitude: -135.3504976},
  {name: "East Chickenbone Campground", city: "Houghton", totalsites: "4", firstcome: "3", reservable: "1", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "An inland campground located on the east end of Chickenbone Lake, off of the East Chickenbone Trail. No boater access. Canoe/kayak access using 1.2 mile portage from McCargoe Cove or 0.2 mile portage from Lake Livermore. Be aware that the campground is not on the shoreline - West Chickenbone Campground is more accessible for watercraft. Stay Limit: 2 nights from June 1 – Labor Day annually. Access: Foot/canoe/kayak", latitude: 48.073437514172, longitude: -88.69425862129464},
  {name: "East Elk Creek Group Campground", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "53.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cure/planyourvisit/camp_east_elk.htm", description: "East Elk Creek is a group campsite that can accommodate a total of 50 people. The site is located 16.5 miles west of Gunnison just north of U.S. Highway 50. The campsites are located under large Cottonwood trees and space is available for tents or RVs. Elevation: 7535 feet", latitude: 38.484167, longitude: -107.188333},
  {name: "East Portal Campground", city: "f", totalsites: "15", firstcome: "15", reservable: "0", fees: "f", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cure/planyourvisit/camp_east_portal.htm", description: "The East Portal Campground is located within Curecanti National Recreation Area, but is adjacent to and only accessible from Black Canyon of the Gunnison National Park, which has a $30 entrance fee. The campground is located at the bottom of the canyon, along the Gunnison River. It is a small campground shaded by box elder trees. East Portal is closed in winter, when the Bureau of Reclamation closes the East Portal Road. Elevation: 6526 feet", latitude: 38.527059599536, longitude: -107.64948401793},
  {name: "East Portal Campground", city: "Montrose", totalsites: "15", firstcome: "15", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/blca/planyourvisit/camp_east_portal.htm", description: "The East Portal Campground is located within Curecanti National Recreation Area, but is adjacent to and only accessible from Black Canyon of the Gunnison National Park, where the entrance fee is $30. The campground is located at the bottom of the canyon, along the Gunnison River. It is a small campground shaded by box elder trees. East Portal Road is closed to vehicles but open to pedestrians and cyclists in winter. The Bureau of Reclamation determines when the East Portal Road closes. Elevation: 6526 feet", latitude: 38.526896, longitude: -107.649306},
  {name: "Echo Bay Campground (Lake Mead)", city: "Boulder City", totalsites: "52", firstcome: "37", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/poi/10108391", description: "Our Echo Bay Park is a fisherman’s and boater’s paradise. Located on the north end of Lake Mead, this secluded park offers a variety of outdoor activities. Close to the “must-see” wonder of the Valley of Fire State Park and the ruins of the historic town of St. Thomas, visitors have an array of hiking and sightseeing opportunities. The large RV sites provide room for your boat, and the free on-site launch ramp are ideal for boaters.", latitude: 36.30813734081407, longitude: -114.4368978637836},
  {name: "Echo Bay RV Park (Lake Mead)", city: "f", totalsites: "55", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://lakemeadmohaveadventures.com/echo-bay-rv-village/", description: "Echo Bay Park is a fisherman’s and boater’s paradise. Located on the north end of Lake Mead, this secluded park offers a variety of outdoor activities. Close to the “must-see” wonder of the Valley of Fire State Park and the ruins of the historic town of St. Thomas, visitors have an array of hiking and sightseeing opportunities. The large RV sites provide room for your boat, and the free on-site launch ramp are ideal for boaters. Enjoy camping at the Echo Bay RV Village! Whether you stay a night or a mon", latitude: 36.310075538623266, longitude: -114.43199832188293},
  {name: "Echo Park Campground", city: "Dinosaur", totalsites: "22", firstcome: "17", reservable: "1", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/dino/planyourvisit/echoparkcampground.htm", description: "Situated along the Green River at the base of towering cliffs, the Echo Park Campground provides a unique camping experience in Dinosaur National Monument. Steamboat Rock dominates the view. Fremont petroglyphs are located on the canyon walls. Bighorn sheep and mule deer frequently roam through the campground. Unimproved hiking trails lead to the confluence of the Green and Yampa Rivers or to views of the Mitten Park Fault. The campground is located 38 miles (61 km) north of the Canyon Visitor Center.", latitude: 40.5208668444, longitude: -108.993230693},
  {name: "El Morro Campground", city: "Ramah", totalsites: "9", firstcome: "9", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/elmo/planyourvisit/campgrounds.htm", description: "El Morro National Monument operates a nine-site campground year-round. First come-first served. Each site has a graveled tent pad, picnic table and ground grill for fires. During the warmer months, water is available at spigots that are centrally located along the campground loop road. Once the overnight low's begin to reach freezing temperatures, water is turned off for the season. There are no hookups for RVs. The length limit on all motor homes is 27 feet overall. One site, #5, is handicapped accessible.", latitude: 35.0368936454882, longitude: -108.33681211762},
  {name: "Elk Creek Campground", city: "f", totalsites: "155", firstcome: "49", reservable: "111", fees: "16.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/cure/planyourvisit/camp_elk_creek.htm", description: "Elk Creek Campground is located 16 miles west of Gunnison on U.S. Highway 50 on the shore of Blue Mesa Reservoir. We recommend making reservations prior to coming to Curecanti because cell service and first-come, first-served sites may be unavailable. Reservations may be made on recreation.gov from mid-May to mid-September. All types of camping from tents to large RVs can be accommodated. Very few sites have trees. This is the only campground with electricity (Loop D only) at Curecanti. Elevation: 7540 feet", latitude: 38.4674202781, longitude: -107.172066844},
  {name: "Elk Mountain Campground", city: "Hot Springs", totalsites: "61", firstcome: "62", reservable: "62", fees: "24.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/wica/planyourvisit/campgrounds.htm", description: "This 62-site campground is open year round. Sites can be reserved at www.recreation.gov late May through September, and are first-come, first-served at all other times. Two sites are handicap-accessible. Flush toilets and drinking water are available late May through September. Fees are half-price when water is not available. Payment is by credit or debit card only. Two group campsites are also available through www.recreation.gov. Ranger programs are offered nightly in the amphitheater during the summer.", latitude: 43.56547071700004, longitude: -103.48823452199997},
  {name: "Elkmont Campground", city: "Gatlinburg", totalsites: "211", firstcome: "0", reservable: "211", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Elkmont Campground—elevation 2,150 feet (~655 m)—is the closest frontcountry campground to Sugarlands Visitor Center in the North District of the park. The climate is characterized by mild winters and hot, humid summers. Mountain ranges and a pristine river are the backdrop for your Elkmont adventure. Camping for both tents and RVs available.", latitude: 35.65875714556928, longitude: -83.5827404743859},
  {name: "Elkmont Group Campground", city: "Gatlinburg", totalsites: "4", firstcome: "0", reservable: "4", fees: "53.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "These sites offers an ideal setting for group camping excursions and outdoor enthusiasts of all kinds.", latitude: 35.6596758456, longitude: -83.5863818735},
  {name: "Elliott Key Campground", city: "f", totalsites: "20", firstcome: "0", reservable: "0", fees: "35.00", showers: nil, toilets: nil, phone: "", url: "https://www.nps.gov/bisc/planyourvisit/elliottkey.htm", description: "Elliott Key campground is located on an island. The only access is by boat. Elliott Key features waterside and partially forested camping areas, picnic tables, and grills. Restrooms with sinks and cold water showers are available. A mile-long loop hiking trail starts near the campground. Another walking trail, the infamous \"Spite Highway\" runs approximately six miles down the center of the island. At low tide, the depth of the entrance to the marina is approximately 2 1/2 feet.", latitude: 25.4535845128, longitude: -80.1958850931},
  {name: "Emigrant Campground", city: "Death Valley", totalsites: "10", firstcome: "10", reservable: "0", fees: "0.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/deva/planyourvisit/emigrant-campground.htm", description: "First come / first served tent only campground with no additional fee. Located next to CA 190 at 2100' feet in elevation. Emigrant campground overlooks the Cottonwood Mountains which is a part of the Panamint Range. This is a small campground with 10 sites southwest of Stovepipe Wells Ranger Station. A building with flush toilets is 270 feet down a dirt trail just outside of the campground.", latitude: 36.4965216499, longitude: -117.227072437},
  {name: "Erbie Campground", city: "Jasper", totalsites: "21", firstcome: "18", reservable: "3", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/camping.htm", description: "Erbie Campground is a first come, first serve campground. Erbie has 14 drive in camp sites, 2 walk in tent only sites, and 5 group sites. There are no electrical amenities, flush restrooms, or water available.", latitude: 36.0713269988, longitude: -93.2149368998},
  {name: "Esker Campsite", city: "Patten", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "", description: "Hike or bike approximately 2.2 miles from the Wassataquoik Gate to the Esker Campsite, located on the bank of Wassataquoik Stream.", latitude: 45.9083960065, longitude: -68.6795909867},
  {name: "Eureka Dunes Campground (Primitive)", city: "Death Valley", totalsites: "7", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/deva/planyourvisit/eureka-dunes-campground-primitive.htm", description: "Remote campground at the base of Eureka Dunes (the tallest sand dunes in California and home to several endemic plant species). This campground is located at 2,880ft and is only accessible to high-clearance vehicles. Please drive only on existing roads and tread lightly on the dunes to protect this fragile and unique environment.", latitude: 37.112, longitude: -117.6809},
  {name: "Evans Campground", city: "Evans", totalsites: "43", firstcome: "0", reservable: "43", fees: "23.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-evans.htm", description: "Evans Campground runs lengthwise along the lakeshore. One end is capped with the group sites and the boat launch. The other end is a Day Use Area with a covered picnic area and swim beach. The campground is busy in the summer and tends to fill on the weekends.", latitude: 48.69881814742667, longitude: -118.01765146027128},
  {name: "Evans Group Site", city: "Evans", totalsites: "1", firstcome: "0", reservable: "1", fees: "55.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-group-evans.htm", description: "The Evans Group site is located in a clearing on one end of Evans Campground. The site holds a maximum of 25 people, with ample space for RV parking and/or tents. Clusters of pine trees provide shade. The boat launch is located nearby and a Day Use Area with covered picnic area and swim beach caps the other end of the campground. The campground itself is busy and may fill on the weekends.", latitude: 48.701517, longitude: -118.018959},
  {name: "Evitts Creek Hiker-Biker Campsite", city: "Hagerstown", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 180.1 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.616979, longitude: -78.73259},
  {name: "Exit Glacier Campground", city: "Seward", totalsites: "12", firstcome: "12", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "Exit Glacier has a 12-site, walk-in, tent-campground. Sites are available on a first-come, first-served basis. There are no reservations or camping fees. There is a fourteen-day stay limit. The campground frequently fills by early evening in July and August. A central food storage, cooking and dining shelter is provided. Cooking and/or storing food in campsites is prohibited. There is a pump for drinking water and pit toilets are available. Pets are not permitted in campsites.", latitude: 60.1909682429161, longitude: -149.618120247669},
  {name: "Fairholme Campground", city: "Port Angeles", totalsites: "88", firstcome: "0", reservable: "88", fees: "24.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/olym/planyourvisit/lake-crescent-area-brochure.htm", description: "(10/2/2023) CLOSED for the winter, Reopening for summer 2024. Neighboring Lake Crescent, Fairholme includes lakeside campsites and a nearby boat launch.", latitude: 48.070079999223964, longitude: -123.91813840922975},
  {name: "Feldtmann Lake Campground", city: "Houghton", totalsites: "7", firstcome: "5", reservable: "2", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Feldtmann Lake Campground is located on the shoreline of Feldtmann Lake, off the Feldtmann Lake and Feldtmann Ridge Trails, on the southwest end of Isle Royale National Park. This is an inland campground. Stay Limit: 2 nights from June 1 – Labor Day annually. Access: Foot", latitude: 47.84857314451423, longitude: -89.18297185832984},
  {name: "Fifteenmile Creek Campground", city: "f", totalsites: "10", firstcome: "0", reservable: "10", fees: "40.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/252968", description: "Mile 140 (Allegany County, MD) - The campground has nine single drive-in campsites. There is one group site available that does not have direct vehicle access. The group site can accommodate 30 people. Each single will accommodate up to eight people. Single campsites have a picnic table, fire ring and grill. Pit toilets are located between campsite #3 and campsite #4 along the dead end circle. Potable water is available from mid-April to mid-November No motor vehicle traffic is allowed on the towpath.", latitude: 39.625303049257006, longitude: -78.38559864575001},
  {name: "Finnegan's Point", city: "Skagway", totalsites: "6", firstcome: "0", reservable: "6", fees: "54.60", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/klgo/planyourvisit/finnegans-point.htm", description: "Finnegan's Camp is the first backcountry campground 5mi (8m) on the Chilkoot Trail. It is located near the Taiya River. On clear days you can see the Irene glacier, a hanging glacier, across the river from the campground. In order to camp overnight along the Chilkoot Trail on the U.S. side you must purchase a backcountry permit from the Chilkoot Trail Center in Skagway.", latitude: 59.575423, longitude: -135.3348773},
  {name: "Fish Creek", city: "West Glacier", totalsites: "178", firstcome: "0", reservable: "178", fees: "23.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Fish Creek is the second largest campground in the park and is one of the campgrounds in the park that takes reservations. Sites within the campground are surrounded by trees and provide shade and some privacy to both tent and RV campers. Some sites, though not directly on the shore of Lake McDonald, offer filtered views of the lake.", latitude: 48.5501313186, longitude: -113.982962737},
  {name: "Fishing Bridge RV Park", city: "Yellowstone National Park", totalsites: "310", firstcome: "0", reservable: "310", fees: "89.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/yell/planyourvisit/fishingbridgecg.htm", description: "Fishing Bridge RV Park—elevation 7,800 feet (2377 m)—is located near the Yellowstone River where it exits Yellowstone Lake on its way toward the Grand Canyon of the Yellowstone. Only campground in Yellowstone to offer water, sewer, and electrical hookups. Because grizzly bears frequent the area, no tents or tent campers are allowed. Yellowstone National Park Lodges provides reservations for this campground.", latitude: 44.5647552489551, longitude: -110.369472282319},
  {name: "Flamingo Campground", city: "Flamingo", totalsites: "274", firstcome: "28", reservable: "159", fees: "50.00", showers: "Free - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "", description: "The Flamingo campground is one of two drive-in campgrounds accessible from the Homestead entrance of the park. It offers solar-heated showers, two dump stations, picnic tables, grills, and an amphitheater for seasonal Ranger programs. Flamingo has several hiking trails and canoe trails, and opportunities for saltwater fishing are plentiful. Check at the visitor center for a daily schedule of Ranger guided programs.", latitude: 25.137301004201746, longitude: -80.93947649002075},
  {name: "Fort Casey State Park", city: "Coupeville", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "Washington State Parks operates two campgrounds in the Reserve. You can find information and reservations at http://www.parks.wa.gov/. Reservations are strongly recommended, especially during the summer months.", latitude: 48.1568610743654, longitude: -122.67400959303},
  {name: "Fort Charlotte Backcountry Campgrounds", city: "Grand Portage", totalsites: "2", firstcome: "0", reservable: "2", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/grpo/planyourvisit/camping.htm", description: "Two backcountry campsites, accessible to hikers and canoeists, are located at the Pigeon River (Canada/U.S. border) end of Grand Portage National Monument. Each campsite is limited to nine people. Camping permits are required and issued by the park. A permit system allows campers to reserve a campsite in advance. Allow a minimum of two weeks in advance for permit processing.", latitude: 48.0036880999, longitude: -89.8283403009},
  {name: "Fort Ebey State Park", city: "Coupeville", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "Washington State Parks operates two campgrounds in the Reserve. You can find information and reservations at http://www.parks.wa.gov/. Reservations are strongly recommended, especially during the summer months.", latitude: 35.6474198892006, longitude: -101.628934056876},
  {name: "Fort Pickens Campground", city: "Pensacola Beach", totalsites: "200", firstcome: "0", reservable: "188", fees: "25.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/234704", description: "The Fort Pickens Campground is one of the top ten busiest campgrounds in all of the 420+ national parks and sites. Available all year round it provides access to white sandy beaches and trails, including the Florida National Scenic Trail. The campground office is located in a historic white building located halfway down Fort Pickens Areas on Fort Pickens Road. Hours are 9 a.m. to 5 p.m. Check-in begins at 2 p.m.", latitude: 30.3232083475706, longitude: -87.27597277635039},
  {name: "Fort Spokane Campground", city: "Davenport", totalsites: "67", firstcome: "0", reservable: "67", fees: "23.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-fortspokane.htm", description: "Fort Spokane Campground is part of a large recreation complex with the Fort Spokane visitor center, day use area, and boat launch within walking distance. The campground itself sprawls among towering pines, with plenty of space for RVs and walk-in sites for tent campers. Fort Spokane is a popular campground conveniently located near the visitor center and nearby gas stations and stores. The back loops of the campground will open May 4, 2022 due to dead tree removal.", latitude: 47.91057783564893, longitude: -118.3059186711921},
  {name: "Fort Wadsworth Campsite", city: "f", totalsites: "7", firstcome: "0", reservable: "7", fees: "30.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "", description: "The Fort Wadsworth Campground is currently closed.", latitude: 40.599998, longitude: -74.054771},
  {name: "Frijole Horse Corral Campground", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "60.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10104621", description: "The Frijole Horse Corral Group Campsite is the only facility for horse users on the east side of the park. The visitor horse corrals and campsites may be reserved up to six months in advance of the visit. This camping area is immediately adjacent Highway 62/180 on the Frijole Ranch access road. Due to proximity to both the highway and the access road, this site has little privacy and is subject to road noise. The Frijole Horse Corral has a capacity of 10 animals.", latitude: 31.8995224439952, longitude: -104.799849430182},
  {name: "Frisco Campground", city: "Frisco", totalsites: "117", firstcome: "0", reservable: "117", fees: "28.00", showers: "Cold- Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/251430", description: "Frisco Campground with 127 sites is located west of the Cape Hatteras Lighthouse near the village of Frisco, North Carolina. The campground is tucked between the sand dunes and shrub thicket. A public boardwalk allows easy ocean access for campers, and public bathhouses, grills, and picnic tables are available. Open April 7-Nov. 27, 2023.", latitude: 35.237075, longitude: -75.603357},
  {name: "Fritch Fortress", city: "Fritch", totalsites: "15", firstcome: "15", reservable: "0", fees: "0.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "A caprock campground, overlooking Lake Meredith located on Fritch Fortress Road. Open for boaters, campers, swimmers, and other approved water activities. Picnic tables, shade shelters, and grills are available. There is a restroom with potable water, showers, and flush toilets. Pull-through parking is available.", latitude: 35.6817457818143, longitude: -101.598311253546},
  {name: "Fruita Campground", city: "Torrey", totalsites: "71", firstcome: "0", reservable: "65", fees: "25.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/care/planyourvisit/fruitacampground.htm", description: "The 71 site Fruita campground is the only developed campground in the park, offering picnic tables, fire rings, restrooms, water and a dump station. A $25 nightly fee is charged. It is open year-round. From March through October, campsites are able to be reserved on www.recreation.gov. From November through February, campsites are filled on a first-come, first-served basis.", latitude: 38.2825607383, longitude: -111.248097934},
  {name: "Furnace Creek Campground", city: "Death Valley", totalsites: "136", firstcome: "0", reservable: "136", fees: "22.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/deva/planyourvisit/furnace-creek-campground.htm", description: "Located near Furnace Creek Visitor Center, Furnace Creek Campground is the only NPS campground in the park that accepts reservations and has sites with full RV hookups as well as dry RV and tent sites. Reservations can be made up to 6 months in advance for camping dates between 10/15 and 4/15; the remainder of the year the campground is first-come-first-serve. This is the most popular campground in the park and reservations are strongly recommended.", latitude: 36.4657541512, longitude: -116.868157545},
  {name: "Garden Key Campground", city: "Homestead", totalsites: "8", firstcome: "8", reservable: "0", fees: "15.00", showers: "None", toilets: "Composting Toilets - year round", phone: "No", url: "", description: "Garden Key, home to Fort Jefferson, has a primitive campground only a short walk from public dock and piers. Individual sites can accommodate up to three 2-person tents (6 persons max per-site) on a first-come, first served basis. In the event a regular campsite is unavailable, an overflow area will be made available. Upon arrival all overnight visitors will be provided a place to camp. For parties of 10 or more, a group campsite is available through our website.", latitude: 24.6270109567431, longitude: -82.8734064423241},
  {name: "Gates of Lodore Campground", city: "Maybell", totalsites: "19", firstcome: "19", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/dino/planyourvisit/gatesoflodorecampground.htm", description: "Gates of Lodore Campground is located on the Green River at the boat ramp at the head of Lodore Canyon. The campground is popular with river rafters who often stay here before launching on the Green River. There are 19 sites, some with shade. The sites have tables and fire pits or grills. During the summer, there is running water and vault toilets, but no showers. The campsites can accommodate tents and RVs (but there are no hook-ups).", latitude: 40.72492348417907, longitude: -108.88782437201075},
  {name: "Gateview Campground", city: "f", totalsites: "6", firstcome: "6", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cure/planyourvisit/camp_gateview.htm", description: "Gateview Campground is located in a deep, narrow canyon at the extreme south end of the Lake Fork Arm of Blue Mesa Reservoir. Take CO Highway 149 7 miles west of Powderhorn, then 6 miles north on the gravel Blue Mesa Cutoff Road. All sites are first-come first-served. Gateview is best suited for tent camping. Due to the narrow gravel road, even small RVs or pop-up trailers are not recommended. Elevation: 7538 feet", latitude: 38.3844578817442, longitude: -107.242874927003},
  {name: "Gator Head", city: "Ochopee", totalsites: "9", firstcome: "9", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "This is a primitive campground, which contains nine campsites. No water. Vault toilets are available. This campground is accessible only by permitted off-road vehicles, biking or hiking.", latitude: 26.2238175484, longitude: -81.2677556747},
  {name: "Gauley Tailwaters", city: "f", totalsites: "18", firstcome: "18", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "", description: "The National Park Service offers primitive camping at Gauley Tailwaters, located just below Summersville Dam. From Route 19 at Mt. Nebo, take Route 129 west across the dam, then turn left at the river access sign. Bear right through the parking lot to the campsites. There are 18 drive-in sites for tents and RVs. Camp only at designated sites. RV, camper, or car camping in non-designated sites, walk-in sites, or any parking area is strictly prohibited.", latitude: 38.21439035407238, longitude: -80.88885151780727},
  {name: "Gifford Campground", city: "Gifford", totalsites: "42", firstcome: "0", reservable: "42", fees: "23.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-gifford.htm", description: "Gifford campground stretches along the lakeshore, giving almost every campsite a sweeping view of Lake Roosevelt and shoreline access. Towering pines provide ample shade. Gifford has a boat launch and dock, a dump station, and a fish cleaning station. A group site provides space and privacy for larger parties. Gifford Campground is conveniently located along WA 25 next to the Gifford Ferry.", latitude: 48.2858575736509, longitude: -118.142799633773},
  {name: "Gifford Group Site", city: "Gifford", totalsites: "1", firstcome: "0", reservable: "1", fees: "55.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-group-gifford.htm", description: "The Gifford group site is located in a clearing on one end of Gifford Campground. The site holds a maximum of 50 people, with ample space for RV parking and/or tents. Clusters of pines provide shade. The boat launch with parking is located at the other end of the campground. The campground itself is busy and may fill on the weekends.", latitude: 48.288223, longitude: -118.143881},
  {name: "Glacier Basin Campground", city: "Estes Park", totalsites: "150", firstcome: "0", reservable: "153", fees: "35.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/romo/planyourvisit/gbcg.htm", description: "Glacier Basin Campground is closed for the 2023 season. Glacier Basin Campground is a seasonal campground open late May through mid-September.", latitude: 40.32980715336969, longitude: -105.59562927944023},
  {name: "Glade Creek Campground", city: "Prince", totalsites: "11", firstcome: "11", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - year round", phone: "Yes - year round", url: "", description: "This forested campground offers both tent and RV sites near the banks of the New River and the Glade Creek Trailhead. There are 6 walk-in tents sites and 5 drive-in sites for tents, RVs, or campers. There is 1 accessible site. Only small (less than 25 feet) and medium (22 - 31.5 feet) RVs or campers are recommended. Camp only at designated sites. RV, camper, or car camping in non-designated sites, walk-in sites, or any parking area is strictly prohibited.", latitude: 37.82841131456489, longitude: -81.01298532714685},
  {name: "Glen Campground", city: "Point Reyes Station", totalsites: "12", firstcome: "1", reservable: "11", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/pore/planyourvisit/glen-campground.htm", description: "Glen Campground is located deep within a wooded valley protected from ocean breezes. The closest beach is 2.5-miles away. One may access Glen Camp by foot or bicycle; not by horse, boat, auto, or RV. The shortest route to Glen Camp is via a 4.6-mile hike from the Bear Valley Trailhead. To ride a bicycle to Glen Camp start at the Five Brooks Trailhead for a 6.3-mile bike ride. No groups (parties of seven or more), horses, or pack animals are allowed. There are 12 regular (e.g., one- to six-person) sites.", latitude: 37.98931070849041, longitude: -122.78848792812425},
  {name: "Goat Island Primitive Camping", city: "Cedar County", totalsites: "2", firstcome: "2", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/mnrr/planyourvisit/campgrounds.htm", description: "Two designated tent camping only areas. The first (southern) campground area is in the process of being established. The second (northern) is targeted to be established in 2022. Primitive camping is allowed anywhere on the Island, with the exception of the established hunting season after Labor Day to January when camping is restricted to the two designated camping areas. Please email or call (605-665-0209) for a back country use permit issued by the Park's HQ Office.", latitude: 42.765746, longitude: -97.066858},
  {name: "Gold Bluffs Beach Campground", city: "Orick", totalsites: "26", firstcome: "0", reservable: "26", fees: "35.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/redw/planyourvisit/gold-bluffs-beach-campground.htm", description: "Experience the wild Pacific coastline and grazing Roosevelt elk in this campground, with easy access to a secluded stretch of beach, Fern Canyon, and 70 miles of hiking and biking trails. Located on the coast within Prairie Creek Redwoods State Park - in the southern part of the parks. Access is on with a narrow, dirt road. Recreational vehicles and trailers are not advised.", latitude: 41.38221738914677, longitude: -124.0698821873994},
  {name: "Goodell Creek Campground", city: "Sedro-Woolley", totalsites: "19", firstcome: "0", reservable: "19", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/246852?tab=info", description: "Goodell Creek Campground is situated in lush, old growth forest on the banks of the Skagit River. Campsites are suitable for tents and small RVs. First-come, first-served during the winter; summer reservations can be made up to 6 months in advance. Nearby recreational opportunities include a raft/kayak launch on the Skagit River and fishing on the Skagit and its tributaries.", latitude: 48.673, longitude: -121.267},
  {name: "Horse camping", city: "Berlin", totalsites: "2", firstcome: "0", reservable: "2", fees: "80.00", showers: "Cold- Seasonal", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/horse-campground-reservation.htm", description: "Horse campers must bring their own horses. Sites have a picnic table, fire ring with grill, and hitching posts. Sites are $80 per night. Maximum 6 people and 6 horses. Up to six vehicles or trailers. Reservations are required 10/16 - 4/14.", latitude: 38.19282100055179, longitude: -75.1574710003365},
  {name: "Gorge Lake Campground", city: "f", totalsites: "8", firstcome: "8", reservable: "8", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/10004932", description: "Gorge Lake Campground is located on the bank of Gorge Lake, next to the cascading Stetattle Creek and is a primitive camp with no water. First-come, first-served during the winter. Summer reservations can be made up to 6 months in advance. Nearby recreational opportunities include a boat launch on Gorge Lake, fishing, and hiking trails.", latitude: 48.716, longitude: -121.152},
  {name: "Governors Landing Campground", city: "f", totalsites: "15", firstcome: "15", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/amis/planyourvisit/governors-landing-campground.htm", description: "Governors Landing Campground is open year-round and rarely fills up, except possibly on holiday weekends. It is suitable for tents and RVs up to 28 feet in length. Governors Landing is the only campground in the park with potable water available, though there are no hookups. This campground has access to some of the most popular swimming areas in the park. Picnicking at vacant sites in Governors Landing Campground is allowed 7am to 3pm. Occupying sites outside these times requires payment of camping fees.", latitude: 29.479076370014475, longitude: -101.02886321679989},
  {name: "Grace Island Campground", city: "Houghton", totalsites: "2", firstcome: "2", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Grace Island Campground is located on Grace Island, outside of Washington Harbor, on the southwest end of Isle Royale National Park. Boats can dock overnight. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 2 Depth at dock (normal conditions): 2’ - 4’ Access: Canoe/kayak/private boat", latitude: 47.88249028850224, longitude: -89.21635637438078},
  {name: "Grand Pitch Lean-to", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "A backcountry lean-to along the International Appalachian Trail and Grand Pitch Portage.", latitude: 46.057785, longitude: -68.748256},
  {name: "Grand Sable Lake Boat-In Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Grand Sable Lake on the park's east end has one boat-in backcountry campsite. Campers must provide their own boat and launch at the Grand Sable Lake boat launch. The campground is 1/2 mile south of the boat launch along the east shore. One fire ring and pit toilet. Water source is Grand Sable Lake. Ideal for kayakers and canoers.", latitude: 46.64323548207715, longitude: -86.0319734881054},
  {name: "Grandview Sandbar", city: "Prince", totalsites: "16", firstcome: "16", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - year round", phone: "Yes - year round", url: "", description: "This campground offers shaded wooded campsites near the banks of the New River. There are 10 wooded drive-in sites for RVs, campers, car camping, and tents; 6 walk-in tents sites; and 2 accessible sites by the river. Only small (less than 25 feet) and medium (22 - 31.5 feet) RVs are recommended due to the tight access road. Camp only at designated sites. RV, camper, or car camping in non-designated sites, walk-in sites, or any parking area is strictly prohibited.", latitude: 37.85653168961612, longitude: -81.05256733791589},
  {name: "Grant Village Campground", city: "Yellowstone National Park", totalsites: "430", firstcome: "0", reservable: "430", fees: "39.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/yell/planyourvisit/grantcg.htm", description: "Grant Campground—elevation 7,800 feet (2377 m)—is located in Grant Village, just off the Grand Loop Road at the south end of Yellowstone Lake. It is one of the larger campgrounds in the park. Group and wheel-chair accessible sites are available. Nearby there are stores, a restaurant, gas station, visitor center, and boat ramp. Yellowstone National Park Lodges provides reservations for this campground.", latitude: 44.3942533493729, longitude: -110.563444983232},
  {name: "Grapevine Campground", city: "f", totalsites: "14", firstcome: "0", reservable: "12", fees: "18.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/bica/planyourvisit/grapevine-campground.htm", description: "- Near Fort Smith, Montana - Open All Year! - $18 per night fee (50% off with Senior Pass) - 14 RV and tent sites are located on the north shore of Afterbay lake - RV sites are back in (not pull through) - All sites are handicapped accessible, have fire rings, and picnic tables - Vault toilets are accessible - There are no RV hookups - Trash cans available seasonally (May-Oct) - RV dump station and drinking water available across Afterbay lake near the Headquarters building and Afterbay Campground", latitude: 45.31804610741141, longitude: -107.94067157826737},
  {name: "Grass Shack", city: "Tucson", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "- 5,300 feet elevation - 3 campsites (6 people max per site)", latitude: 32.183854, longitude: -110.592818},
  {name: "Graves Creek Campground", city: "Port Angeles", totalsites: "30", firstcome: "30", reservable: "0", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/east-fork-quinault-river-trail.htm", description: "Located in the Quinault Rain Forest, relax near a serene stream at Graves Creek Campground.", latitude: 47.573831087, longitude: -123.576795443},
  {name: "Great Island Cabin Camp", city: "Core Banks", totalsites: "23", firstcome: "0", reservable: "23", fees: "80.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/calo/planyourvisit/lodging.htm", description: "Great Island Cabin Camp offers 23 rustic, wooden beachfront cabins with porches for individuals and families or small groups of up to 12 people. Cabins range in size from 288 to 960 sq ft., and Cabin 2 is accessible. All cabins are wired for generator use, needed to power electricity. Guests must provide their own generators, no larger than 5,500 watts or equipped with a GFI.", latitude: 34.76055561111111, longitude: -76.41222219444445},
  {name: "Green Island Primitive Camping", city: "f", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/mnrr/planyourvisit/campgrounds.htm", description: "Green Island has five primitive tent campsites with fire rings. Two isolated tent sites on the east side of the island & three adjacent sites on the west side. Fires allowed only in designated fire rings. Island access is by boat only. Please visit the Camping page for rules and regulations. Primitive, Leave No Trace camping is allowed and free. Camp only in designated sites marked with brown camping signs and metal fire rings. Please call us at 605-665-0209 and leave your basic itinerary for your safety.", latitude: 42.86381530885288, longitude: -97.4033088680153},
  {name: "Green River Campground", city: "Jensen", totalsites: "80", firstcome: "27", reservable: "54", fees: "18.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/dino/planyourvisit/greenrivercampground.htm", description: "The Green River Campground is located along the banks of the Green River in a grove of cottonwood trees at an elevation of 4795 (4162 feet). The highly eroded Split Mountain towers to the north of the campground. The famous dinosaur quarry, where you can see 150 million year old dinosaur bones still encased in rock is approximately 5 miles (8 km) from the campground. Nearby is the Split Mountain Boat Ramp where river rafters come off the Green River after trips through Dinosaur National Monument's canyons.", latitude: 40.420977472422145, longitude: -109.24317566803745},
  {name: "Greenbelt Park Campground", city: "Greenbelt", totalsites: "172", firstcome: "0", reservable: "172", fees: "20.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/gree/planyourvisit/campgroundreservations.htm", description: "The park features a 172 site campground and is open year round. The campground is known for its affordability, safety, peaceful surroundings, and National Park Service hospitality. Campers must pay for sites via the online reservation system.", latitude: 38.97908, longitude: -76.89841},
  {name: "Grey Cliffs Campground", city: "f", totalsites: "16", firstcome: "0", reservable: "16", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grba/planyourvisit/campgrounds.htm", description: "For visitors to Great Basin National Park, Grey Cliffs Campground is an ideal place to setup and start exploring. Experience the solitude of the desert, the smell of sagebrush after a thunderstorm, the darkest of night skies, and the beauty of Lehman Caves.", latitude: 38.99115, longitude: -114.219121},
  {name: "Gros Ventre Campground", city: "Kelly", totalsites: "279", firstcome: "0", reservable: "322", fees: "50.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grte/planyourvisit/gros-ventre-campground.htm", description: "The park's largest campground is in the southeast part of the park, closest to the town of Jackson. Sagebrush and grasses cover the campground as cottonwoods and blue spruce rise above. The Gros Ventre River is a short stroll away. Some sites offer views of the Grand Teton and Blacktail Butte. Wildlife including bison, moose and mule deer frequent the area.", latitude: 43.6159078257903, longitude: -110.665883983664},
  {name: "Group Campsite", city: "Torrey", totalsites: "1", firstcome: "0", reservable: "1", fees: "125.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "The Group Campsite is a secluded site located near the Fruita Campground and can accommodate a maximum of 40 people. It may be reserved through www.recreation.gov up to one year in advance. The Fruita Group Campsite is open from mid-April to mid-October. It is closed every Tuesday and Wednesday nights for maintenance. The nightly rate is $125.", latitude: 38.2789, longitude: -111.2514},
  {name: "Guadalupe Peak Wilderness Campground", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/gumo/planyourvisit/gp_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. This primitive campground is located on the Guadalupe Peak Trail, 3.1 miles from the Pine Springs Trailhead, and 1 mile below the Peak. The elevation gain from the trailhead to the campsites is 2,200 feet. The campground is on a small knoll and only minimally protected from high winds. Five designated campsites are defined by tent pads. These sites are exposed and high winds are common.", latitude: 31.8934963936164, longitude: -104.851581112569},
  {name: "Gulpha Gorge Campground", city: "Hot Springs", totalsites: "40", firstcome: "0", reservable: "40", fees: "34.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/hosp/planyourvisit/campground.htm", description: "Camping at Gulpha Gorge Campground costs $34 per night for all sites. All sites have full hookups: 30 and 50 amp electric, water and sewer connections. Sites are not pull-through. Each campsite has a picnic table, pedestal grill, and water. Camping in Gulpha Gorge Campground is limited to a total of 14 consecutive days and a cumulative total of 30 days in any calendar year. Each 14 day consecutive stay must be followed by a minimum of a 7 day break.", latitude: 34.52436309933005, longitude: -93.03604779926388},
  {name: "Gunter Hill Campground", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.recreation.gov/camping/campgrounds/232593", description: "Campground near Montgomery, Al", latitude: 32.358663, longitude: -86.452365},
  {name: "Guy Sandy Campground", city: "f", totalsites: "40", firstcome: "40", reservable: "0", fees: "14.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Guy Sandy Campground is open seasonally for the summer. It opens no later than Memorial Day Weekend and closes no earlier than Labor Day Weekend. It is located on the western shore of Lake of the Arbuckles. This primitive campground offers basic amenities only. RVs are permitted, but no hookups are available. Communal water spigots and pit toilets are available. Each campsite has a picnic table and fire ring with a grill.", latitude: 34.4561844867, longitude: -97.052303804},
  {name: "Haag Cove Campground", city: "Kettle Falls", totalsites: "18", firstcome: "0", reservable: "18", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-haagcove.htm", description: "Haag Cove is a quiet, isolated campground on Lake Roosevelt’s west side. Sites are spread out along the shoreline in an open area with scattered pine trees. The open setting does not allow for much privacy between sites, but provides sweeping views of the lake to most campers. The campground is typically quiet, though it may fill on holidays and weekends. There is not a boat launch or courtesy dock.", latitude: 48.5606608808908, longitude: -118.151804154105},
  {name: "Halls Crossing RV & Campground", city: "Lake Powell", totalsites: "72", firstcome: "41", reservable: "24", fees: "100.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.lakepowell.com/rv-camping/halls-crossing-rv-campground/", description: "With Halls Crossing RV Park & Campground, you're just steps from food, fun, and the Village Store. It's easy to get to Halls Crossing. Simply ride the ferry located at Bullfrog Marina and Halls Crossing, it's just a short ride away. Stop by the Village Store to check-in - and don't forget to pick up food and beverages while you are there.", latitude: 37.4577993989406, longitude: -110.71820138441},
  {name: "Happy Valley Saddle", city: "Tucson", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "- 6,200 feet elevation - 3 campsites (6 people max per site)", latitude: 32.154022, longitude: -110.518452},
  {name: "Harbor Bay Campground", city: "f", totalsites: "25", firstcome: "25", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "This campground is located outside of Fritch at the end of Lakeview Drive off Hwy. 136. The area features both designated sites and open meadow camping. Harbor Bay also features two new trails, which are the Harbor Bay and South Turkey Creek Trails. Picnic tables, shade shelters, and grills are available. No drinking water or flush toilets; vault-evaporator toilets. A beautiful area for water activities, hiking, biking, and birding.", latitude: 35.6478560545659, longitude: -101.628983197957},
  {name: "Harlequin Campground", city: "f", totalsites: "7", firstcome: "1", reservable: "6", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/10101324", description: "Harlequin Campground is located near Stehekin in Lake Chelan National Recreation Area, 4.5 miles up the Stehekin Valley Rd from Stehekin Landing and 0.5 miles from the public airstrip. All sites are walk-in sites though some have limited parking. First-come, first-served during the winter; summer reservations can be made up to 6 months in advance through www.recreation.gov or by calling 1-877-444-6777. Stehekin is a remote community with limited facilities and is only accessible by foot, boat, or plane.", latitude: 48.3468, longitude: -120.7137},
  {name: "Haskell Campsite", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "Backcountry campsite along the banks of Haskell Deadwater and the Haskell Rock Pitch Portage.", latitude: 46.073046, longitude: -68.768473},
  {name: "Haskell Hut", city: "Patten", totalsites: "1", firstcome: "0", reservable: "1", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "Rustic cabin with wood stove that can be reserved by groups of up to 8 people. Available for overnight stays in the winter by reservation, the hut is reached by cross-country skiing or snowshoeing. Reservations required through Recreation.gov.", latitude: 46.079623645522545, longitude: -68.77007902706657},
  {name: "Hatchet Lake Campground", city: "Houghton", totalsites: "8", firstcome: "5", reservable: "3", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Hatchet Lake Campground is located on Hatchet Lake off of the Hatchet Lake Trail. This campground is located near the center of the island. Stay Limit: 2 nights from June 1 – Labor Day annually. Access: Foot", latitude: 48.02113448367796, longitude: -88.85081644657868},
  {name: "Hawk Campground", city: "Sausalito", totalsites: "3", firstcome: "0", reservable: "3", fees: "25.00", showers: "None", toilets: "Portable Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/258815", description: "Hawk Campground is the most remote campground and is located above Tennessee Valley and offers sweeping views of the Marin Headlands. The site is a 2.5 mile uphill hike from the Tennessee Valley Trailhead parking lot, or a 3.5 mile uphill hike from the Miwok Trailhead. There are 3 sites that can accommodate 4 people each. Maximum stay is three nights per year. Campsites are available March-November. $25.00/per campsite per night", latitude: 37.854157537291584, longitude: -122.52064607251748},
  {name: "Hawk Creek Campground", city: "Creston", totalsites: "20", firstcome: "0", reservable: "20", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/laro/planyourvisit/cg-hawkcreek.htm", description: "Hawk Creek Campground is tucked back from the lake on Hawk Creek. The campground is popular, but small enough to feel private and secluded. A small waterfall adds ambiance to this beautiful and unique campground. Hawk Creek is close to Fort Spokane Visitor Center, but far enough away to avoid the hustle and bustle. A courtesy dock provides access to Lake Roosevelt.", latitude: 47.815164484753, longitude: -118.324951949107},
  {name: "Hay Bay Campground", city: "Houghton", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Hay Bay Campground is located in Hay Bay on the south shore of Isle Royale National Park. Boats can overnight at dock. Stay Limit: 3 nights from June 1 – Labor Day annually. Depth at dock (normal conditions): 3’-7’. Access: Canoe/kayak/private boat", latitude: 47.93386908337534, longitude: -88.93999432938057},
  {name: "Haypress Campground", city: "Sausalito", totalsites: "6", firstcome: "0", reservable: "6", fees: "25.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/10067346", description: "Haypress Campground is nestled within the coastal scrub of Tennessee Valley, near Mill Valley. The hike to this campground is 0.7 miles from the Tennessee Valley Trail head parking lot, and is an ideal campground for first-time backpackers. Haypress campers often enjoy hiking to Tennessee Beach, where they can admire its dramatic geology and colorful sand. Campground is open March-November. $25.00/per campsite per night", latitude: 37.860785115938235, longitude: -122.54645447713449},
  {name: "Headwaters Campground", city: "Moran", totalsites: "171", firstcome: "0", reservable: "171", fees: "52.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grte/planyourvisit/headwaterscg.htm", description: "This campground has facilities for both tent campers and RVs. Located within the John D. Rockefeller, Jr. Memorial Parkway, Flagg Ranch is only two miles south of Yellowstone and five miles north of Grand Teton. Wilderness surrounds the Headwaters development. The upper reaches of the Snake River flow through meadows mixed with open conifer forest. The campground offers 34 tent sites, 97 full-hookup and pull-through RV sites, and 40 Camper Cabins.", latitude: 44.1058980602854, longitude: -110.671020215965},
  {name: "Heart O' the Hills Campground", city: "Port Angeles", totalsites: "97", firstcome: "97", reservable: "0", fees: "24.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/heart-o-the-hills-area-brochure.htm", description: "Surrounded by old growth forest, Heart O' the Hills offers summer ranger programs and great family fun", latitude: 48.0362664284, longitude: -123.429967329},
  {name: "Hickory Hill Wilderness Campsite", city: "f", totalsites: "4", firstcome: "0", reservable: "4", fees: "9.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/cuis/planyourvisit/hickoryhill.htm", description: "Hickory Hill is one of three campgrounds in Cumberland Island’s designated wilderness on the northern part of the island. The campsite is located 5.5 miles north of the Sea Camp dock. Wilderness sites have no amenities. Fires are prohibited, but camp stoves can be used for cooking. Campers should be prepared to hang all food and trash. Water is available from a nearby well and must be treated. Permits are $9 a night, good for up to 6 people.", latitude: 30.8327, longitude: -81.4514},
  {name: "Hidden Valley Campground", city: "f", totalsites: "44", firstcome: "44", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/hidden-valley-campground.htm", description: "The Hidden Valley Campground has 44 sites, pit toilets and no water. The campground is located along park boulevard and is surrounded by large boulders and Joshua Trees. All sites at Hidden Valley Campground are first-come, first-served. When you arrive, look for an open, vacated site. Move into the site and set up camp. Then proceed as soon as possible to an entrance station to complete registration and pay. If arriving after entrance stations close, payment may be delayed until the following morning.", latitude: 34.01718844292067, longitude: -116.16273695815448},
  {name: "Hite Outpost Adventure Center - Closed until further notice", city: "Campground is closed", totalsites: "35", firstcome: "0", reservable: "35", fees: "f", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Hite is a remote Ranger District located at the top of Lake Powell, adjacent to the confluence of the Colorado and Dirty Devil Rivers. Primitive camping is available in various spots. They offer developed camping for tents and RVs.", latitude: 37.87686, longitude: -110.381007},
  {name: "Hite Primitive (Dirty Devil, Farley, White Canyon) camping", city: "Page", totalsites: "0", firstcome: "0", reservable: "0", fees: "12.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/glca/planyourvisit/hite.htm", description: "Designated primitive camping areas that are accessible by vehicle and sometimes by vessel as well. First-come first served, no reservations. No designated sites. No potable water. When pit toilets are unavailable, campers must bring portable toilets for use and proper disposal into the sewer system. Quiet hours 10pm-6am. $12 per night.", latitude: 37.864131, longitude: -110.399782},
  {name: "Hodgdon Meadow Campground", city: "f", totalsites: "103", firstcome: "0", reservable: "103", fees: "36.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/yose/planyourvisit/hodgdoncamp.htm", description: "Hodgdon Meadow Campground is conveniently located along the Big Oak Flat Road at the Big Oak Flat Entrance. At an elevation of 4,900 feet (1,219 m), the campground is located along the western edge of the park, 25 miles and approximately 45 minutes from Yosemite Valley. There is a gas station and minimal convenience items located nearby at Crane Flat. Trailheads for both the Merced Grove and Tuolumne Grove of Giant Sequoias are located within a short drive of the campground.", latitude: 37.7998824480597, longitude: -119.867033107265},
  {name: "Hoh Campground", city: "Forks", totalsites: "78", firstcome: "0", reservable: "78", fees: "24.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/hoh-rain-forest-area-brochure.htm", description: "Surround yourself with moss and ancient trees in this temperate rain forest. Hoh campground offers summer ranger programs and some riverside campsites along the Hoh River. Reservations in summer, first come first served the rest of the year.", latitude: 47.8583524952499, longitude: -123.935540122439},
  {name: "Hole-in-the-Wall Campground", city: "Essex", totalsites: "35", firstcome: "35", reservable: "0", fees: "12.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/moja/planyourvisit/campgrounds.htm", description: "At 4,400 feet in elevation, Hole-in-the-Wall Campground is surrounded by sculptured volcanic rock walls and makes a great basecamp for hikers. Thirty-five campsites accommodate RVs and tents; two walk-in sites are also available. Maximum RV or trailer length is 33 Feet.", latitude: 35.046023820433334, longitude: -115.39356516179197},
  {name: "Homestake Campground (Primitive)", city: "Death Valley", totalsites: "10", firstcome: "10", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/deva/planyourvisit/homestake-campground-primitive.htm", description: "Extremely remote campsite at 3,785ft of elevation, accessible only by high-clearance vehicles with all-terrain tires. Access from Saline Valley Road requires navigating Lippincott Pass and should only be attempted by experienced 4-wheel drivers. There are no approved toilet facilities at this location and no water available. Pack in all that you need, pack out all that you bring.", latitude: 36.6376, longitude: -117.5738},
  {name: "Horse Camp", city: "Whiskeytown", totalsites: "2", firstcome: "0", reservable: "2", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/whis/planyourvisit/horse-camp.htm", description: "Horse Camp is specifically for camping with horses. The open area allow ease of parking for horse trailers. Several trails are accessible from this campground. Both sites are in partial shade and feature bear-proof storage lockers, picnic tables, a lamp pole, fire pit with swing-arm BBQ grill, and a shared ADA accessible vault toilet. Each site has a maximum occupancy of 10 people and 3 vehicles. Potable water available from a spigot most the year. No corrals, tie posts/rails, or water troughs onsite.", latitude: 40.57611111, longitude: -122.531388889},
  {name: "Horsepen Branch Hiker-Biker Campsite", city: "Poolesville", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 26.1 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.06938, longitude: -77.398038},
  {name: "Horseshoe Bend Campground", city: "Lovell", totalsites: "68", firstcome: "38", reservable: "10", fees: "35.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/bica/planyourvisit/horseshoe-bend-campground.htm", description: "- Located near Lovell, WY - Open All Year - All sites include fire pit and picnic table. - 68 RV and tent sites - 28 sites have water and electrical hook-ups. The water is only available at these sites Memorial Day to Labor Day. The electricity remain on year round. - 3 pull through sites - Modern Restrooms, RV dump station and drinking water are available - Swim beach and picnic less than 1 mile hike from the campground - Horseshoe Bend Marina and boat docks less than 1 mile hike from the campground", latitude: 44.96, longitude: -108.26},
  {name: "Horseshoe Bend Hiker-Biker Campsite", city: "Mercersville", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "No", url: "", description: "Mile 79.2 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.486903, longitude: -77.774341},
  {name: "Hosmer Grove Campground", city: "Kula", totalsites: "6", firstcome: "0", reservable: "6", fees: "5.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/hale/planyourvisit/drive-up-camping.htm", description: "Hosmer Grove lies in the cloud belt of Haleakalā, just below the 7,000 foot level (2134m) in the Summit District. Be prepared for rain and cold weather. Nighttime temperatures can drop into the to near freezing (0°C); daytime highs average 50-65°F (10-18°C). The campground has picnic tables, barbeque grills, drinking water, and pit toilets. Overnight stays are limited to three (3) nights total for all Park areas in a thirty day period. Pets are not permitted in or around campsite facilities.", latitude: 20.768155, longitude: -156.237592},
  {name: "Houchin Ferry Campground", city: "Brownsville", totalsites: "12", firstcome: "0", reservable: "12", fees: "20.00", showers: "None", toilets: "Portable Toilets - year round", phone: "No", url: "", description: "The Houchin Ferry Campground lies next to the Green River at the former location of the Houchin Ferry. This campground is operated year-round and is located 15 miles from the Visitor Center and two miles east of Brownsville, KY. For campers who like it simple, these 12 tent-only campsites afford a quiet visit with drive-up access.", latitude: 37.2016901005, longitude: -86.2377215005},
  {name: "Hovenweep Campground", city: "Cortez", totalsites: "31", firstcome: "31", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/hove/planyourvisit/camping.htm", description: "A 31-site campground near the visitor center is open year-round, first-come, first-served. The campground is designed for tent camping, though a few sites will accommodate RVs up to 36 feet long. Groups are limited to eight people and two vehicles. Sites include tent pads, fire rings and picnic tables with shade structures; there are no hookups available. One campsite is wheelchair-accessible but is not designed for tent camping.", latitude: 37.3832032699, longitude: -109.070815001},
  {name: "Hozomeen Campground", city: "f", totalsites: "75", firstcome: "75", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Hozomeen Campground is located at the north end of Ross Lake and is open from late May through October. There is no camping fee, and all sites are first-come, first-served. Facilities include boat launches, pit toilets, potable water, picnic tables and fire grates. Guests must pack-out all trash. Campsites located in the Upper Loop are closed until further notice due to hazard trees.", latitude: 48.9871, longitude: -121.0711},
  {name: "Huckleberry Hill Hiker-Biker Campsite", city: "Hagerstown", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "No", url: "", description: "Mile 62.0 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.34152, longitude: -77.75747000000001},
  {name: "Huginnin Cove Campground", city: "Houghton", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Huginnin Cove Campground is located at Huginnin Cove, on the north shore of Isle Royale National Park, off of the West and East Huginnin Cove Trails. Stay Limit: 3 consecutive nights from June 1 – Labor Day annually. Access: Foot/canoe/kayak access. No dock available for boats.", latitude: 47.934272022481146, longitude: -89.17647822364461},
  {name: "Hunters Campground", city: "Hunters", totalsites: "37", firstcome: "0", reservable: "37", fees: "23.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-hunters.htm", description: "Hunters Campground is part of a larger recreation complex with a boat launch and Day Use Area. Campsites are tucked among the pines in a centrally-located loop with an easy walk to the beach area. Group sites offer privacy and beach access for larger parties. Hunters Campground is very popular and often busy in the summer.", latitude: 48.1255472978347, longitude: -118.231331985386},
  {name: "Hunters Group Sites", city: "Hunters", totalsites: "3", firstcome: "0", reservable: "3", fees: "55.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-group-hunters.htm", description: "The Hunters Group sites are part of a larger recreation complex with a boat launch and Day Use Area. The group sites are tucked among the pines on one side of the swim beach. An individual-site campground is located nearby. Hunters Campground is very popular and often busy in the summer.", latitude: 48.12544, longitude: -118.230877},
  {name: "Hurricane River Campground", city: "Grand Marais", totalsites: "21", firstcome: "0", reservable: "21", fees: "25.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/camping.htm", description: "There are two loops: 11 campsites in the lower loop and ten in the upper loop. Both loops include one disabled- accessible campsite. Several campsites are designated as generator-free. Because the turning radius and some parking site lengths are limited within this campground, we recommend single vehicles no more than 36 feet and vehicle/trailer combined length no more than 42 feet use this campground. The campground is open by reservation only, mid-May through mid-October. Leashed pets allowed.", latitude: 46.663953, longitude: -86.1661587},
  {name: "Igloo Creek", city: "f", totalsites: "7", firstcome: "7", reservable: "7", fees: "21.75", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/dena/planyourvisit/campground-igloo.htm", description: "Igloo Creek Campground is currently closed due to the Pretty Rocks construction project. The campground is at mile 35 on the Park Road. It offers 7 sites, making it the smallest in Denali (along with Sanctuary River Campground, which also has 7 sites). Igloo is tent-only—you will not find any RVs or other vehicles in this campground. It is accessible only by camper bus. The bus stop is a short walk from the campsites (less than 50 yards).", latitude: 63.60969751677842, longitude: -149.583358087987},
  {name: "Indian Cove Campground", city: "Twentynine Palms", totalsites: "101", firstcome: "0", reservable: "101", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/indian-cove-campground.htm", description: "Indian Cove Campground is located off of Highway 62, thirteen miles east of Joshua Tree Village and ten miles west of Twentynine Palms on the north side of the Wonderland of Rocks. Indian Cove Road dead-ends at this secluded area. Indian Cove has 101 campsites, including thirteen group campsites. Between Memorial Day and Labor Day it is 39 reservable sites. There are vault toilets but no water at the campsites. Water is available at the small ranger station roughly two miles north of the campground.", latitude: 34.0934671521967, longitude: -116.15885356362554},
  {name: "Indian Creek Campground", city: "Yellowstone National Park", totalsites: "70", firstcome: "0", reservable: "70", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yell/planyourvisit/indiancreekcg.htm", description: "Located about eight miles south of Mammoth Hot Springs on the road to Norris, Indian Creek Campground—elevation 7,300 feet (2225 m)—sits near the base of the Gallatin Mountains and offers breathtaking views of Electric Peak. The area offers easy access to fishing and hiking. The campground is away from the main road and provides a quieter, more primitive, experience than many other locations. This is a reservable campground run by the National Park Service.", latitude: 44.887023247756964, longitude: -110.73606858357869},
  {name: "Indian Flats Hiker-Biker Campsite", city: "Dickerson", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "", description: "Mile 42.5 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.233516, longitude: -77.462711},
  {name: "Indian Well Campground", city: "Tulelake", totalsites: "43", firstcome: "43", reservable: "1", fees: "10.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/labe/planyourvisit/campgrounds.htm", description: "Lava Beds has one campground, Indian Well Campground, located 1/2 mile (0.8 km) from the Visitor Center and cave loop. There are 43 sites available on a first-come, first serve basis. Sites can accommodate tents, pickup campers, small trailers and motor homes up to 30 feet. Note: not all sites can accommodate motor homes.", latitude: 41.7174707355158, longitude: -121.504144731988},
  {name: "Indigo Neck Hiker-Biker Campsite", city: "Little Orleans", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 139.2 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.626697, longitude: -78.358666},
  {name: "Intermediate Lake Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/canoe-and-kayak.htm", description: "Intermediate Lake Campground is located on the shoreline of Intermediate Lake. Intermediate Lake can be accessed via a .6 mile portage from Lake Richie or a .4 mile portage from Siskiwit Lake. Stay Limit: 2 nights effective June 1 – Labor Day annually. Access: Canoe/kayak, no private boat access.", latitude: 48.0337769036068, longitude: -88.72265854573033},
  {name: "Irons Mountain Hiker-Biker Campsite", city: "Hagerstown", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.nps.gov/choh/planyourvisit/camping.htm", description: "Mile 175.3 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.58747900000001, longitude: -78.73558400000002},
  {name: "Island Mine Campground", city: "Houghton", totalsites: "6", firstcome: "4", reservable: "2", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Island Mine Campground is located .5 miles south from the junction of the Greenstone and Island Mine Trails, on the Island Mine Trail. This inland campground offers no canoe/kayak/boat access. Little Siskiwit River runs south of the campground, where water can usually be obtained for filtering, unless it is dried up. Check with a Visitor Center for current water status. Stay Limit: 3 nights effective June 1 – Labor Day annually. Access: Foot", latitude: 47.93022395196004, longitude: -89.03997478443017},
  {name: "Island in the Sky (Willow Flat) Campground", city: "Moab", totalsites: "12", firstcome: "12", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "http://www.nps.gov/cany/planyourvisit/camp-isky.htm", description: "Island in the Sky Campground (Willow Flat) has 12 sites, first-come, first-served. The campground is open year-round. The spectacular Green River Overlook is nearby. Nightly camping fee is $15 per site. Sites fill quickly spring through fall. There are toilets, picnic tables, and fire rings in the campground. There is no water at the campground. You can get drinking water outside the visitor center spring through fall.", latitude: 38.3833807187, longitude: -109.888129255},
  {name: "Jedediah Smith Campground", city: "Crescent City", totalsites: "86", firstcome: "0", reservable: "86", fees: "35.00", showers: "Coin-Operated - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/redw/planyourvisit/jedediah-smith-campground.htm", description: "Situated in a magnificent old-growth redwood grove on the banks of the wild and scenic Smith River, this campground offers hiking trails, swimming, fishing, and seasonal campfire programs. Cabins available.", latitude: 41.79572647634519, longitude: -124.08644744393519},
  {name: "Jeff Busby Campground, Milepost 193.1", city: "Ackerman", totalsites: "18", firstcome: "18", reservable: "0", fees: "0.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/natr/planyourvisit/camping.htm", description: "Jeff Busby Campground at milepost 193.1, midway on the Parkway, includes an 18-site campground, picnic tables, restrooms, trails, exhibits and an overlook atop Little Mountain. Campground sites are on a first come first served basis, no reservations. Pull-through and back-in sites are available. Campgrounds are typically busiest during the spring and fall, especially during holiday weekends. There is no fee for camping. There is a 14 day stay limit.", latitude: 33.4171640485, longitude: -89.2673709764},
  {name: "Jenny Lake Campground", city: "Moose", totalsites: "61", firstcome: "10", reservable: "51", fees: "49.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grte/planyourvisit/jennylakecg.htm", description: "This small campground is a few hundred yards from the east shore of Jenny Lake. The campground straddles a glacial moraine covered with an open forest of lodgepole pine, subalpine fir and Douglas fir. Across the lake, Teewinot Mountain, Cascade Canyon and Mount St. John dominate the landscape. No trailers, campers, or generators are allowed. Vehicles cannot be over 8 feet high or 14 feet long. Maximum per site is two tents, one vehicle, and six guests for up to 7 nights.", latitude: 43.7552631986, longitude: -110.719393468},
  {name: "Jones Bay Campground", city: "Wilbur", totalsites: "9", firstcome: "0", reservable: "9", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/laro/planyourvisit/cg-jonesbay.htm", description: "Jones Bay is one of Lake Roosevelt’s smaller, more rustic campgrounds. The campground is mostly gravel and packed dirt, with not much vegetation, on the lake shore. It can be reached by boat and has a boat launch and dock. Jones Bay can be also be reached by car, but may be difficult to find.", latitude: 47.9198995489921, longitude: -118.582833386076},
  {name: "Jordan Junction Hiker-Biker Campsite", city: "Williamsport", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 101.2 - THiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.60919199999999, longitude: -77.85283},
  {name: "Julian Price Park Campground", city: "Blowing Rock", totalsites: "190", firstcome: "98", reservable: "92", fees: "20.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/blri/planyourvisit/price-cg.htm", description: "Julian Price Campground sits beside Price Lake, a pristine body of water that sparkles against the backdrop of rolling mountains. The surrounding forest offers a colorful assortment of wildflowers in the spring and vivid orange and red foliage in the fall. Canoes can be rented at the lake, and a dock offers opportunities for fishing. Area hiking trails include Price Lake, Boone Fork, Green Knob, and Tanawha trails. Campers may see bears, grouse, turkeys, owls, hawks and more.", latitude: 36.1203493459, longitude: -81.7652698731},
  {name: "Jumbo Rocks Campground", city: "Twentynine Palms", totalsites: "124", firstcome: "0", reservable: "124", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/jumbo-rocks-campground.htm", description: "There are 124 individual/family sites in Jumbo Rocks Campground. Reservations are required. The campground is centrally located and offers great views of rock formations.", latitude: 33.99075644553917, longitude: -116.05925183764725},
  {name: "Juniper Basin", city: "Tucson", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "- 6,000 elevation - 3 campsites (6 people max per site)", latitude: 32.182818, longitude: -110.644513},
  {name: "Juniper Campground", city: "f", totalsites: "50", firstcome: "50", reservable: "1", fees: "14.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/thro/planyourvisit/juniper-campground.htm", description: "Juniper Campground is 5 miles from Hwy 85 and is the only campground in the park's North Unit. All sites are open to tent camping and most can also be used by vehicles/RVs (no hookups). All regular sites are first come, first served. The group site is by reservation only at recreation.gov. See the RESERVATIONS section below.", latitude: 47.59524746300008, longitude: -103.33315676199999},
  {name: "Juniper Family Campground", city: "Los Alamos", totalsites: "52", firstcome: "50", reservable: "2", fees: "12.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/band/planyourvisit/juniper-family-campground.htm", description: "3 loops, 52 site campground near the entrance to the monument. 2 sites for small groups up to 20 people. No reservations. First come, first serve except for the 2 group sites.", latitude: 35.795338186866466, longitude: -106.27904543051932},
  {name: "Juniper Lake Campground", city: "Chester", totalsites: "18", firstcome: "18", reservable: "3", fees: "12.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/juniper-lake-campground.htm", description: "The Juniper Lake Campground is located on the east shore of Juniper Lake via a 13-mile paved/gravel road. All single campsites are first-come, first-served only. Reservations are required for the two group sites and one stock corral. Drinking water is not available.", latitude: 40.4496486561308, longitude: -121.296461605835},
  {name: "Kalaloch Campround", city: "f", totalsites: "170", firstcome: "0", reservable: "170", fees: "24.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/kalaloch-area-brochure.htm", description: "Oceanside camp at Kalaloch with some sites overlooking the Pacific Ocean. Online reservations accepted for peak season. Check recreation.gov for current season dates. All sites are first-come, first-served in off season.", latitude: 47.6127053601112, longitude: -124.375074131062},
  {name: "Kamloops Island Campground", city: "Kettle Falls", totalsites: "17", firstcome: "0", reservable: "17", fees: "23.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-kamloopsisland.htm", description: "Kamloops Island is a small, shady campground at the mouth of the Kettle River. The main hub of campsites is tucked into the vegetation at the top and can fit tents and RVs. A cluster of walk-in tent sites is spread out in a grove of pines. Many campsites offer views of the river, lake, and surrounding landscape. There is a courtesy dock, but no boat launch.", latitude: 48.6783241500684, longitude: -118.117008793336},
  {name: "Katahdin Brook Lean-to", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "The closest backcountry site at 0.4 miles from the Barnard Mountain Trailhead / Southern Terminus of the International Appalachian Trail and the Loop Road. This is a Lean-to site only.", latitude: 45.9204007640828, longitude: -68.7628803658878},
  {name: "Katherine Landing Campground (Lake Mohave)", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.katherinelanding.com/camping", description: "With views of the lake and within walking distance of the marina, swim beach, dining and retail store, campsites at Katherine Landing feature a wide array of amenities including concrete picnic tables, fire rings with cooking grills, modern restrooms, showers, with laundry facilities close by. Wi-fi access through Access Parks is available. Campsites at Katherine Landing offer guests a scenic, affordable basecamp for adventures on and around beautiful Lake Mohave.", latitude: 35.223175690624814, longitude: -114.55677723796998},
  {name: "Katherine Landing RV Park (Lake Mohave)", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.katherinelanding.com/camping", description: "The Katherine Landing RV campsites come complete with water, sewer and electrical hook-ups. All spaces have the option of household current, 30 amp, or 50 amp power. Laundry, restroom and shower facilities are available on site. All spaces are back-in with the largest space accommodating a 40′ RV. For a Lake Mohave camping adventure in the comfort of your own rig, the RV campsites at Katherine Landing offer all the connections you need!", latitude: 35.22234390934542, longitude: -114.56200692629523},
  {name: "Kendesnii Campground", city: "Nabesna", totalsites: "10", firstcome: "10", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/wrst/planyourvisit/camping-along-the-nabesna-road.htm", description: "Located on the Nabesna Road, this free camping area is the only National Park Service campground in Wrangell-St. Elias National Park. It has 10 designated campsites, each with a picnic table and a fire ring. There are two vault toilets. Each site can accommodate a small to medium RV or other vehicles. It is free and open year-round on a first-come, first-served basis, but is not plowed during the winter so campsites may not be accessible. Reservations are not taken and are not required.", latitude: 62.52982, longitude: -143.258776},
  {name: "Kettle Falls Campground", city: "Kettle Falls", totalsites: "74", firstcome: "0", reservable: "74", fees: "23.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-kettlefalls.htm", description: "Kettle Falls is a large, fairly wooded campground on the lake shore. All sites are open to tent campers, vehicles, and RVs. The campground is adjacent to Kettle Falls Marina (with boat launch and space for trailer parking) and nearby to Kettle Falls Day Use Area. The area is fairly well-trafficked and busy, but offers all the conveniences and amenities of nearby town of Kettle Falls.", latitude: 48.601517648676904, longitude: -118.12357127064581},
  {name: "Kettle River Campground", city: "Boyds", totalsites: "13", firstcome: "0", reservable: "13", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-kettleriver.htm", description: "Kettle River is a shady campground on the banks of the Kettle River. Most of the sites are on the shoreline and have beautiful views of the Kettle River and surrounding landscape. Kettle River does not have a boat launch but does have a courtesy dock. This campground is a great place for birding, with a variety of waterfowl and raptors seen in the area. Kettle River Campground is typically quiet and doesn’t often fill.", latitude: 48.7159308743606, longitude: -118.122687332665},
  {name: "Killiansburg Cave Hiker-Biker Campsite", city: "Sharpsburg", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 75.2 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.45920199999999, longitude: -77.796583},
  {name: "Kintla Lake", city: "West Glacier", totalsites: "13", firstcome: "13", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Kintla Lake campground is Glacier National Park's most remote frontcountry and car camping campground. It is located in the uppermost northwest section of the park known as the North Fork, approximately 40 miles from the west entrance. Access is via a rough dirt road. Trailers not permitted. Due to its remote location, the campground is very quiet and rarely fills, offering tent campers a sense of solitude. The campground sits on Kintla Lake.", latitude: 48.935745209091245, longitude: -114.34593003158409},
  {name: "Kirby Cove Campground", city: "Sausalito", totalsites: "6", firstcome: "0", reservable: "6", fees: "40.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/232491", description: "Kirby Cove is the most popular campground in the Marin Headlands, with spectacular views of San Francisco and the Golden Gate. Enjoy a wooded setting, seclusion and protected campsites which were recently restored for all to enjoy. Campsites are available for use only by prior reservation but anyone can walk down the road to enjoy the vistas and picnic on the bluffs or beach. Campground is open March-November. $40.00/per campsite per night.", latitude: 37.82790211736253, longitude: -122.49039346494743},
  {name: "Kulanaokuaiki", city: "Hawaii National Park", totalsites: "9", firstcome: "9", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/havo/planyourvisit/kulanaokuaiki-campground.htm", description: "Kulanaokuaiki is located off Hilina Pali Road at 3,200 feet (975 m), there are nine walk-in campsites with picnic tables. There is an accessible vault toilet; however, no water is available and campfires are not permitted. Use fueled camping stoves only. This campground is subject to closure when the area is dry and during times of high fire risk. No dogs or pets are allowed at this campground to protect endangered nene.", latitude: 19.3436666463, longitude: -155.274076368},
  {name: "Kyles Landing", city: "Jasper", totalsites: "33", firstcome: "33", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/camping.htm", description: "Kyles Landing Campground is a first come, first serve campground located between Jasper, AR and Ponca, AR.. Kyles Landing has 33 tent only sites with no electrical amenities, but flush restrooms and water are available from March 15 through November 14.", latitude: 36.0557786001, longitude: -93.2807538999},
  {name: "Kīpahulu Campground", city: "Hāna", totalsites: "9", firstcome: "0", reservable: "9", fees: "8.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/hale/planyourvisit/drive-up-camping.htm", description: "The Kīpahulu campground is about 1/8 mile (.2km) south of the Kīpahulu Visitor Center. It overlooks ocean cliffs and is a short walk from 'Ohe'o Gulch. The campground has picnic tables, BBQ grills, and pit toilets. No water is available at Kīpahulu Campground; However, drinking water is available at the Kīpahulu Visitor Center restrooms. There are two general stores in the nearby town of Hāna (10 miles [16km] away) where you can purchase water and basic food supplies.", latitude: 20.659705, longitude: -156.044492},
  {name: "Lake Fork Campground", city: "f", totalsites: "90", firstcome: "19", reservable: "57", fees: "16.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/cure/planyourvisit/camp_lake_fork.htm", description: "The campground is located 27 miles west of Gunnison on CO Highway 92 near the intersection with U.S. Highway 50 on the shore of Blue Mesa Reservoir. We recommend making reservations at recreation.gov prior to coming to Curecanti because cell service and first-come, first-served sites may be unavailable. Reservations may be made on recreation.gov from mid-May to mid-September. Tents and large RVs can be accommodated. The campground is paved except for the tent walk-in sites. Elevation: 7580 feet", latitude: 38.4548253084593, longitude: -107.326981170259},
  {name: "Lake Mead RV Village (Lake Mead)", city: "f", totalsites: "115", firstcome: "0", reservable: "115", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://lakemeadmohaveadventures.com/lake-mead-rv-village/", description: "Lake Mead's Boulder Beach area offers many things to see and do. Whether you’d rather go sightseeing, tour Hoover Dam, go shopping, visit historic Boulder City or try your luck in Las Vegas, Lake Mead RV Village at Boulder Beach is conveniently located to a variety of attractions. There’s a lot going on around Lake Mead RV Village, so set up camp and discover this diverse and historically significant area, while enjoying your stay on the shores of Lake Mead.", latitude: 36.03503, longitude: -114.800453},
  {name: "Lake Richie Campground", city: "Houghton", totalsites: "6", firstcome: "4", reservable: "2", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Lake Richie Campground is located on the north shore of Lake Richie, off of the Indian Portage Trail. Canoe/kayak access from .6 mile portage from Intermediate Lake, 2.0 mile portage from Moskey Basin, 1.2 miles portage from Chippewa Harbor, and .6 mile portage from Lake LeSage. Paddlers may want to consider staying at Lake Richie Canoe Campground. Stay Limit: 2 nights from June 1 – Labor Day annually. Access: Foot/canoe/kayak. No private boat access.", latitude: 48.05134221029036, longitude: -88.68736051726773},
  {name: "Lake Richie Canoe Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/canoe-and-kayak.htm", description: "Lake Richie Canoe Campground is located on the north shore of Lake Richie. Canoe/kayak access via the .6 mile portage from Intermediate Lake, the 1.2 mile portage from Chippewa Harbor, the 2.0 mile portage from Moskey Basin, and the .6 mile portage from Lake LeSage. Stay Limit: 2 nights effective from June 1st – Labor Day annually. Access: Canoe/kayak. No private boat access.", latitude: 48.04382454287261, longitude: -88.70018995569336},
  {name: "Lake Whittlesey Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/canoe-and-kayak.htm", description: "Lake Whittlesey Campground is located on the north shore of Lake Whittlesey. Consecutive night stay limit is two nights. Three individual tent sites are available. No boat access. Canoe/kayak access via .6 mile portage from Wood Lake and .6 mile portage from Chippewa Harbor. Stay Limit: 3 nights effective June 1 – Labor Day annually. Access: Canoe/kayak, no private boat or trail access.", latitude: 48.007628545489816, longitude: -88.7070852277252},
  {name: "Lakeview Campground", city: "f", totalsites: "9", firstcome: "0", reservable: "9", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/10101333?tab=info", description: "Located at the head of Lake Chelan, Lakeview Campground is a five minute walk from the boat dock at Stehekin Landing. All sites are walk-in sites for small to medium-sized tents. First-come, first-served during the winter; summer reservations can be made up to 6 months in advance through www.recreation.gov or by calling 1-877-444-6777. Stehekin is a remote community with limited facilities and is only accessible by foot, boat, or plane.", latitude: 48.3079, longitude: -120.6544},
  {name: "Lane Cove Campground", city: "Houghton", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Lane Cove Campground is located in Lane Cove, on the north shore of Isle Royale National Park, off of the Lane Cove Trail. The Lane Cove Trail can be accessed via the Greenstone Ridge Trail. Stay Limit: 1 night effective June 1 – September 17 annually Access: Foot/canoe/kayak. No boat access.", latitude: 48.14490683133697, longitude: -88.55767854958867},
  {name: "Las Vegas Bay Campground (Lake Mead)", city: "Boulder City", totalsites: "85", firstcome: "84", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10082283", description: "Las Vegas Bay Campground is located just minutes from Las Vegas on the western edge of the park and has lush vegetation that shades nearly every campsite. There is a mix of palm trees, oleanders, mature cottonwood trees and native vegetation that also helps provide privacy between sites. Wildlife in the area includes many species of birds and lizards along with antelope squirrels and the occasional coyote.", latitude: 36.12784458588996, longitude: -114.87256967216257},
  {name: "Lava Flow Campground", city: "Arco", totalsites: "42", firstcome: "42", reservable: "0", fees: "15.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/crmo/planyourvisit/camping.htm", description: "The Lava Flow Campground is a 42-site campground accessible by automobile from May through November. All sites are available on a first-come, first-served basis. Some sites will accommodate large RVs, but no hookups are available.", latitude: 43.46070030861083, longitude: -113.55913194531865},
  {name: "Lava Point Campground", city: "f", totalsites: "6", firstcome: "0", reservable: "6", fees: "f", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/10138237", description: "This campground is typically open May through September, as weather allows. Situated at 7890 feet above sea level, it is off the Kolob Terrace Road, 25 miles (45 minutes) north of the town of Virgin. It takes approximately one hour and 20 minutes to drive to the campground from the South Entrance of Zion Canyon. There are 6 primitive campsites available for reservations. The campground has pit toilets and trash cans, but no water. Vehicles longer than 19 feet are not permitted.", latitude: 37.38387909363943, longitude: -113.03284569282363},
  {name: "Lees Ferry Campground", city: "Marble Canyon", totalsites: "54", firstcome: "54", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/glca/planyourvisit/camping.htm", description: "No reservations. $20 per site/per night. 54 designated sites. No hookups. RV dump station. Grills provided, no open fires. Quiet time 10pm-6am. Modern bathroom/comfort station, potable water available, launch ramp 2 miles. Gas and supply store at Marble Canyon, about 5 miles away.", latitude: 36.8593470173641, longitude: -111.606274705221},
  {name: "Leopards Mill Hiker-Biker Campsite", city: "Hancock", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 129.9 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.65192764428288, longitude: -78.24015520133435},
  {name: "Lewis Lake Campground", city: "Yellowstone National Park", totalsites: "85", firstcome: "0", reservable: "85", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yell/planyourvisit/lewislakecg.htm", description: "Lewis Lake Campground—elevation 7,800 ft (2377 m)—is about eight miles from the South Entrance and a short walk from the southeast shore of Lewis Lake. A boat ramp is located near the campground information and registration area. Canoes, kayaks, and motor boats are allowed on Lewis Lake. Boat permits and an aquatic invasive species inspection performed by park staff are required. This is a reservable campground run by the National Park Service.", latitude: 44.282205650024956, longitude: -110.62798728261384},
  {name: "Lewis Mountain Campground", city: "Shenandoah", totalsites: "30", firstcome: "30", reservable: "0", fees: "30.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/shen/planyourvisit/lewis-mountain-campground.htm", description: "Lewis Mountain (mile 57.5), the smallest campground in Shenandoah National Park, appeals to those who want a little more privacy while still staying within a close distance to many of the most popular destinations in the Park, including Big Meadows (7 miles away). Lewis Mountain Campground operates on a self-registration, first-come, first-serve basis. There are no reservable sites. Please follow the posted directions to register once arriving at the campground.", latitude: 38.43556, longitude: -78.478096},
  {name: "Licking Creek Hiker-Biker Campsite", city: "Pool", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "", description: "Mile 116.0 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.650293999999995, longitude: -78.04877600000002},
  {name: "Linville Falls Campground", city: "Linville Falls", totalsites: "64", firstcome: "25", reservable: "39", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/blri/planyourvisit/linville-falls-cg.htm", description: "Surrounded by pine forest and within hiking distance of Linville Gorge, Linville Falls Campground is ideal for family camping. The campground is central to many area attractions including the Linville Falls waterfall and fishing in the stocked Linville River. Linville Gorge is a designated Wilderness area with 11,786 acres of rolling hills and thick forest. The Gorge surrounds Linville River, which is located 1,400 feet below the ridge, creating “The Grand Canyon of North Carolina.”", latitude: 35.9671524465, longitude: -81.9326240728},
  {name: "Little Beaver Lake Campground", city: "Melstrand", totalsites: "8", firstcome: "0", reservable: "8", fees: "25.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/camping.htm", description: "Little Beaver Lake Campground has 8 campsites on a beautiful inland lake. One campsite is disabled- accessible. Single unit vehicles in excess of 36 feet and vehicle/trailer combined units in excess of 42 feet are prohibited at Little Beaver because of the small campsites and the narrow, twisting, hilly access road. Pets are NOT allowed at this campground. The campground is open by reservation only, mid-May through mid-October. Closed the rest of the year.", latitude: 46.5575, longitude: -86.3638},
  {name: "Mosquito River Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Mosquito River Group Campground is on the west side of Mosquito River just a few steps away from the North Country Trail. There is a bluff near the campsite overlooking Lake Superior, which provides easy access to Mosquito Beach and the river. Shared pit toilet. Water source is Mosquito River and Lake Superior. Lake Superior access for kayakers. NO CAMPFIRES.", latitude: 46.5226013349, longitude: -86.4938020699},
  {name: "Little Pool Hiker-Biker Campsite", city: "Hancock", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "No", url: "", description: "Mile 120.4 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.68477600000001, longitude: -78.11312500000001},
  {name: "Little Todd Campground", city: "Houghton", totalsites: "4", firstcome: "4", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Little Todd Campground is located in Little Todd, on the north shore of Isle Royale National Park, off of the Minong Ridge Trail. Stay Limit: 2 nights effective June 1 – Labor Day annually. Access: Foot/canoe/kayak. No boat access.", latitude: 48.020255955459476, longitude: -88.92666555449784},
  {name: "Lizard Creek Campground", city: "Moran", totalsites: "60", firstcome: "0", reservable: "60", fees: "48.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grte/planyourvisit/lizardcreekcg.htm", description: "This rustic campground is in a remote part of Grand Teton National Park. The campground is 11 miles south of Yellowstone and eight miles north of Colter Bay Village providing easy access to both parks. The campsites are in a spruce and fir forest on the shores of Jackson Lake. One mile across the lake is the northern portion of the Teton Range including Webb Canyon, Owl and Ranger peaks. Looking south, the expanse of the Teton Range towers over Jackson Lake.", latitude: 44.0034692521, longitude: -110.688659309},
  {name: "Locust Grove Group Sites", city: "Kettle Falls", totalsites: "2", firstcome: "0", reservable: "2", fees: "55.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-group-locustgrove.htm", description: "The Locust Grove Group sites are located at the townsite of Old Kettle Falls, near Kettle Falls Campground and Kettle Falls Day Use Area. There are two sites with a capacity for 50 people and 75 people, respectively. A nature trail leads to Kettle Falls Campground and Kettle Falls Day Use Area with swim beach. Locust Grove Group sites are spacious and set apart from the hustle and bustle of the Kettle Falls Campground area.", latitude: 48.592901, longitude: -118.114979},
  {name: "Lodgepole Campground", city: "Lodgepole", totalsites: "214", firstcome: "0", reservable: "214", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/lodgepole.htm", description: "All sites are reservation only. Reservations are on a four-month rolling basis. Season Opening Date: Typically the second Friday in June. This large campground is on the Marble Fork of the Kaweah River within easy walking distance of Lodgepole Village. At an elevation of 6,700 ft (2,042 m), the campground can be snowy in spring and fall. Lodgepole Village offers a visitor center, market, shower, and laundry facilities. In summer, ride the free Sequoia Shuttle. Many nearby trails are located here.", latitude: 36.60577263264627, longitude: -118.7268830432024},
  {name: "Loft Mountain Campground", city: "Crozet", totalsites: "207", firstcome: "50", reservable: "141", fees: "30.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/shen/planyourvisit/loft-mountain-campground.htm", description: "Loft Mountain (mile 79.5) sits atop Big Flat Mountain in the southern part of Shenandoah National Park, with outstanding views to east and west. Two waterfalls and the trails into the Big Run Wilderness area are nearby. Loft Mountain has a combination of reservable and first-come, first-served sites. Reservable sites are released in sections. No reservations can be made 48 hours before your selected date.", latitude: 38.246415, longitude: -78.67070100000001},
  {name: "Logging Creek", city: "West Glacier", totalsites: "7", firstcome: "7", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "The Logging Creek Campground is one of the smaller campgrounds in Glacier and is located on the west side of the park, south of Polebridge. Access is via the Inside North Fork Road, a dusty narrow winding road. Caution is advised when traveling this road. RVs and trailers are not permitted. Once there, campers will find a small primitive campground with all the solitude one could want. The trailhead to Logging Lake is nearby and the easy hike to the lake is a great family day hike.", latitude: 48.69845476183268, longitude: -114.19165560397357},
  {name: "Lone Rock Beach Primitive Camping Area", city: "Greenehaven", totalsites: "5000", firstcome: "10000", reservable: "0", fees: "14.00", showers: "Cold- Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/glca/planyourvisit/camping.htm", description: "No reservations. $14 per vehicle/per night in addition to entry fees. Primitive camping is on a sandy beach or in dunes. No designated campsites. Open fires permitted, must be within four foot squared area. Quiet time 10pm-6am. 4 micro flush toilets, 6 vault toilets, 1 comfort station/wheelchair accessible, outdoor cold shower, Off Road Vehicle area, dump station, potable water (seasonal), and day use area. No launch ramp.", latitude: 37.016159934, longitude: -111.540346568},
  {name: "Long Pine Key Campground", city: "Homestead", totalsites: "108", firstcome: "108", reservable: "108", fees: "33.00", showers: "Free - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/ever/planyourvisit/camping.htm", description: "Long Pine Key campground is open seasonally November-May. It is one of two frontcountry camping options run by the “Flamingo Adventures'' concession. Reservations are available for RV’s and tents along with first come first serve sites. If sites are booked, more camping may be available further down the Main Park Road in Flamingo.", latitude: 25.3979002291, longitude: -80.6569697813},
  {name: "Longleaf Campground", city: "f", totalsites: "14", firstcome: "14", reservable: "14", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10190876", description: "The Longleaf Campground offers walk up campsites for visitors wishing to camp in the frontcountry of Congaree. Campers may park and walk to their site. All campsites have a firepit and picnic table, and the campground has two vault toilets. 10 sites are for individuals or small groups of up to 8 people, while 4 sites are designated for larger groups from 9 to 24 people. The campground is a short drive from the visitor center, where running water is available, as well as park information.", latitude: 33.8357177464838, longitude: -80.82739767307794},
  {name: "Longs Peak Campground", city: "f", totalsites: "26", firstcome: "26", reservable: "0", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/romo/planyourvisit/lpcg.htm", description: "Longs Peak Campground is closed for the 2023/2024 winter season. It typically re-opens in July. Located about 20 minutes south of Estes Park on Hwy 7. This small, tents-only campground is forested and at a fairly high elevation of 9500 feet (3000 m). This campground has vault toilets and does not have any water. Campers must bring their own drinking water when camping at Longs Peak Campground.", latitude: 40.27479364272779, longitude: -105.55770432202075},
  {name: "Look Rock Campground", city: "Walland", totalsites: "68", firstcome: "0", reservable: "68", fees: "36.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grsm/planyourvisit/look-rock-campground.htm", description: "Look Rock Campground—elevation 2,600 feet (~792 m)—is located along the beautiful Foothills Parkway West between Walland, Tennessee and Chilhowee Lake. Flush toilets and drinking water are available. Ten of the sites offer electric and water hookups. No shower facilities are located in the campground.", latitude: 35.63690474693128, longitude: -83.93614247447806},
  {name: "Moskey Basin Campground", city: "Houghton", totalsites: "10", firstcome: "8", reservable: "2", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Moskey Basin Campground is located in Moskey Basin at the west end of Rock Harbor Channel, off of the Indian Portage and Rock Harbor Trails. Boats can overnight at dock. Canoe/kayak access from Rock Harbor Channel or via 2.0 mile portage from Lake Richie. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 6 Depth at dock (normal conditions): 8’. Access: Foot/canoe/kayak/private boat", latitude: 48.06298074914441, longitude: -88.64516328280524},
  {name: "Lost Creek Campground, elevation 6,000 feet (1,829 meters)", city: "Crater Lake", totalsites: "16", firstcome: "16", reservable: "0", fees: "5.00", showers: "None", toilets: "Portable Toilets - seasonal", phone: "No", url: "https://www.nps.gov/crla/planyourvisit/lost_creek.htm", description: "CLOSED ALL YEAR 2023 Lost Creek Campground is managed by the National Park Service. It usually opens in early July and closes in mid-October, depending on weather and other factors. It is for tent campers only--NO RVs, buses, trailers, or vans and truck with toilets. Registration is self-serve on the day of arrival. No advance registration is available. The campground fills by mid-afternoon. The only means of knowing site availability is upon arrival at the campground.", latitude: 42.87974939365855, longitude: -122.03871121690514},
  {name: "Lost Creek Group Campground", city: "Shingletown", totalsites: "8", firstcome: "0", reservable: "8", fees: "62.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/lost-creek-group-campground.htm", description: "Lost Creek Group Campground contains only group campsites. All sites are reservation-only. The campground is located five miles south of Manzanita Lake on the park highway. The campground offers easy access to Manzanita Lake, Devastated, and Summit Lake Areas.", latitude: 40.56184114262118, longitude: -121.51771884547509},
  {name: "Lower Goodell Creek Group Campground", city: "Sedro-Woolley", totalsites: "2", firstcome: "2", reservable: "2", fees: "75.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/234088", description: "Lower Goodell Group Campground is a convenient campground on the banks of Goodell Creek. Each group site has tent pads, a pavilion with picnic tables, and a fire grate. Maximum capacity is 50 people (20 vehicles) per site. Lower Goodell sites can accommodate RV's. Vault toilets and garbage service provided is provided. There are no hook-ups or potable water.", latitude: 48.67657475935347, longitude: -121.27136454780918},
  {name: "Lower Lehman Campground", city: "f", totalsites: "11", firstcome: "11", reservable: "0", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/grba/planyourvisit/campgrounds.htm", description: "Among the lush green vegetation which hugs Lehman Creek, you'll secure your tent or park your RV within earshot of clear mountain water which has tumbled 3000 vertical feet from the lakes and snowfields of the high Snake Range. Naturally landscaped with red-barked water birch, aspen and white fir a stroll through this sky island ecosystem provides for great bird-watching or a cooling dip in the stream while placing you just minutes from spectacular Lehman Caves.", latitude: 39.01737, longitude: -114.23798},
  {name: "Lower Pines Campground", city: "Yosemite", totalsites: "73", firstcome: "0", reservable: "73", fees: "36.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/yose/planyourvisit/pinescampgrounds.htm", description: "Lower Pines is located along the Merced River in Yosemite Valley. Yosemite Valley is centrally located in the park and boasts some of Yosemite’s most iconic features. This campground is located at 4,000 feet (1,219 m) elevation and can be accessed from all park roads. Lower Pines is within biking and walking distance of many services and trailheads in Yosemite Valley and is located on the free shuttle route. There are food and grocery services nearby at Curry Village and Yosemite Village.", latitude: 37.7394658916, longitude: -119.566313767},
  {name: "Lower Scorpion Campground", city: "f", totalsites: "7", firstcome: "7", reservable: "0", fees: "f", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/gicl/planyourvisit/camping.htm", description: "Lower Scorpion Campground is a partially developed campground within walking distance of the West Fork of the Gila River. The campground is located less than one mile from the Gila Cliff Dwellings National Monument just off NM State Highway 15. Drinking water is available, tables and grills are provided at select sites. Vault toilets are available on site. Camping is free of charge and first-come, first-served.", latitude: 33.229944, longitude: -108.25757},
  {name: "Lower St. Croix District", city: "f", totalsites: "19", firstcome: "19", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/sacn/planyourvisit/lowerstcroix.htm", description: "19, primitive, Leave No Trace campsites are located on this reach from below Highway 8 down to the Boom Site Landing near Stillwater, Minnesota. An annual camping permit is required to camp. The District also consists of Minnesota and Wisconsin state park properties with campgrounds.", latitude: 45.253281, longitude: -92.760328},
  {name: "Lowney Creek Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: nil, toilets: nil, phone: "", url: "", description: "Lowney Creek Group Campground is one mile from Little Beaver Lake Campground and 2.5 miles inland from Lake Superior. Lowney Creek Group Campground is in the Beaver Basin Wilderness. The trail to the campground follows the Beaver Lake shoreline. Water source is Beaver Lake. One shared fire ring and NO toilet. Water access to this campground is via Little Beaver Lake, then Beaver Lake.", latitude: 46.557203, longitude: -86.344702},
  {name: "Lowney Creek Individual Backpacking Sites", city: "f", totalsites: "4", firstcome: "0", reservable: "4", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Lowney Creek Campground is 1.5 miles from Little Beaver Lake Campground and three miles inland from Lake Superior. Lowney Creek Campground is in the Beaver Basin Wilderness. The four sites are in a meadow scattered with big trees on the south shore of Beaver Lake. Lowney Creek is on the east side of the campground. One shared fire ring and NO toilet. Water source is Lowney Creek and Beaver Lake. Water access to the campground is via Little Beaver Lake, then Beaver Lake.", latitude: 46.558462, longitude: -86.33977},
  {name: "Lunksoos Campsites", city: "Patten", totalsites: "7", firstcome: "7", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "Walk-to campsites near the East Branch of the Penobscot River.", latitude: 45.908436, longitude: -68.613925},
  {name: "Lunksoos Mountain Lean-To", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "A rugged lean-to site within the heart of the backcountry of Katahdin Woods & Waters National Monument.", latitude: 45.9569815730881, longitude: -68.7016996665859},
  {name: "Madison Campground", city: "Yellowstone National Park", totalsites: "276", firstcome: "0", reservable: "278", fees: "33.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yell/planyourvisit/madisoncg.htm", description: "Madison Campground—elevation 6,800 feet (2073 m)—sits about 14 miles east from the town of West Yellowstone and 16 miles north of Old Faithful. Nearby, the Gibbon and Firehole rivers join to form the Madison River. In early summer, meadows teem with wildflowers and bison. In September and October, you can often hear bugling elk. Yellowstone National Park Lodges provides reservations for this campground.", latitude: 44.6454215492, longitude: -110.861302682},
  {name: "Mahogany Flat Campground (Primitive)", city: "Death Valley", totalsites: "9", firstcome: "9", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/deva/planyourvisit/mahogany-flat-primitive-campground.htm", description: "First come / first served primitive campground with no additional fee located in a Pinyon Pine and Juniper forest at 8,200' elevation. Great views down into Death Valley. Located near the Telescope Peak trailhead. Dirt road access requires high-clearance vehicles, often 4x4 required.", latitude: 36.23, longitude: -117.068377},
  {name: "Malaquite Campground", city: "Corpus Christi", totalsites: "48", firstcome: "48", reservable: "0", fees: "14.00", showers: "Cold - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/pais/planyourvisit/malaquite_campground.htm", description: "Malaquite Campground is open to tents and RVs on a first-come, first-served basis (no reservations). It is between the dunes with beach access. There are designated paved and gravel camp sites, restrooms, cold water showers, and picnic tables. Some sites have grills and shade structures. No hookups but a dump station and potable water are on the road to the campground. Campers with a paid site in the campground can pitch a tent either at the site or on the beach in front of the campground.", latitude: 27.43023, longitude: -97.296189},
  {name: "Malone Bay Campground", city: "Houghton", totalsites: "7", firstcome: "5", reservable: "2", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Malone Bay Campground is located in Malone Bay, on the south shore of Isle Royale National Park, off of the Ishpeming Trail. Boats can overnight at dock. A ranger station, information station, and dock can be found just further east of the campground. The ranger station is not always staffed. Stay Limit: 3 nights effective June 1 – Labor Day annually. Shelters: 5 Depth at dock (normal conditions): 3’-6’. Access: Foot/canoe/kayak/private boat", latitude: 47.98524426035943, longitude: -88.80722670151698},
  {name: "Mammoth Campground", city: "Yellowstone National Park", totalsites: "85", firstcome: "0", reservable: "85", fees: "25.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/yell/planyourvisit/mammothhscg.htm", description: "CLOSED for 2023 (2024 dates TBD). The only campground in the park open year-round, Mammoth Campground—elevation 6,200 feet (1890 m)—is located five miles south of the park's North Entrance. Scattered juniper and Douglas fir trees provide shade during hot summer months. The campground is close to fishing, hiking, and the Mammoth Hot Springs. Great wildlife viewing opportunities abound with elk and bison occasionally passing through the campground. This is a reservable campground 4/1–10/15 run by the NPS.", latitude: 44.97360954829384, longitude: -110.69323158293622},
  {name: "Mammoth Cave Campground", city: "Mammoth Cave", totalsites: "111", firstcome: "2", reservable: "109", fees: "25.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "The Mammoth Cave Campground is ideal for visitors seeking an authentic national park experience while still having easy access to amenities, cave tours, and other park activities. This developed campground is located within ¼ mile from the visitor center and contains 111 primitive campsites with a mixture of tent only sites, group sites, tent or RV sites, and accessible sites. Rangers on duty in the campground kiosk are happy to provide information to make your stay a special adventure.", latitude: 37.1821894000506, longitude: -86.09704310043361},
  {name: "Manning Camp", city: "Tucson", totalsites: "6", firstcome: "6", reservable: "6", fees: "8.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "- 8,000 feet elevation - 6 campsites (max 6 people per site) - water available year-round", latitude: 32.207386, longitude: -110.554542},
  {name: "Many Glacier", city: "West Glacier", totalsites: "109", firstcome: "0", reservable: "110", fees: "23.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "The campground at Many Glacier is one of the most popular campgrounds in Glacier National Park. The campground is situated within trees for tent and RV campers, though there are only 13 sites that can accommodate vehicle lengths up to 35 feet in length. Bring your binoculars, as there are opportunities to view wildlife like bighorn sheep, moose, and bears. Many Glacier also provides access to some of the best day hikes in the park. Advanced reservations are required for all sites.", latitude: 48.7964957236, longitude: -113.677619731},
  {name: "Manzanita Lake Campground", city: "Shingletown", totalsites: "179", firstcome: "0", reservable: "179", fees: "26.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/manzanita-lake-campground.htm", description: "Manzanita Lake Campground is located one mile east of the Manzanita Lake Entrance and is the largest campground in the park. This popular campground is ideal for families, RVs, and trailers. Located adjacent to Manzanita Lake, campers can enjoy swimming, fishing, kayaking, and hiking. This campground is open the longest except for the year-round Southwest Walk-in Campground. The campground includes rustic camping cabins and group campsites, both of which are available by reservation only.", latitude: 40.5278515681602, longitude: -121.563186589946},
  {name: "Maple Springs Group Campground", city: "Mammoth Cave", totalsites: "7", firstcome: "0", reservable: "8", fees: "50.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Maple Springs Group Campground lies on the park's north side, six miles from the visitor center and three miles north of Green River Ferry. This campground offers more secluded sites ideal for larger groups of campers and their horse companions. All sites have electric and water hookups for RVs. This campground is a natural launching-point for forays along the more than 70 miles of backcountry trails in Mammoth Cave National Park.", latitude: 37.204205, longitude: -86.136092},
  {name: "Marble Quarry Hiker-Biker Campsite", city: "Poolesville", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 38.2 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.179557999999986, longitude: -77.492708},
  {name: "Marcus Island Campground", city: "Marcus", totalsites: "25", firstcome: "0", reservable: "25", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-marcusisland.htm", description: "Marcus Island Campground is tucked into the trees on the lake shore. The campground is heavily shaded and, in the summer, has shoreline on three sides. The main access road is single lane and winding, but campers flock to this shaded area in summer. A boat launch with parking is available before reaching the campground.", latitude: 48.6687321922, longitude: -118.057368267},
  {name: "Marcus Wilderness Campground", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/ms_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. From Dog Canyon, hikers travel through grassy areas then cross Manzanita Ridge to view West Dog Canyon before descending to an elevation equivalent to the starting point. Not frequently used; pay attention to the trail and rock cairns marking the way. The campground is in pinion and juniper, shaded and protected from the wind. The distance from Dog Canyon is 3.7 miles.", latitude: 31.981171147850624, longitude: -104.87685414903677},
  {name: "Marsden Tract Group Campground", city: "f", totalsites: "6", firstcome: "0", reservable: "6", fees: "40.00", showers: "None", toilets: "Portable Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/251544", description: "Mile 11.5 (Montgomery County, MD). The campground has six group campsites; sites accommodate up to 30 campers at each site. Each campsite has a picnic table and a fire ring. Potable water is located a short walk up the towpath and across the footbridge (water may not be available during cold winter months). Portable toilets are in the campground. No vehicle traffic is allowed on the towpath and access to the campsites is by foot only. Carderock Recreation Area or Anglers are the two closest parking areas.", latitude: 38.97679701853521, longitude: -77.21484911273555},
  {name: "Masse Homestead Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Masse Homestead Group Campground is to the west of the regular campground. It too is at the base of the dunes in a thick forest canopy. One shared fire ring and NO toilet. Visitors must bring water. NO ACCESS TO WATER.", latitude: 46.6468043665, longitude: -86.0956764204},
  {name: "Masse Homestead Individual Backpacking Sites", city: "f", totalsites: "3", firstcome: "0", reservable: "3", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Masse Homestead Campground is at the base of the dunes (south side) and sheltered due to the thick forest canopy. The three campsites are close together at the base of the dunes. One shared fire ring and NO toilet. Visitors must bring water. NO ACCESS TO WATER.", latitude: 46.647693, longitude: -86.090932},
  {name: "Mather Campground - South Rim", city: "Grand Canyon", totalsites: "327", firstcome: "15", reservable: "300", fees: "18.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/grca/planyourvisit/mather-campground-south-rim.htm", description: "Mather Campground is located in Grand Canyon Village on the South Rim of Grand Canyon National Park. There are 327 sites. Each includes a campfire ring/cooking grate, picnic table, parking space and room for up to six people, three tents, and two vehicles. There are flush toilets and drinking water throughout the campground. No hookups are available, however there is a free dump station. Most RV spaces are pull-through. Pine loop is a tent-only area where generators are not permitted.", latitude: 36.04918080776177, longitude: -112.11849726539117},
  {name: "Mathews Arm Campground", city: "Luray", totalsites: "165", firstcome: "47", reservable: "112", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/shen/planyourvisit/mathews-arm-campground.htm", description: "Mathews Arm Campground (mile 22.1) is the nearest campground for those entering Shenandoah National Park from Front Royal, in the northern section of the Park. All sites include a place for a tent or RV, a fire ring, and picnic table. Elkwallow Wayside, with camping supplies and food service, is two miles away. Mathews Arm has a combination of reservable and first-come, first-served sites. Reservable sites are released in sections. No reservations can be made 48 hours before your selected date.", latitude: 38.763144, longitude: -78.29638799999998},
  {name: "Maumee South", city: "Marshall", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Maumee South Campground is a large campground in the lower district of the Buffalo River. There are no amenities at Maumee South. There are no designated sites. A vault restroom is available. Fees are not charged at Maumee South.", latitude: 36.0394860005, longitude: -92.635645801},
  {name: "Mazama Campground", city: "Crater Lake", totalsites: "214", firstcome: "0", reservable: "214", fees: "21.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/crla/planyourvisit/mazama_campground.htm", description: "Mazama Campground is open only in summer. It typically opens in June and closes in late September. The exact opening and closing dates are determined each year by the snow level in the camp sites, safety issues, and other factors. Mazama Campground is nestled in an old-growth forest at 6,000 feet (1,829 m) in elevation. There are 214 sites each with a picnic table, fire ring, and bear-resistant food locker. The concessioner, Crater Lake Hospitality, manages the campground. For more details go reservations.", latitude: 42.86862146054785, longitude: -122.1677826463598},
  {name: "McBride Canyon and Mullinaw Creek Campgrounds", city: "f", totalsites: "40", firstcome: "40", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "These areas are located south of the lake off of State Highway 136. There are large cottonwood trees that provide shade. The Mullinaw Creek area features the expanded Mullinaw Trails System . This trail system is open to hiking, biking, and horseback riding. NO OFF ROAD VEHICLES ALLOWED. No drinking water or flush toilets; vault-evaporator toilets only. Picnic tables, shade shelters, and grills. Horse corrals are located at Mullinaw Campground. The dirt roads can become impassable after rain.", latitude: 35.529449, longitude: -101.759431},
  {name: "McCargoe Cove Campground", city: "Houghton", totalsites: "12", firstcome: "9", reservable: "3", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "McCargoe Cove Campground is located in McCargoe Cove, on the north shore of Isle Royale National Park, off of the Indian Portage and East Chickenbone Trails. Boats can overnight at dock. Canoe/kayak access from Lake Superior or 1.2 mile portage from Chickenbone Lake. Stay Limit: 3 nights effective from June 1 – Labor Day annually. Shelters: 6 Depth at dock (normal conditions): 7’. Access: Foot/canoe/kayak/private boat", latitude: 48.08719604689656, longitude: -88.70934934701029},
  {name: "McKittrick Ridge Wilderness Campground", city: "f", totalsites: "8", firstcome: "0", reservable: "8", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/mr_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. Travel through the beautiful McKittrick Canyon before beginning the very steep climb up to the ridge. From McKittrick Canyon Trailhead, the distance is 7.6 miles, with the elevation gain (significant for quite some distance) of over 2,700 feet. If you'd like to visit this beautiful ridge without quite the workout, begin instead at Dog Canyon.", latitude: 31.974347732744395, longitude: -104.80822691423316},
  {name: "McMillan Woods Youth Campground", city: "Gettysburg", totalsites: "10", firstcome: "10", reservable: "10", fees: "0.00", showers: "None", toilets: "Portable Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/gett/planyourvisit/campgrounds.htm", description: "For scouting and organized youth groups that visit Gettysburg National Military Park, we offer McMillan Woods Youth Campground, located in the park on West Confederate Avenue. The campground is open from mid-April through the end of October every year. Any organized youth group with adequate adult supervision (minimum of one adult for every 10 youths) is welcome to use the camping area. Camping here is by reservation only and camp sites are reserved on a first come, first served basis.", latitude: 39.8210049992, longitude: -77.2461709993},
  {name: "Mccoys Ferry Campground", city: "f", totalsites: "13", firstcome: "0", reservable: "13", fees: "40.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/252969", description: "Mile 110.4 (Washington County, MD) - The campground has 12 single drive-in campsites. Each single accommodates up to eight people. Single campsites have a picnic table, fire ring and grill. There is one group site available with space for two vehicles and a small trailer or support vehicle less than 20 ft. Group site can accommodate 30 people and has four picnic tables with grill and fire pit. Pit toilets and accessible portable toilets are available. Hand pump water is available April 15 - November 1.", latitude: 39.60923039169212, longitude: -77.9666398364306},
  {name: "Meadow Creek Campground", city: "Sandstone", totalsites: "26", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/neri/planyourvisit/camping.htm", description: "This tent-only primitive campground located along the New River is only one mile from the Sandstone Visitor Center. It offers 17 tent sites in an open field. There are no drive-in sites at this campground. The railroad crossing is dangerous for RVs and trailers. RV, trailer, or car camping is prohibited. This campground is located close to active CSX freight railroad tracks and railroad crossing. Trains may pass through at any hour. Campers should expect to hear some noise such as train horns.", latitude: 37.79789071761994, longitude: -80.92104624026769},
  {name: "Medano Pass Primitive Road Campsites", city: "f", totalsites: "21", firstcome: "21", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/grsa/planyourvisit/medano-pass-road.htm", description: "Roadside camping is permitted at 21 campsites in Great Sand Dunes National Preserve beginning 5.2 miles from where the road begins near Piñon Flats Campground. These designated sites are free of charge and first-come, first-served. All 21 sites fill on summer weekends. Passable only in the warmer months and only with high-clearance 4-wheel drive vehicles. This road crosses areas of deep sand, traverses Medano Creek nine times, and passes through excellent habitat for bighorn sheep.", latitude: 37.803519, longitude: -105.495654},
  {name: "Medicine Creek Campground", city: "Lovell", totalsites: "5", firstcome: "8", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/bica/planyourvisit/medicine-creek.htm", description: "Medicine Creek campground is a boat-in or hike-in only campground. It is approximately 3 miles north of Barry's Landing by land or water. - Open All Year - Lake level affects the proximity of the boat mooring to campsites", latitude: 45.1101806592348, longitude: -108.20431158086},
  {name: "Meriwether Lewis Campground, Milepost 385.9", city: "Hohenwald", totalsites: "31", firstcome: "32", reservable: "0", fees: "0.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/places/meriwether-lewis-campground-milepost-385-9.htm", description: "The Meriwether Lewis Campground at milepost 385.9, on the northern end of the Parkway, has 32 sites. All sites are free and available on a first-come, first served basis. They do not offer electricity, showers, or dump stations. Drinkable water and flush toilets are available on site. Enjoy the Meriwether Lewis site which includes hiking trails, a section of Old Trace, exhibits and picnic areas.", latitude: 35.5224918473591, longitude: -87.4556851746386},
  {name: "Merritt Lane Campground", city: "Houghton", totalsites: "2", firstcome: "2", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/canoe-and-kayak.htm", description: "Merritt Lane Campground is located in Merritt Lane on the northeast end of Isle Royale National Park. Boats can overnight at dock. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 1 Depth at dock (normal conditions): 8’ Access: Canoe/kayak/private boat.", latitude: 48.18459316327891, longitude: -88.42965712508789},
  {name: "Mescalero Wilderness Campground", city: "f", totalsites: "8", firstcome: "0", reservable: "8", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/mo_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. This campground is near several trails leading in different directions, making it an excellent choice for a \"base camp\" while exploring the high country. (Listen for wild turkeys off in the distance.) Mescalero is situated in ponderosa pine and brush, and is on a slope overlooking a small drainage. Located on the Tejas Trail, Mescalero is 6.2 miles from Pine Springs Trailhead or 4.7 miles from Dog Canyon.", latitude: 31.9526879019085, longitude: -104.847984983904},
  {name: "Mesquite Spring Campground", city: "Death Valley", totalsites: "40", firstcome: "40", reservable: "0", fees: "7.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/deva/planyourvisit/mesquite-spring-campground.htm", description: "First come / first served campground at an elevation of 1,800 feet, located 2 miles off of the Scotty’s Castle Road below Grapevine Canyon. It is a great base camp for your adventures in the northern parts of Death ValIey National Park. It is surrounded by desert mountains and geological features. Each site has a fire grate and picnic table. There are no hook-ups available. Fee is paid at the automatic pay station at the front of the campground.", latitude: 36.9636498079, longitude: -117.367774161},
  {name: "Mid-Hills Campground", city: "Barstow", totalsites: "26", firstcome: "26", reservable: "0", fees: "12.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/moja/planyourvisit/campgrounds.htm", description: "The Hackberry Fire swept through the Mid Hills area in June 2005, burning much of the vegetation. About half of the 26 campsites were left unburned, however, and remain surrounded by pinyon pine and juniper trees. At 5,600 feet in elevation, Mid Hills is much cooler than the desert floor below. The access road is unpaved and somewhat steep, and is therefore not recommended for large motorhomes or trailers. Water is not available at Mid Hills Campground.", latitude: 35.1299204178, longitude: -115.435791576},
  {name: "Middle St. Croix", city: "f", totalsites: "57", firstcome: "57", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/sacn/planyourvisit/middlestcroix.htm", description: "57, primitive, Leave No Trace campsites are located on the St. Croix River from Riverside Landing to Highway 8 at St. Croix Falls - Taylors Falls. All sites are free. No permit required. Camp only in designated sites marked with brown camping sign and metal fire ring. Sites are available first-come, first-served.", latitude: 45.773341, longitude: -92.781074},
  {name: "Midway Campground", city: "Ochopee", totalsites: "36", firstcome: "0", reservable: "36", fees: "30.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "", description: "This campground offers electric hookups for RV sites, dump station, flush toilets, and water. Each RV campsite has its own picnic table and hibachi style grill. Covered picnic areas are located around the lake for day use.", latitude: 25.851926, longitude: -80.989444},
  {name: "Mill Creek Campground", city: "Crescent City", totalsites: "145", firstcome: "0", reservable: "145", fees: "35.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/redw/planyourvisit/mill-creek-campground.htm", description: "Sleep beneath towering maples and young redwoods. Access to Mill Creek, miles of varied hiking trails, and summer programs. Campers at Mill Creek may hear restoration work including saws and heavy equipment through October 15. For more information, visit Redwoods Rising main - https://www.nps.gov/redw/redwoodsrisingmain.htm. Located in Del Norte Coast Redwoods State Park. This is 6.7 miles south of Crescent City.", latitude: 41.702724169382016, longitude: -124.09778463073394},
  {name: "Mitchell's Landing", city: "Ochopee", totalsites: "11", firstcome: "0", reservable: "11", fees: "24.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Primitive camping with no water. Vault toilets are available. Eleven sites available. Access to the Mitchell Landing Campground is along a secondary gravel road.", latitude: 25.7564496482, longitude: -80.9305981752},
  {name: "Mohican Outdoor Center", city: "Blairstown", totalsites: "16", firstcome: "7", reservable: "8", fees: "f", showers: "Hot - Year Round", toilets: "Portable Toilets - year round", phone: "No", url: "https://www.outdoors.org/lodging-camping/lodges/mohican", description: "Primitive group campsites in a wooded setting adjacent to the Appalachian National Scenic Trail in New Jersey; Drinking water available; Call 603-466-2727 for additional information regarding lodge or cabin rentals.", latitude: 41.0350165004, longitude: -75.0010072019},
  {name: "Monument Lake", city: "Ochopee", totalsites: "36", firstcome: "0", reservable: "36", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Monument Lake campground offers restrooms, drinking water and designated 26 RV and 10 tent sites. NO HOOKUPS for electricity, sewer or water are available at this campground.", latitude: 25.8684663476, longitude: -81.1131314752},
  {name: "Mora Campground", city: "Forks", totalsites: "94", firstcome: "0", reservable: "94", fees: "24.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/mora-area-brochure.htm", description: "Situated in a coastal forest, some sites offer views views of the Quillayute River. Mora is located two miles from Rialto Beach.", latitude: 47.9168610105267, longitude: -124.60593738701},
  {name: "Moraine Campground", city: "Kings Canyon National Park", totalsites: "121", firstcome: "0", reservable: "121", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/moraine.htm", description: "Not currently taking reservations for summer 2023, due to winter storm damage. Typically opens end of May with reservations beginning end of April, on a one-month rolling basis. Just 0.75 miles (1.2 km) from Cedar Grove Village, Moraine Campground rests in the heart of King Canyon. It is located along the South Fork of the Kings River under stands of evergreen trees at an elevation of 4,600 feet (1,400 m). Visit Cedar Grove Village for services such as showers and food.", latitude: 36.78521626309886, longitude: -118.66105680282118},
  {name: "Moraine Park Campground", city: "Estes Park", totalsites: "244", firstcome: "0", reservable: "239", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/romo/planyourvisit/mpcg.htm", description: "Moraine Park Campground is closed through early June 2024 for a major rehabilitation project. For details, visit https://www.nps.gov/romo/getinvolved/moraine-park-campground-and-headquarters-east-utility-rehabilitation-project.htm", latitude: 40.36066149616986, longitude: -105.60006623223796},
  {name: "Morefield Campground", city: "Mesa Verde National Park", totalsites: "267", firstcome: "267", reservable: "267", fees: "38.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.visitmesaverde.com/lodging-camping/morefield-campground/", description: "Morefield Campground is located just four miles from the park entrance. The 267 campsites are located within a high grassy canyon filled with Gambel oak, native flowers, deer, and wild turkeys. A camp store offers registration, food, and camp supplies. Firewood, gasoline, showers, a coin-operated laundromat, and a kennel are located nearby. Each site has a picnic table, gravel tent pad, and fire pit/BBQ grill. The campground is managed by park concessioner Aramark and is open between spring and fall.", latitude: 37.3014992738134, longitude: -108.417912473544},
  {name: "Mosquito River Individual Backpacking Sites", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Mosquito River Campground is on the east side of the Mosquito River high above the lake. Depending upon which of the five campsites you choose, you can hear the waves crashing on the cliffs or the Mosquito River flowing into Lake Superior. Shared pit toilet. Water source is Mosquito River and Lake Superior. Lake Superior access for kayakers. NO CAMPFIRES.", latitude: 46.527061, longitude: -86.492008},
  {name: "Mount Pisgah", city: "Asheville", totalsites: "125", firstcome: "72", reservable: "53", fees: "20.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov//blri/planyourvisit/mt-pisgah-cg.htm", description: "Mount Pisgah Campground is located in the mountains of western North Carolina at an elevation of 4,980 feet, and offers moderate to cool temperatures during camping seasons. Mount Pisgah is part of the Appalachian Mountain Range, an area of the country with scenery that never disappoints, whether blanketed in wildflowers in the spring or a bursting with orange, yellow and red hues in the fall. Several popular hiking trails, ranging from moderate to difficult, are easily accessible from the campground.", latitude: 35.40286804539159, longitude: -82.75691997303858},
  {name: "NPS Priest Rock Public Use Cabin", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.nps.gov/lacl/planyourvisit/priest-rock-cabin.htm", description: "The Priest Rock Cabin sits on the north shore of Lake Clark, approximately eight miles north of Port Alsworth. The cabin is perched above a small creek that runs into the lake. It commands a sublime view of Lake Clark's upper reaches, backed by mountains rising to 6,000 feet. It's an ideal place for kayaking, boating, fishing and wildlife viewing.", latitude: 60.3093333056, longitude: 154.177},
  {name: "Namekagon District", city: "f", totalsites: "67", firstcome: "67", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/sacn/planyourvisit/namekagon-river.htm", description: "67, primitive, Leave No Trace camp sites are located on the Namekagon River from Namekagon Dam to the confluence of the St Croix River and on the St. Croix River from Gordon Dam to Riverside Landing. All sites are free. No permit required. Camp only in designated sites marked with brown camping sign and metal fire ring. Sites are available first-come, first-served.", latitude: 45.905263, longitude: -91.815991},
  {name: "Natural Bridges Campground", city: "Blanding", totalsites: "13", firstcome: "13", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/nabr/planyourvisit/camping.htm", description: "Camp in solitude among the juniper trees at Natural Bridges campground. The campground is conveniently located next to the visitor center off the main park road. Campsites are first-come, first-served and open year-round. Each site has a fire grill, picnic table, and tent pad, but no running water, electricity, or hookups.", latitude: 37.60921843789215, longitude: -109.98419312147183},
  {name: "Naval Live Oaks Youth Group Camping", city: "Gulf Breeze", totalsites: "1", firstcome: "0", reservable: "1", fees: "20.00", showers: nil, toilets: nil, phone: "", url: "https://www.nps.gov/guis/planyourvisit/nlo-group.htm", description: "This area is available reservation throughout the year for organized youth groups comprised primarily of young people under the age of 18. This group camping area is located near Gulf Breeze, Florida and has direct access to Pensacola Bay for water recreation. The campsite features restrooms, outdoor showers, a picnic shelter, and campfire ring. The Naval Live Oaks Area has over 7.5 miles of trails for campers to explore.", latitude: 30.3689892485485, longitude: -87.1423119758788},
  {name: "Newhalem Creek Campground", city: "Sedro-Woolley", totalsites: "107", firstcome: "0", reservable: "107", fees: "24.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/234060", description: "Newhalem Campground is a comfortable facility close to the town of Newhalem, Washington, and the Skagit River. It is surrounded by forests that that can be explored through the area's many hiking trails. The campground has individual campsites as well as two group sites. It is private and secluded, yet the town, a visitor center and other amenities are only a short walk away.", latitude: 48.671, longitude: -121.261},
  {name: "Norris Campground", city: "Yellowstone National Park", totalsites: "112", firstcome: "0", reservable: "112", fees: "25.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yell/planyourvisit/norrishscg.htm", description: "CLOSED IN 2023. This campground–elevation 7,500 feet (2,286 m)–is located near a large open meadow, which provides opportunity for wildlife viewing. Bison frequently walk through the campground. Most sites are shaded by lodgepole pine. The Museum of the National Park Ranger is a quick walk from the campground, as are Norris Geyser Basin Museum and Norris Geyser Basin--the hottest and most changeable thermal area in Yellowstone. This is a reservable campground run by the National Park Service.", latitude: 44.73784854861962, longitude: -110.69361508287096},
  {name: "North Campground", city: "Bryce", totalsites: "100", firstcome: "0", reservable: "96", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/brca/planyourvisit/northcampground.htm", description: "Located across the road from the Visitor Center and is comprised of 100 sites in 4 loops; A, B, C, D. Loops A & B are for RV campers. Loops C & D are for tent campers (tents are permitted in all loops). There are no sewer, water or electrical hook-ups available. A dump station is available in summer months near North Campground; use is included with campsite fee. Potable water is available near the dump station. North Campground is close to the Visitor Center, General Store, and Fairyland Loop/Rim Trail.", latitude: 37.638096, longitude: -112.167922},
  {name: "North Desor Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "North Desor Campground is located on the north side of Lake Desor off of the Minong Ridge Trail. There is no boat/canoe/kayak access. Stay Limit: 2 nights from June 1 – Labor Day Access: Foot", latitude: 47.98085044383097, longitude: -88.99386540717819},
  {name: "North Fork Campground", city: "Port Angeles", totalsites: "9", firstcome: "9", reservable: "0", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/quinault-area-brochure.htm", description: "Surrounded by temperate rain forest, this small and remote campground is a great spot for campers seeking solitude.", latitude: 47.5698520216, longitude: -123.650803296},
  {name: "North Gorge Campground", city: "Evans", totalsites: "12", firstcome: "0", reservable: "12", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-northgorge.htm", description: "North Gorge is a small, but popular, campground at the northeast edge of the park. A few sites are large enough for an RV, but most are built for tent camping. The campground is set in a grove of pines on the edge of the lake. A boat launch and dock are on the north or right side of the main loop. In the summer, North Gorge is busy on the weekends.", latitude: 48.7864756738437, longitude: -118.003288633079},
  {name: "North Mountain Hiker-Biker Campsite", city: "Clear Spring", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 110.0 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.610663, longitude: -77.959636},
  {name: "Pink Jeep", city: "Ochopee", totalsites: "9", firstcome: "9", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "This is a primitive campground, containing nine campsites. No water. Vault toilets are available. Pink Jeep can be accessed by off-road vehicle, hiking or biking. Backcountry permits are required for all, off-road vehicle permits are required for off-road vehicles.", latitude: 26.216331047905975, longitude: -81.29514337546641},
  {name: "North Pines Campground", city: "Yosemite", totalsites: "80", firstcome: "0", reservable: "80", fees: "36.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/yose/planyourvisit/pinescampgrounds.htm", description: "North Pines is located along the Merced River in Yosemite Valley. Yosemite Valley is centrally located in the park and boasts some of Yosemite’s most iconic features. This campground is located at 4,000 feet (1,219 m) elevation and can be accessed from all park roads. North Pines is within biking and walking distance of many services and trailheads in Yosemite Valley and is located on the free shuttle route. There are food and grocery services nearby at Curry Village and Yosemite Village.", latitude: 37.7404163398378, longitude: -119.565030847118},
  {name: "North Rim Campground", city: "Crawford", totalsites: "13", firstcome: "13", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/blca/planyourvisit/camp_northrim.htm", description: "The North Rim campground is remote and has 13 sites dispersed among pinyon and juniper trees. Sites available on a first-come, first-served basis. The last 7 miles of the North Rim Road and the campground road are unpaved. Services are very limited. To protect vegetation and forest resources, please use designated tent pads and do not tie, hang, or attach anything to trees. This campground fills quickly during the summer, especially on weekends. Maximum combined length of RV, car and/or trailer is 22 feet.", latitude: 38.58518332872675, longitude: -107.70942777084946},
  {name: "North Rim Campground", city: "North Rim", totalsites: "87", firstcome: "0", reservable: "78", fees: "18.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grca/planyourvisit/cg-nr.htm", description: "The North Rim Campground, scheduled to be open from June 9, through October 15, 2023, is located on the North Rim of Grand Canyon National Park in northern Arizona. The canyon's rustic and less populated North Rim is home to abundant wildlife, hiking trails, and unparalleled views of one of the Seven Natural Wonders of the World. The facility is at an elevation of 8,200 ft., with pleasant summer temperatures and frequent afternoon thunderstorms.", latitude: 36.208690159698094, longitude: -112.05997437790796},
  {name: "Nāmakanipaio", city: "Hawaii National Park", totalsites: "16", firstcome: "16", reservable: "16", fees: "15.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/havo/planyourvisit/namakanipaio-campground.htm", description: "Nāmakanipaio Campground is located 31.5 miles south of Hilo on Highway-11 at 4,000' elevation. It is a large, open grassy area with tall eucalyptus and 'ōhi'a trees. This campground has restrooms, water, picnic tables and barbecue pits. Campfires are permitted in the barbeque pits only. If there are more than 2 people in your party, you can expand to 4 people per site with another tent. Maximum stay is 7 days.", latitude: 19.4253736529641, longitude: -155.295878745916},
  {name: "Oak Ridge Campground", city: "Triangle", totalsites: "100", firstcome: "0", reservable: "95", fees: "26.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/prwi/planyourvisit/oakridge.htm", description: "Oak Ridge Campground is a 100-site campground with bathrooms, grills and picnic tables, and drive-up campsites. Leashed pets are welcome. Both tents and RVs are welcome, though there are no hook-ups for RVs and maximum RV length is 32\". Reservations are required for all campsites. Make a reservation on recreation.gov or by calling 877-444-6777. The campground is closed for the winter on December 1 and re-opens on March 3.", latitude: 38.60042021817536, longitude: -77.41609901660377},
  {name: "Oceanside Drive-in Campground", city: "Berlin", totalsites: "16", firstcome: "0", reservable: "16", fees: "40.00", showers: "Cold- Seasonal", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://cms.nps.gov/asis/planyourvisit/oceanside-drive-in-campground-reservation.htm", description: "Tents, trailers and recreational vehicles (no hookups). Sites have a picnic table and a fire ring. Sites are $40 per night. Reservations are required from March 15 through November 15. All sites are First Come/First Serve from November 16 through March 14.", latitude: 38.20161059949485, longitude: -75.15386509969085},
  {name: "Oceanside Group Campground", city: "Berlin", totalsites: "5", firstcome: "0", reservable: "5", fees: "80.00", showers: "Cold- Seasonal", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/oceanside-group-campground-reservation.htm", description: "Organized clubs and affiliated groups must use group campsites. Tent-only sites, located 100-200 feet from centralized parking area. Sites have multiple picnic tables and fire rings with grills. Group campsites can be reserved year round, up to 6 months in advance at $80 per night per site. 7 minimum - 25 maximum campers per site.", latitude: 38.20377, longitude: -75.1524},
  {name: "Oceanside Walk-in campground", city: "Berlin", totalsites: "62", firstcome: "0", reservable: "21", fees: "40.00", showers: "Cold- Seasonal", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/asis/planyourvisit/oceanside-walk-in-campground-reservation.htm", description: "Tent only sites. Sites have a picnic table and a fire ring. Sites are $40 per night. Reservations are required from March 15 through November 15. These sites are closed from November 16 through March 14.", latitude: 38.1948063, longitude: -75.1564668},
  {name: "Ocracoke Campground", city: "Ocracoke", totalsites: "135", firstcome: "0", reservable: "131", fees: "28.00", showers: "Cold - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/232504", description: "Ocracoke Campground with 136 sites is located east of the village of Ocracoke, North Carolina on the island of Ocracoke. The campground offers grills, flush toilets, drinking water, cold showers, and is pet-friendly. The soil is sandy, so tent-campers will need extra-long tent stakes. RVs under 40 feet are welcome at this campground. There's no electricity, but there is a nearby dump station. There is no shade at the campground. Mosquitoes are common, and insect spray is recommended. Open year round.", latitude: 35.12571784418955, longitude: -75.92241525650024},
  {name: "Ohanapecosh Campground", city: "f", totalsites: "179", firstcome: "50", reservable: "129", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/mora/planyourvisit/campgrounds.htm", description: "Ohanapecosh Campground is tucked in the forest along the banks of the Ohanapecosh River at an elevation of 1,914 feet. It is located in the southeast corner of the park along State Route 123. Under the tall trees, the campsites are relatively private. Trails that begin at the campground lead to Silver Falls and the Grove of the Patriarchs. Stop by the Ohanapecosh Visitor Center for information and exhibits.", latitude: 46.73599667059602, longitude: -121.56684815883636},
  {name: "Opequon Junction Hiker-Biker Campsite", city: "Williamsport", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 90.9 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.51593300000001, longitude: -77.860112},
  {name: "Oregon Inlet Campground", city: "Nags Head", totalsites: "107", firstcome: "0", reservable: "107", fees: "28.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/251431", description: "Oregon Inlet Campground has 107 sites and is open year round. Camping on a barrier island provides a chance to take in the power and nature of this special place. Listen to the breaking ocean waves while staring up at the night sky filled with stars. Walk along the sandy beaches during the day and share stories with friends at night. RV hookups are available at select sites. Running water and flush toilets are throughout the campground.", latitude: 35.80027781, longitude: -75.545},
  {name: "Otter Creek", city: "Big Island", totalsites: "68", firstcome: "39", reservable: "29", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/blri/planyourvisit/peaks-otter-cg.htm", description: "As Otter Creek descends to the James River, the Blue Ridge Parkway dips to its lowest elevation, only 649 feet above sea level. This area's abundant water and warmer temperatures support plant and animal communities that are unlike those found at other areas on the Parkway. A short walk along the Trail of Trees or a hike along the clear, cool waters of Otter Creek will give ample proof that there is more to the Blue Ridge Parkway than mountain-top vistas.", latitude: 37.5756939454, longitude: -79.3374360711},
  {name: "Owens Creek Campground", city: "Sabillasville", totalsites: "50", firstcome: "0", reservable: "50", fees: "30.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/247762?q=Owens%20Creek%20Campground", description: "Reservations Required. No Walk-Ins. Owens Creek Campground offers family camping in a wooded setting. There are 50 sites, 3 are ADA accessible. Maximum trailer length is 22 feet. One vehicle is permitted per site. A picnic table and fire ring are provided at each site. There are 2 comfort stations and hot showers, but there are no hook-ups. Firewood is available, with a donation suggested. Use of alcohol is prohibited. Pets allowed and must leashed and tended to at all time.", latitude: 39.6596869874, longitude: -77.4866425571},
  {name: "Ozark Campground", city: "Jasper", totalsites: "31", firstcome: "31", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/camping.htm", description: "Ozark Campground is a first come, first serve campground near Jasper, AR. Ozark has 31 drive in and tent sites with no electrical amenities, but flush restrooms and water are available from March 15 through November 14.", latitude: 36.0640098001, longitude: -93.1598566994},
  {name: "Ozette Campground", city: "Port Angeles", totalsites: "15", firstcome: "15", reservable: "0", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/lake-ozette-area-brochure.htm", description: "Adjacent to Lake Ozette, this small campground is great for those that enjoy lakeside camping and water activities", latitude: 48.1532400873933, longitude: -124.667693189397},
  {name: "Paul Grist State Park", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.alapark.com/parks/paul-grist-state-park", description: "https://www.alapark.com/parks/paul-grist-state-park", latitude: 32.591452, longitude: -86.99976},
  {name: "Paw Paw Tunnel Campground", city: "f", totalsites: "10", firstcome: "0", reservable: "10", fees: "20.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/252971", description: "Mile 156 (Allegany County, MD) - There are 10 single campsites, each has a picnic table, fire ring and grill. Each site can accommodate eight people. If there is a larger group, reserve two sites next to each other. Portable toilets are available. Hamp pump water is available from April 15-November 1.", latitude: 39.54423610033746, longitude: -78.46148084682035},
  {name: "Peaks of Otter Campground", city: "Bedford", totalsites: "139", firstcome: "78", reservable: "61", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov//blri/planyourvisit/peaks-otter-cg.htm", description: "The campground is nestled at the base of two mountains in the rolling hills of Virginia. Nearby Abbott Lake is well-stocked with small mouth bass and bluegill. A stream winds through the campground, creating a tranquil backdrop for your camping trip. Hikers may choose to climb a mountain or stroll on a flat, lakeside trail. Wildlife such as deer and wild turkey are spotted daily at the Peaks of Otter Campground.", latitude: 37.443384745, longitude: -79.6049784725},
  {name: "Pebble Creek Campground", city: "Yellowstone National Park", totalsites: "27", firstcome: "0", reservable: "27", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yell/planyourvisit/pebblecreekcg.htm", description: "CLOSED IN 2023 for flood recovery work. Pebble Creek Campground—elevation 6,900 feet (2103 m)—lies against the dramatic backdrop of the Absaroka Mountains near the park's Northeast Entrance and offers a more isolated camping experience. Day hiking opportunities are available nearby. Soda Butte Creek offers fishing opportunities and there are outstanding wildlife viewing opportunities throughout Lamar Valley. This is a reservable campground run by the National Park Service.", latitude: 44.91698134898363, longitude: -110.11380838340915},
  {name: "Peltier Bridge", city: "Whiskeytown", totalsites: "9", firstcome: "0", reservable: "9", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/whis/planyourvisit/peltier-bridge.htm", description: "Sited along Clear Creek, the Peltier Bridge campground offers 9 sites and access to numerous trails. All sites offer a bear-proof storage locker, picnic table, lamp pole, fire pit with swing-arm BBQ grill, and shared access to two ADA-accessible vault toilets.", latitude: 40.5857833, longitude: -122.5518167},
  {name: "Pickerel Cove Campground", city: "Houghton", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/canoe-and-kayak.htm", description: "Pickerel Cove Campground is located in Pickerel Cove on the north shore of Isle Royale National Park. Canoe/kayak access from Pickerel Cove or via .1 mile portage from Lake Superior. Stay Limit: 2 nights from June 1 – Labor Day annually. Access: Canoe/kayak. No dock for boats.", latitude: 48.1245169558947, longitude: -88.65218294378656},
  {name: "Pigmans Ferry Hiker-Biker Campsite", city: "Hagerstown", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "", description: "Mile 169.1 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.53895800000001, longitude: -78.650428},
  {name: "Pine Bluff Individual Backpacking Sites", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Pine Bluff sites are on a sandy bluff above Lake Superior just off the North Country Trail. The five campsites are on a sandy open area covered in ferns with pine trees scattered throughout the area. Access the beach and Lake Superior from a trail on the bluff at the entrance to the campground.", latitude: 46.586272, longitude: -86.328552},
  {name: "Pine Springs Campground", city: "Salt Flat", totalsites: "35", firstcome: "0", reservable: "35", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/pine_springs_campground.htm", description: "The Pine Springs Campground has 20 tent and 13 RV sites available. All campsites are reservable through recreation.gov. Individual campsite cost is $20 per night, per site. There are 2 group sites for groups of 10-20 people. Campground amenities include drinking water and toilets (NO hookups, dump stations or showers). Only cooking stoves are permitted; charcoal, wood fires, and propane firepits are prohibited.", latitude: 31.895261730692052, longitude: -104.82634069250241},
  {name: "Pine Top Wilderness Campground", city: "f", totalsites: "8", firstcome: "0", reservable: "8", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/pt_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. Pine Top is the backcountry campground in closest proximity to the Bowl and offers excellent views of the park's highest peaks. Pine Top is an excellent choice for a single night backpack trip. It is 3.9 miles from Pine Springs Trailhead via the Tejas and Bush Mountain Trails. Elevation gain is 2,300 feet. Though secluded in trees, Pine Top is susceptible to high winds & lightning. Eight designated campsites are defined by tent pads.", latitude: 31.9185398240834, longitude: -104.845801986861},
  {name: "Pinecrest", city: "Ochopee", totalsites: "4", firstcome: "0", reservable: "4", fees: "30.00", showers: "None", toilets: "No Toilets", phone: "No", url: "", description: "This campground is for group camping only. Four sites available to accommodate eight tents at 15 people each.There is no water or restroom facilities. Access to the Pinecrest Campground is along a secondary gravel road.", latitude: 25.7615555478, longitude: -80.9180630747},
  {name: "Pinnacles Campground", city: "Paicines", totalsites: "134", firstcome: "5", reservable: "134", fees: "39.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "", description: "Pinnacles Campground is run by Pinnacles Recreation Company. The campground offers tent and group camping, tent cabins, and RV sites. Each tent and group site has a picnic table and fire ring. Most RV sites have electrical hookups and share community tables and barbecue pits. Some tent cabins have electricity. Each tent cabin sleeps up to four people. To make reservations please visit recreation.gov. For questions and concerns call (831) 200-1722.", latitude: 36.4898445958, longitude: -121.148968014},
  {name: "Piñon Flats Campground", city: "Mosca", totalsites: "91", firstcome: "0", reservable: "91", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grsa/planyourvisit/pinonflatscampground.htm", description: "Piñon Flats is a National Park Service campground located one mile north of the Visitor Center, open April through October. All sites are by reservation on recreation.gov. Individual sites can be reserved up to 6 months in advance, and group sites can be reserved up to 1 year in advance.", latitude: 37.7449368560859, longitude: -105.504703076291},
  {name: "Platte River Campground", city: "Honor", totalsites: "179", firstcome: "0", reservable: "179", fees: "31.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/slbe/planyourvisit/platterivercamp.htm", description: "Platte River Campground is open year-round and offers a wide variety of camping styles. You can find back-in and pull-through sites for RV's, including electrical hookups; but it also offers beautiful tent sites, walk-in sites for the slightly more adventurous, and group sites (hike-in, tents only) accommodating up to 25 people. There is even a nearby backcountry campground for those who enjoy a great backpacking experience, but don't have the time to head for the Manitou Islands.", latitude: 44.71551, longitude: -86.119114},
  {name: "Pleasant Camp", city: "Skagway", totalsites: "11", firstcome: "0", reservable: "11", fees: "54.60", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/klgo/planyourvisit/pleasant-camp.htm", description: "Pleasant Camp is located near the Taiya River 10.7mi (17.3km) from the trailhead. It is just after a relatively large elevation gain coming from Canyon City (hiking south to north) and often a \"pleasant\" spot to spend some time. Pleasant Camp is the trail's least used campground and offers hikers a tranquil evening. However, if you are hiking over the pass directly from Pleasant Camp, prepare for a very long, next day traversing the Golden Stairs. You must purchase a backcountry permit for this campground.", latitude: 59.6385221, longitude: -135.2937064},
  {name: "Plum Creek Campgrounds", city: "Fritch", totalsites: "45", firstcome: "45", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "There are four camping areas in this region of the recreation area. These campgrounds are located on the southwest side of the lake off FM 1913. This area is a favorite for hiking, horseback riding, birding, and wildlife watching. Devil's Canyon Trail is a good trail for beginner to experienced horseback riders. This area is covered with cottonwood trees. There is no drinking water or flush toilets; vault-evaporator toilets only. Picnic tables, shade shelters, and grills are available.", latitude: 35.516667, longitude: -102.78333},
  {name: "Point Supreme Campground", city: "f", totalsites: "25", firstcome: "0", reservable: "25", fees: "24.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cebr/planyourvisit/psc.htm", description: "Point Supreme Campground is surrounded by meadows of wildflowers in the summer! At 10,000 feet elevation, it is a comfortable place to camp during the hotter summer months. The Point Supreme Campground has 25 campsites and accommodates both tents and RVs. Camping is available from mid-June to mid-September. Note that the campground opening and closing dates may be vary & are subject to favorable weather.", latitude: 37.6103850583356, longitude: -112.830926692645},
  {name: "Pond Pitch Campsite", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "A rustic backcountry campsite overlooking the majestic Pond Pitch and wild East Branch of the Penobscot River.", latitude: 46.0633204470942, longitude: -68.7548784505108},
  {name: "Ponderosa", city: "Los Alamos", totalsites: "2", firstcome: "0", reservable: "2", fees: "35.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/band/planyourvisit/ponderosa-group-campground.htm", description: "Ponderosa group campground is open year round, with water available May - October only. Site is available by reservation only.", latitude: 35.8328556589, longitude: -106.356758549},
  {name: "Ponderosa Campground", city: "f", totalsites: "28", firstcome: "0", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cure/planyourvisit/camp_ponderosa.htm", description: "Ponderosa Campground is located at the northwest end of the Soap Creek Arm of Blue Mesa Reservoir. From U.S. Highway 50, follow CO Highway 92 1/2 mile past Blue Mesa Dam to Soap Creek Road. Proceed 7 miles north on the gravel Soap Creek Road. Tents and medium sized RVs can be accommodated at Ponderosa. All sites are first-come first-served. Elevation: 7880 feet Note: During dry conditions, the first 7 miles of Soap Creek Road are passable to trailers and RVs. Rain can make the Soap Creek Road impassable.", latitude: 38.5257080480139, longitude: -107.314899399111},
  {name: "Poplar Grove Youth Group Tent Campground", city: "f", totalsites: "3", firstcome: "0", reservable: "3", fees: "35.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/232503?q=Poplar%20Grove%20Youth%20Group%20Campground", description: "Reservations Required. No Walk-Ins. Poplar Grove is open to adult supervised organized youth groups whose members are under age 18. There are 3 sites within Poplar Grove. Each site accommodates up to 25 persons. Groups share pit toilets and a pavilion. Each site has picnic tables, grills and a fire circle.", latitude: 39.6558291260591, longitude: -77.47561993940099},
  {name: "Popple Campground - South Manitou Island", city: "Empire", totalsites: "7", firstcome: "7", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/slbe/planyourvisit/smipopplecamp.htm", description: "The Popple Campground is the furthest from the dock (about 3.5 miles), so you are likely to have fewer people camping there. The campground is close to the beach on the northern tip of the island. Bring water filtration equipment since there is no source of purified drinking water. There are 7 individual sites", latitude: 45.046024, longitude: -86.116205},
  {name: "Porcupine Bay Campground", city: "f", totalsites: "20", firstcome: "0", reservable: "20", fees: "f", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-porcupinebay.htm", description: "Porcupine Bay Campground and Boat Launch includes 20 campsites, seasonal flush facilities, and fish cleaning station, swim beach, and boat launch. The campground is tucked up into the shaded pines against the wall of the coulee, with beautiful views of the lake.", latitude: 47.8945931616, longitude: -118.175135367},
  {name: "Porcupine Flat Campground", city: "f", totalsites: "52", firstcome: "0", reservable: "52", fees: "24.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yose/planyourvisit/porcupinecamp.htm", description: "Porcupine Flat Campground is located off the Tioga Road about 30 minutes west of Tuolumne Meadows and over an hour from Yosemite Valley. RVs and Trailers are not recommended for the narrow roads in this more primitive campground. At an elevation of 8,100 feet (2,500 m) elevation, the campground is near Porcupine Creek, which is the only water source (must be filtered, treated, or boiled). The Porcupine Creek Trailhead is nearby. There are no visitor services close to the campground.", latitude: 37.8082397641, longitude: -119.565691823},
  {name: "Santa Cruz Island Scorpion Canyon Campground", city: "Santa Cruz Island", totalsites: "31", firstcome: "0", reservable: "31", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/santa-cruz-scorpion/r/campgroundDetails.do?contractCode=NRSO&parkId=70980", description: "Primitive camping is available (25 individual sites at $15 per night-per site and 6 group sites at $40 per night-per site; reservations required). Picnic table, food storage box, potable water, and pit toilet are provided. Distance from landing to campground is one-half mile.", latitude: 34.0480064918, longitude: -119.56112541},
  {name: "Potato Patch Individual Backpacking Sites", city: "f", totalsites: "3", firstcome: "0", reservable: "3", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Potato Patch Campground is inland from the North Country Trail. Follow a trail through an open field with the sounds of a waterfall at your back. The fire ring is at the entrance to the campground. Follow the trail to the right to reach each of the three sites. Within the campground, the view of Grand Island and Lake Superior is blocked during the summer by the birch and maple leaves. One shared fire ring and pit toilet. Visitors must bring water. NO ACCESS TO WATER.", latitude: 46.497249, longitude: -86.530046},
  {name: "Potomac Forks Hiker-Biker Campsite", city: "Hagerstown", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "", description: "Mile 164.8 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.53094000000004, longitude: -78.58897499999999},
  {name: "Potwisha Campground", city: "f", totalsites: "42", firstcome: "0", reservable: "40", fees: "28.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/potwisha.htm", description: "Except when weather or safety conditions require a closure, Potwisha Campground is open year-round with a four-month advance booking window. The campground sits at 2,100 ft (640 m) elevation along the Middle Fork of the Kaweah River under an open stand of oaks. Hot and dry weather in the foothills often require fire restrictions in the summer. In the winter, the campground is usually snow-free.", latitude: 36.51650783772912, longitude: -118.80083536436518},
  {name: "Prairie Creek", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.recreation.gov/camping/campgrounds/232679", description: "Alabama River Lakes Corp of Engineers", latitude: 32.3336736, longitude: -86.7701881},
  {name: "Primitive campsites at Cathedral Campground", city: "Torrey", totalsites: "6", firstcome: "6", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/care/planyourvisit/primitive-campgrounds.htm", description: "It is located approximately halfway on the Cathedral Valley Loop Road. About 36 miles (57.9 km) from the visitor center, this primitive, no-fee campground has 6 sites, each with a picnic table and fire grate. There is a pit toilet, but no water available. The campground is open year-round; however, visitors should check road conditions with the Capitol Reef Visitor Center prior to planning an overnight stay. The campground is at ~7,000 feet (2,133 m) in elevation, No reservations; first-come, first-served.", latitude: 38.4746, longitude: -111.3673},
  {name: "Primitive campsites at Cedar Mesa Campground", city: "Torrey", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/care/planyourvisit/primitive-campgrounds.htm", description: "It is located approximately 23 miles (37.0 km) south of Utah State Highway 24 on the Notom-Bullfrog Road and is at 5,500 feet (1,676 m) in elevation. This primitive, no-fee campground has five sites, each with a picnic table and fire grate. There is also a pit toilet, but no water is available. The campground is open year-round, but visitors should check with the Capitol Reef Visitor Center for road conditions prior to planning an overnight stay. No reservations; first-come, first-served.", latitude: 38.0073, longitude: -111.0846},
  {name: "Prince William Forest RV Campground", city: "Triangle", totalsites: "72", firstcome: "0", reservable: "0", fees: "65.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "http://www.princewilliamforestrvcampground.com/", description: "The Prince William Forest Park RV Campground is a concessionaire-operated campground with full hook-ups. The campground boasts pull through sites, a pool and laundry facility. It is Virginia's closest RV camping to Washington, D.C. There are several tents-only sites. Open year-round. It is currently closed due to infrastructure repair work and will hopefully re-open in June 2023.", latitude: 38.603929, longitude: -77.350905},
  {name: "Pulltite Campground", city: "Eminence", totalsites: "55", firstcome: "20", reservable: "34", fees: "20.00", showers: "Free - Seasonal", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/ozar/planyourvisit/pulltite-campground.htm", description: "Pulltite Campground is located north of Round Spring, at the end of EE Highway off State Highway 19 in Shannon County.", latitude: 37.3394667448, longitude: -91.4653730395},
  {name: "Purple Point Campground", city: "f", totalsites: "6", firstcome: "0", reservable: "6", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/10101344", description: "Located at the head of Lake Chelan, Purple Point Campground is a ten minute walk from the boat dock at Stehekin Landing. All sites are walk-in sites for small to medium-sized tents. First-come, first-served during the winter; summer reservations can be made up to 6 months in advance through www.recreation.gov or by calling 1-877-444-6777. Stehekin is a remote community with limited facilities and is only accessible by foot, boat, or plane.", latitude: 48.3131, longitude: -120.6581},
  {name: "Purslane Run Hiker-Biker Campsite", city: "Hagerstown", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 156.9 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.535244000000006, longitude: -78.46414900000002},
  {name: "Quartz Creek", city: "West Glacier", totalsites: "7", firstcome: "7", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "", description: "Quartz Creek campground is the smallest campground in Glacier National Park and remains in primitive status all season. It is located on the west side of the park in the North Fork area and is accessed by the Inside North Fork Road, a rough, dusty, dirt road with many blind curves and few pullouts. Trailers are not permitted. Tent campers will enjoy a wilderness experience at the campground.", latitude: 48.7226099101896, longitude: -114.2242732955342},
  {name: "Queets Campground", city: "Port Angeles", totalsites: "20", firstcome: "20", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/queets-area-brochure.htm", description: "Relax in this secluded campground near the Queets River. This campground is only accessible from the Upper Queets River Road due to a past mudslide", latitude: 47.6259946274311, longitude: -124.017776901915},
  {name: "Rainbow Park Campground", city: "Jensen", totalsites: "4", firstcome: "4", reservable: "0", fees: "6.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/dino/planyourvisit/rainbowparkcampground.htm", description: "Rainbow Park Campground is 28 miles (45 km) from the Quarry Visitor Center in the Utah portion of the monument. It is located on a dirt road that is impassable when wet. The campground sits beside the Green River near the Rainbow Park Boat Ramp at the head of Split Mountain Canyon. Rainbow Park Campground is open year-round, but there is no winter maintenance on the unpaved road.", latitude: 40.4956930727, longitude: -109.174431587},
  {name: "Red Creek Campground", city: "f", totalsites: "2", firstcome: "1", reservable: "1", fees: "16.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/cure/planyourvisit/camp_red_creek.htm", description: "Red Creek Campground is located 19 miles west of Gunnison just north of U.S. Highway 50. There are 2 sites - 1 individual site and 1 group site. The individual site is first-come first-served, and the group site is reservation only. RVs longer than 22 feet are not recommended due to a lack of space to turn around. Elevation: 7542 feet", latitude: 38.4793732438395, longitude: -107.231989784489},
  {name: "Riley Creek", city: "Denali Park", totalsites: "142", firstcome: "20", reservable: "127", fees: "0.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/dena/planyourvisit/campground-riley.htm", description: "Riley Creek is a fairly wooded campground, with some screening between sites whenever possible. Most of the campground sites are open to either tent campers or vehicles / RVs. The proximity of Highway 3 means there is occasional traffic noise, but also means campers have all the conveniences and amenities of the park entrance area, and businesses outside the park, close at hand. Trails leading from the campground connect to the Denali Visitor Center, which is the central hub of trails in the entrance area.", latitude: 63.733359835872776, longitude: -148.8940189729629},
  {name: "Rio Grande Village Campground", city: "f", totalsites: "93", firstcome: "0", reservable: "93", fees: "16.00", showers: "Coin-Operated - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/bibe/planyourvisit/rgv_campground.htm", description: "Set in a grove of cottonwoods and acacia trees, the Rio Grande Village Campground is located near the Rio Grande. Elevation: 1,850 feet. Paved roads connect each campsite, and grassy areas separate each site. Flush toilets, running water, picnic tables, grills, and some overhead shelters. Dump station nearby. Campers enjoy birdwatching, hiking, exploring. A camp store with showers and a park visitor center are nearby.", latitude: 29.1801945038, longitude: -102.955634488},
  {name: "Rio Grande Village RV", city: "f", totalsites: "25", firstcome: "0", reservable: "25", fees: "36.00", showers: "Coin-Operated - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/bibe/planyourvisit/rgv_hookups.htm", description: "Open, paved lot with grassy, tree-lined edges. Adjacent to the Rio Grande Village camp store. This campground, operated by Forever Resorts, has the only full hook-ups in the park. 25-site RV park (full hook-ups—water, electrical, and 3-inch sewer connection). All sites are back-in only. Tents are NOT permitted. Operated by the park's concessioner, Forever Resorts. Periodically, a few sites may not be available for a 40' or longer RVs due to the size of the parking lot and orientation of the spaces.", latitude: 29.1831689600897, longitude: -102.963812137708},
  {name: "Rising Sun", city: "West Glacier", totalsites: "84", firstcome: "84", reservable: "0", fees: "20.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Rising Sun Campground is located just west of St. Mary and halfway along St. Mary Lake. Campers at Rising Sun will enjoy beautiful sunrises in the morning with Red Eagle Mountain as a backdrop, and the campground serves as a convenient base camp to many day hikes located east of Logan Pass. Some sites are open, allowing for cool breezes throughout the day, while others are located among trees, accommodating those seeking some shade and privacy.", latitude: 48.6945321731, longitude: -113.522684368},
  {name: "Rivers Bend Group Campground", city: "Walpack Township", totalsites: "5", firstcome: "0", reservable: "5", fees: "70.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/dewa/planyourvisit/group-camping.htm", description: "Five (5) Primitive group campsites for parties of up to 15 people per site, in an open area with some trees next to and above the river; drinking water available; minimum of five people required to reserve site. Parking available for up to six vehicles per site.", latitude: 41.0961181004, longitude: -74.9666324998},
  {name: "Rock Creek Campground", city: "f", totalsites: "11", firstcome: "0", reservable: "11", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/obed/planyourvisit/campgrounds.htm", description: "The Rock Creek Campground, located adjacent to the Nemo area of the Obed Wild & Scenic River, offers 11 spots for campers. A $10 per night fee is charged and reservations are required. You may obtain your reservation by visiting www.recreation.gov and searching for \"Rock Creek Campground - TN\". Reservations are site-specific. The campground has grills and primitive toilet facilities, but no running water.", latitude: 36.06963726622717, longitude: -84.66287612915039},
  {name: "Rock Creek Campground", city: "f", totalsites: "100", firstcome: "99", reservable: "1", fees: "14.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "Located along Rock Creek and near Veterans Lake, this wooded campground is open year round, though some sites within it close seasonally. Built with small travel trailers in mind, this campground is good for primitive camping. No hook ups are available.", latitude: 34.4980236115, longitude: -96.9861506941},
  {name: "Rock Harbor Campground", city: "Houghton", totalsites: "23", firstcome: "20", reservable: "3", fees: "0.00", showers: "Hot - Seasonal", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/rock-harbor.htm", description: "Rock Harbor Campground is located in Rock Harbor on the northeast end of Isle Royale, off of the Rock Harbor and Tobin Harbor Trails. Additional fees for boats to stay overnight at marina. Stay Limit: 1 night from June 1 – September 15 annually. Shelters: 9 Depth at dock (normal conditions): 3’-12’. Access: Foot/canoe/kayak/private boat", latitude: 48.14543517120799, longitude: -88.48932435367544},
  {name: "Rocky Knob Campground", city: "Floyd", totalsites: "106", firstcome: "49", reservable: "57", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/blri/planyourvisit/rocky-knob-cg.htm", description: "Located in the Rocky Knob Recreation area, surrounded by 4,000 acres of forest, the campground is ideal for a traditional camping experience. The site offers easy access to the Rock Castle Gorge Trail, a 10.8-mile loop trail ranging in elevation from 1,700 to 3,572 feet. Rock Castle Gorge, a 1,500-foot ravine carved by the waters of Rock Castle Creek, is the focal point of this trail, offering hikers breathtaking scenery.", latitude: 36.8129880454, longitude: -80.349391372},
  {name: "Rocky Springs Campground, Milepost 54.8", city: "Hermanville", totalsites: "22", firstcome: "22", reservable: "0", fees: "0.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/natr/planyourvisit/camping.htm", description: "Rocky Springs Campground at milepost 54.8 on the southern end of the Parkway, includes a 22-site campground, picnic tables, restrooms, a section of the Old Natchez Trace, self-guiding walking trails through the old town site. Campground sites are on a first come first served basis, no reservations. Pull-through and back-in sites are available. Campgrounds are typically the busiest during the spring and fall, especially during holiday weekends. Potable water may be an issue. Check \"What's Open What's Closed\"", latitude: 32.08743114913807, longitude: -90.79884007702435},
  {name: "Rosita Flats", city: "f", totalsites: "75", firstcome: "75", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "This campground consists of ispersed camping along the Canadian River in one of the park's ORV-use areas. ORV operators must have state of Texas Off-Highway Vehicle decal and wear DOT-approved safety gear.", latitude: 35.3647, longitude: -101.24451},
  {name: "Rough Canyon Campground", city: "f", totalsites: "4", firstcome: "4", reservable: "0", fees: "6.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/amis/planyourvisit/rough-canyon-campground.htm", description: "Rough Canyon Campground is generally open year-round and is in close proximity to a boat ramp. There may be a camp host on duty in the winter, and a Ranger Station is staffed intermittently. This campground offers 4 primitive sites, each with a covered picnic table and a BBQ grill. The sites are suitable for tents or RVs. No drinking water or hookups are available. Comfort station and running water available nearby at the Ranger Station.", latitude: 29.573599, longitude: -100.97746},
  {name: "Round Spring Campground", city: "Eminence", totalsites: "55", firstcome: "65", reservable: "65", fees: "30.00", showers: "Free - Seasonal", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/ozar/planyourvisit/round-spring-campground.htm", description: "Round Spring Campground is located near Round Spring, approximately 12 miles north of Eminence on State Highway 19.", latitude: 37.2795173, longitude: -91.4068743},
  {name: "Village Campground - North Manitou Island", city: "Empire", totalsites: "8", firstcome: "8", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/slbe/planyourvisit/nmicamping.htm", description: "The small Village Campground contains eight first-come, first-served designated campsites, two fire rings and one outhouse. There is a limit of two tents and four people per site. Fires are permitted in the community fire rings at the Village Campground.", latitude: 45.1281213, longitude: -85.9775979},
  {name: "Roundup Group Horse Camp", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "40.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/thro/planyourvisit/roundup-group-horse-campground.htm", description: "Roundup is the park's only camping facility in which horses are permitted. This private campsite is located 12 miles from Medora, ND in the park's South Unit. It is reserved by one group at a time; space is not be shared among different parties. Reservations for Roundup begin each season on the first business day in March at 8:00 am MST. See the RESERVATIONS section below. Roundup can accommodate up to 20 people and 20 horses or 30 people if camping without horses.", latitude: 47.0069444, longitude: -103.5055556},
  {name: "Rush Campground", city: "Yellville", totalsites: "12", firstcome: "12", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/camping.htm", description: "Rush Campground is a first come, first serve campground in the lower district of the park. Rush has 12 tent only sites with no electrical amenities and no flush restrooms. Drinking water is available when fees are collected March 15 - November 14. It is free to camp at Rush in the winter. All sites are limited to 6 people per site and each site is $16 per night. This is a pack in/pack out facility, trash collection is not available.", latitude: 36.1227472999, longitude: -92.5504511998},
  {name: "Ryan Campground", city: "Twentynine Palms", totalsites: "32", firstcome: "0", reservable: "31", fees: "20.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/ryan-campground.htm", description: "Ryan Campground has 31 campsites. All campsites are reservation only. The campground is centrally located in the park and is adjacent to the California Riding and Hiking Trail. There are 4 designated equestrian sites and reservations are required. For more equestrian campsites, see Black Rock Campground. There is no water available at Ryan Campground. There are 3 bicycle sites available at $5 per night, with no more than 3 tents and 3 people per campsite.", latitude: 33.9830583394153, longitude: -116.154390354172},
  {name: "Saddlehorn Campground", city: "Fruita", totalsites: "80", firstcome: "29", reservable: "51", fees: "22.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/colm/planyourvisit/saddlehorn-campground.htm", description: "Saddlehorn Campground is 4 miles from the west (Fruita) entrance. It is located in an area of pinyon pine and Utah juniper trees, and is within walking distance to the visitor center. Sites cost $22 a night plus entry fee to Monument. Loop A is open year round. Reservations can be made for A and B loops from mid-March through October on recreation.gov. There are flush toilets, but no hookups or dump station. Each site has a picnic table and charcoal grill, no wood fires allowed. RV length limit is 40 feet.", latitude: 39.10560328979322, longitude: -108.73257527355514},
  {name: "Sage Creek Campground", city: "Wall", totalsites: "22", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/thingstodo/backcountry-camping-badl.htm", description: "Sage Creek Campground is a primitive campground. There is no charge to stay here and camping is available on a first come first serve basis. The campground rarely fills to capacity.", latitude: 43.893146, longitude: -102.414254},
  {name: "Saline Valley Campground (Primitive)", city: "f", totalsites: "15", firstcome: "10", reservable: "0", fees: "0.00", showers: nil, toilets: nil, phone: "", url: "https://www.nps.gov/deva/planyourvisit/saline-valley-campground-primitive.htm", description: "This is a clothing-optional hot spring area approximately 35 miles from the nearest paved road. A high-clearance vehicle and all-terrain tires are necessary to access this campground and road/weather conditions may necessitate 4-wheel drive. This campground is located at 1,375ft, but requires navigating either North Pass at 7,300ft or South Pass at 5,997ft and may be inaccessible due to winter conditions.", latitude: 36.8056, longitude: -117.7738},
  {name: "San Miguel Island Campground", city: "San Miguel Island", totalsites: "9", firstcome: "0", reservable: "9", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/san-miguel-island/r/campgroundDetails.do?contractCode=NRSO&parkId=70983", description: "Primitive camping is available (nine sites; $15 per night per site; reservations required). Wind shelter, picnic table, food storage box, and pit toilet are provided. No water is available. Distance from landing to campground is one mile up a steep canyon (400 ft. climb).", latitude: 34.0407937961, longitude: -120.349462009},
  {name: "San Pedro Campground", city: "f", totalsites: "41", firstcome: "40", reservable: "1", fees: "6.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/amis/planyourvisit/san-pedro-campground.htm", description: "San Pedro Campground is open year-round and rarely fills to capacity. Of Amistad's campgrounds, it is the best suited for large RVs. This campground is accessed via a 1¼ mile graded, gravel road and offers 34 sites suitable for tents or RVs. There are also 8 walk-in (tent only) sites. Each site has a covered picnic table and a BBQ grill. Ground fires are prohibited. Vault toilets are available. No drinking water or hook-ups.", latitude: 29.46768229853263, longitude: -100.95197435103285},
  {name: "Sanctuary River", city: "Denali Park", totalsites: "7", firstcome: "7", reservable: "0", fees: "21.75", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/dena/planyourvisit/campground-sanctuary.htm", description: "Open in summer only (May 20 to mid-September). Sanctuary River is a heavily wooded campground, with screening between most sites. The immediate area is brushy, but nearby mountains offer chances to hike directly from the campground and gain elevation, with views of Denali to the south possible on clear days, after some strenuous up-hill hiking. Sanctuary is tent-only—you will not find any RVs or other vehicles in this campground. It is accessible only by camper bus.", latitude: 63.72255876395471, longitude: -149.47374843040546},
  {name: "Sandbank Stream Campground", city: "Patten", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "Located approximately 10 miles from the intersection of Route 11 and Swift Brook Rd. Contains 3 sites.", latitude: 45.862757263787714, longitude: -68.67453987014596},
  {name: "Sandy Hook Camp Ground", city: "f", totalsites: "20", firstcome: "0", reservable: "20", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/gate/planyourvisit/camp-sandy-hook.htm", description: "20 tent campsites are available at Sandy Hook for seasonal camping. The season typically runs from mid April through late October.", latitude: 40.4500495, longitude: -73.9950964},
  {name: "Sanford Yake Campground", city: "f", totalsites: "51", firstcome: "51", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://www.nps.gov/lamr/planyourvisit/camping.htm", description: "A caprock campground, overlooking Lake Meredith. 10 RV sites with electric and water hookups available. Reservations can be made by calling 806-865-3131. Picnic tables, shade shelters, and grills are available. Restrooms with potable water, showers, and flush toilets are available. Pull-through parking available.", latitude: 35.413937, longitude: -101.333084},
  {name: "Santa Barbara Island Campground", city: "Santa Barbara Island", totalsites: "10", firstcome: "0", reservable: "10", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/santa-barbara-island/r/campgroundDetails.do?contractCode=NRSO&parkId=70982", description: "Primitive camping is available (seven sites; $15 per night per site; reservations required). Picnic table, food storage box, and pit toilet are provided. No water is available. Distance from landing to campground is one-quarter mile and includes a steep climb.", latitude: 33.4800017206, longitude: -119.029461163},
  {name: "Santa Cruz Island Del Norte Backcountry Campground", city: "Santa Cruz Island", totalsites: "4", firstcome: "0", reservable: "4", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/santa-cruz-del-norte-backcountry/r/campgroundDetails.do?contractCode=NRSO&parkId=70981", description: "The Santa Cruz Island Del Norte Backcountry Campground is currently the only backcountry campground on Santa Cruz Island. The hike to the campground is 3.5 miles from Prisoners Harbor and 12 miles from Scorpion Anchorage. It has four primitive campsites (four persons per site) and users must camp within these designated sites.Reservations are required ($15 per night-per site). A picnic table, food storage box, and pit-style toilet are provided (campers must bring their own toilet paper).", latitude: 34.0110749692, longitude: -119.66034054},
  {name: "Santa Rosa Island Backcountry Beach Camping", city: "Santa Rosa Island", totalsites: "30", firstcome: "0", reservable: "30", fees: "10.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.recreation.gov/camping/santa-rosa-island-backcountry-beach-camping/r/campgroundDetails.do?contractCode=NRSO&parkId=1", description: "Backcountry camping on Santa Rosa Island is currently limited to certain beaches between August 15th and December 31st. Hiking is along the beach and rugged, unsigned (and sometimes unmaintained) dirt roads. The closest beach for camping is 8 miles from the boat/plane drop-off location. Reservations are required ($10 per night-per site). No services are provided.This is primitive, dispersed, backcountry camping. Visitors must backpack to all locations and carry all of their own gear, food and water.", latitude: 33.885295509365314, longitude: -120.11494221408148},
  {name: "Santa Rosa Island Campground", city: "Santa Rosa Island", totalsites: "15", firstcome: "0", reservable: "15", fees: "15.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.recreation.gov/camping/santa-rosa-island/r/campgroundDetails.do?contractCode=NRSO&parkId=70979", description: "Primitive camping is available (15 sites; $15 per night per site; reservations required). Wind shelter, picnic table, food storage box, toilet, and potable water are provided. Distance from landing to campground is 1.5 miles from the pier and .25 miles from the airstrip.", latitude: 33.9918639133255, longitude: -120.047413236541},
  {name: "Savage River", city: "Denali Park", totalsites: "32", firstcome: "0", reservable: "33", fees: "45.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/dena/planyourvisit/campground-savage.htm", description: "Open in summer only (May 20 to mid-September). Savage River campground is located at Mile 13 on the Denali Park Road. It sits in a spruce forest, with moderate screening between sites. On clear days, Denali can be seen from a short walk below the campground.", latitude: 63.7153041825, longitude: -149.261655331},
  {name: "Schoodic Woods Campground", city: "Winter Harbor", totalsites: "89", firstcome: "0", reservable: "89", fees: "22.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/acad/planyourvisit/camping.htm", description: "Schoodic Woods Campground is the newest campground in the park and located 1.5 miles (2.5 km) southeast of Winter Harbor on the Schoodic Peninsula. It is approximately 60-70 minutes from Bar Harbor and other areas of Mount Desert Island.", latitude: 44.3800630004, longitude: -68.0665938},
  {name: "Sea Camp Campground", city: "f", totalsites: "18", firstcome: "0", reservable: "16", fees: "22.00", showers: "Cold - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/cuis/planyourvisit/seacamp.htm", description: "Sea Camp Campground is located a half mile from the Sea Camp dock. All sites are walk-to, tent sites and campers are responsible for transporting all gear. Carts can be used. There are flush toilets, drinking water, cold showers, and a dish washing sink. Each site has a fire ring with grill, picnic table and food storage. It is common for this campground to fill up. Permits are $22 a night for individual sites good for up to 6 people. Group sites are $40 a night good for up to 20 people.", latitude: 30.7636395998859, longitude: -81.4637252995094},
  {name: "Seawall Campground", city: "Southwest Harbor", totalsites: "202", firstcome: "0", reservable: "202", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/acad/planyourvisit/seawall-campground.htm", description: "Seawall Campground is located on the west side of Mount Desert Island about 4 miles (6 km) south of Southwest Harbor. The campground is approximately 18 miles from Bar Harbor. All sites at Seawall are within a short walking distance of the coastline.", latitude: 44.2415646998274, longitude: -68.3051438998646},
  {name: "Sentinel Campground", city: "Kings Canyon National Park", totalsites: "82", firstcome: "0", reservable: "82", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/sentinel.htm", description: "Not currently taking reservations for summer 2023, due to winter storm damage. Typically opens end of April with reservations beginning end of December, on a four-month rolling basis. This campground is located on Highway 180, is next to Cedar Grove Visitor Center, and is 0.25 miles (400 m) from Cedar Grove Village. The campground is situated in the canyon along the South Fork of the Kings River under open stands of evergreen trees. Services at Cedar Grove Village include food and showers.", latitude: 36.79032772286891, longitude: -118.67203619119141},
  {name: "Sevenmile Creek Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: nil, toilets: nil, phone: "", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Sevenmile Creek Group Campground is on the east side of Sevenmile Creek. The North Country Trail guides you over a foot bridge, through the woods and opens up into the group site. It is a wide-open area with access to Lake Superior. One shared fire ring and NO toilet. Water source is Sevenmile Creek and Lake Superior. Lake Superior access for kayakers.", latitude: 46.61946398113322, longitude: -86.25810096766202},
  {name: "Sevenmile Creek Individual Backpacking Sites", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Sevenmile Creek Campground is on the west bank of Sevenmile Creek. The North Country Trail wanders through the campground and past the communal fire ring. The sites are separated by trees, which offer a bit of privacy. Three of the five sites are along the creek. Lake Superior access is on the east side of the creek through the Sevenmile Creek Group Campground. NO toilet. Water source is Sevenmile Creek and Lake Superior. Lake Superior access for kayakers.", latitude: 46.619268, longitude: -86.258742},
  {name: "Sheep Camp", city: "Skagway", totalsites: "27", firstcome: "0", reservable: "27", fees: "54.60", showers: "None", toilets: "Composting Toilets - year round", phone: "No", url: "https://www.nps.gov/klgo/planyourvisit/sheep-camp.htm", description: "Sheep Camp is the last campground on the U.S. side of the trail before (at 13mi, 20.9km). As a result it is a popular campsite in the peak summer months and can be fully booked. Sheep Camp is the location of the National Park Service backcountry ranger station. Each evening a ranger will do a presentation in the campground providing an update on current trail conditions, tips for the trail, and some history of the area. You must purchase a permit to stay at this campground at the Trail Center in Skagway.", latitude: 59.6590711, longitude: -135.2678594},
  {name: "Sheep Camp", city: "Whiskeytown", totalsites: "4", firstcome: "0", reservable: "4", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "Yes - year round", url: "https://www.recreation.gov/camping/sheep-camp-primitive-campground/r/campgroundDetails.do?contractCode=NRSO&parkId=156990", description: "Sheep Camp is nestled in the mixed oak/pine/fir woodlands with nearby access to numerous trails and is a great launching point for treks up the 6,199' Shasta Bally, the highest point in Whiskeytown National Recreation Area. This campsite is closed during the winter. All sites are in partial shade and feature a bear-proof storage locker, picnic table, lamp pole, fire pit with swing-arm BBQ grill, with a shared ADA accessible vault toilet. Each site has a maximum occupancy of six people and two vehicles.", latitude: 40.5998762241, longitude: -122.594808442},
  {name: "Sheep Creek Campground", city: "Kings Canyon National Park", totalsites: "111", firstcome: "0", reservable: "111", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/sheep_creek.htm", description: "Not currently taking reservations for summer 2023, due to winter storm damage. Typically opens end of May with reservations beginning end of April, on a one-month rolling basis. Sheep Creek Campground is located on Highway 180, 0.25 miles (400 m) from Cedar Grove Village. The campground is situated on the floor of the canyon beside the confluence of the South Fork of the Kings River and Sheep Creek. Services can be found in Cedar Grove Village.", latitude: 36.792137062471234, longitude: -118.68059669181162},
  {name: "White River Campground", city: "Ashford", totalsites: "88", firstcome: "88", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/mora/planyourvisit/campgrounds.htm", description: "Located on the eastern side of the park below Sunrise, White River campground takes its name from the river flowing beside it. Tucked into a steep canyon, curves can be tight. When open for the summer, it is always first-come, first-serve. The Wonderland trail runs through the campground on its way up to Sunrise or Summerland.", latitude: 46.9024098268932, longitude: -121.641814850334},
  {name: "Sheep Pass Group Campground", city: "Twentynine Palms", totalsites: "6", firstcome: "0", reservable: "6", fees: "50.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/sheep-pass-group-campground.htm", description: "Sheep Pass Group Campground has 6 group campsites and is centrally located within Joshua Tree National Park and is easily accessible to hiking trails and rock climbing routes. All campsites are by reservation only. It is one of three group campgrounds in the park. Towering rock formations and uniquely-shaped Joshua trees surround the facility. There is no water available in the campground.", latitude: 34.0000909432, longitude: -116.120500649},
  {name: "Shumard Canyon Wilderness Campground", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/sc_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. If you enjoy arid Chihuahuan desert or you find yourself fascinated by the geology of the Guadalupes, consider a backpack trip to this remote location. Though it is 9.0 miles from Pine Springs Trailhead, the elevation gain is much less significant than many of the other trails—though it's by no means flat. Do make sure you are prepared for sun, wind, and weather exposure. The trail is not protected by trees along the way.", latitude: 31.8887274040177, longitude: -104.887056599334},
  {name: "Signal Mountain Campground", city: "Moran", totalsites: "81", firstcome: "0", reservable: "81", fees: "54.00", showers: "Coin-Operated - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/grte/planyourvisit/signalmountaincg.htm", description: "This campground is in an open lodgepole pine forest near the Signal Mountain Lodge. Some sites lie just above Jackson Lake, and other sites are a short walk from the lake. Enjoy spectacular views of Mount Moran and the northern Teton Range. The campground accepts both tents and smaller RVs (up to 30 feet total length). The developed area offers a wide variety of services and amenities including lodging, restaurants, showers, laundry, and marina. Maximum stay is 14 nights.", latitude: 43.8414780508, longitude: -110.613486984},
  {name: "Siskiwit Bay Campground", city: "Houghton", totalsites: "9", firstcome: "6", reservable: "3", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Siskiwit Bay Campground is located in Siskiwit Bay on the southwest end of Isle Royale National Park, off of the Island Mine and Feldtmann Ridge Trails. Boats can overnight at dock. Stay Limit: 3 nights from June 1 to Labor Day annually. Shelters: 2 Depth at dock (normal conditions): 2’-6’. Access: Foot/canoe/kayak/private boat", latitude: 47.890444970478676, longitude: -88.99876148310005},
  {name: "Sky Campground", city: "Point Reyes Station", totalsites: "12", firstcome: "1", reservable: "11", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/pore/planyourvisit/sky-campground.htm", description: "Sky Campground is located on the western side of Mount Wittenberg at an elevation of 1025 feet. In clear weather, it has a sweeping view of Point Reyes, Drakes Bay, and the Pacific Ocean. It is a steep 4-mile hike down to the beach. One may access Sky Campground by foot, bicycle, or horse; not by boat, auto, or RV. The easiest and shortest approach is a 1.4-mile moderate uphill hike or bicycle ride from the Sky Trailhead (elevation 680 feet) via Sky Trail. There are 11 regular sites and one group site.", latitude: 38.039915109550705, longitude: -122.82882744007327},
  {name: "Slough Creek Campground", city: "Yellowstone National Park", totalsites: "16", firstcome: "0", reservable: "16", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yell/planyourvisit/sloughcreekcg.htm", description: "Near some of the best wildlife watching opportunities in the park. Located at the end of a two-mile dirt road, this campground is best suited for tents and small RVs. There are plenty of hiking and fishing opportunities in the area, including the Slough Creek Trail which begins nearby. Nighttime offers a quiet, unimpeded view of the stars and the possibility of hearing wolves howl. Elevation: 6,250 ft (1905 m). This is a reservable campground run by the National Park Service.", latitude: 44.94884664880159, longitude: -110.30687918305057},
  {name: "Smokemont Campground", city: "Cherokee", totalsites: "142", firstcome: "0", reservable: "142", fees: "30.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "", description: "Smokemont Campground—elevation 2,198 feet (~670 m)—is the closest frontcountry campground to Oconaluftee Visitor Center in the South District of the park. Whether blanketed in bright spring wildflowers or vivid fall colors, the scenery at Smokemont never disappoints. Characterized by mild winters and hot, humid summers, Smokemont Campground offers an unforgettable outdoor experience with campsites for both tents and RVs.", latitude: 35.55645004606256, longitude: -83.31162547281389},
  {name: "Smokemont Group Campground", city: "Gatlinburg", totalsites: "0", firstcome: "0", reservable: "3", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "Mountain ranges and pristine streams and rivers are the backdrop for your camping adventure in Smokemont. Whether blanketed in spring wildflowers or vivid fall colors, the scenery at Smokemont never disappoints. At 2,198 feet (~670 m), Smokemont provides a moderate climate, characterized by mild winters and hot, humid summers. Smokemont Campground offers an unforgettable outdoor experience with campsites for both tents and RVs.", latitude: 35.55980784571487, longitude: -83.31280157335709},
  {name: "Smoky Mountain Campground", city: "Almo", totalsites: "38", firstcome: "38", reservable: "38", fees: "28.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/ciro/planyourvisit/smoky-mountain-campground.htm", description: "There are 38 campsites available, 9 of which are pull-through. Sites 11 and 24 are designated ADA accessible. A separate loop with 6 sites accommodates equestrian campers. - water service May-September - 30 amp electrical hook-ups - fire-ring/ground grill - picnic table - partial shade There are flush toilets and showers open May-September and a vault toilet open year-round at the equestrian trail head. An RV dump station is provided near the campground entrance.", latitude: 42.0755503036454, longitude: -113.651634013004},
  {name: "Snag Cove Campground", city: "Kettle Falls", totalsites: "9", firstcome: "0", reservable: "9", fees: "23.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-snagcove.htm", description: "Snag Cove is a small, fairly isolated campground located on the western side of Lake Roosevelt. Eight out of the nine sites are on the lake shore and all sites are open to tents and RVs. A boat launch with trailer parking and courtesy dock frames one side of the campground. Because of its size and popularity, Snag Cove often fills in the summer.", latitude: 48.7337074263939, longitude: -118.060418094258},
  {name: "Snake Creek Primitive Campgrounds", city: "f", totalsites: "12", firstcome: "12", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grba/planyourvisit/campgrounds.htm", description: "Make yourself at home in the Snake Creek Campsites along the Snake Creek River, located on the south-eastern side of the park. These sites are nestled among Aspen groves and at the base of impressive limestone cliffs. Sites to choose from include, Monkey Rock, Squirrel Springs, Pinnacles, Eagle Peak. The Shoshone and Johnson Lake trail sites are a short 0.5 mile hike from the parking lot at the end of the Snake Creek Road.", latitude: 38.925381, longitude: -114.251512},
  {name: "Sorrel Ridge Hiker-Biker Campsite", city: "Hagerstown", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 154.1 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.57169900000001, longitude: -78.45321300000002},
  {name: "South Beach Campground", city: "Port Angeles", totalsites: "55", firstcome: "55", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/kalaloch-area-brochure.htm", description: "Positioned on a bluff overlooking the Pacific Ocean, South Beach offers panoramic ocean views and beach access.", latitude: 47.5660233967878, longitude: -124.361387859395},
  {name: "South Campground", city: "f", totalsites: "117", firstcome: "0", reservable: "117", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/zion/planyourvisit/south-campground.htm", description: "South Campground is located near the Zion Canyon Visitor Center, ½ mile from the South Entrance at Springdale, Utah. Tent, dry RV, and group campsites are available by reservation from March through October. All sites include a place for a tent or RV, a picnic table, and a fire ring. Reservations may be made 14 days in advance of your stay. Reservations are needed because the campground is full nearly every night during the reservation season.", latitude: 37.203742599284524, longitude: -112.98419155753885},
  {name: "South Core Banks -- Beach camping", city: "Great Island", totalsites: "1", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://go.nps.gov/x2s8yq", description: "Dispersed camping along the ocean beach of this 20 mile long barrier island -- this is backcountry-style camping -- all camping is done directly on the ocean beach, there are no camp sites. This area is reached only by ferry. Both tent camping and vehicle camping is allowed. Vehicles -- 4x4 highly recommended -- must be able to drive on the soft sand of the beach and the sand trail behind the dunes known as the \"back road\". There are no hard surfaces on the island. Practice Leave No Trace principles.", latitude: 34.76055561111111, longitude: -76.41222219444445},
  {name: "South Fork Campground", city: "Sequoia National Park", totalsites: "10", firstcome: "10", reservable: "0", fees: "6.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/seki/planyourvisit/south_fork.htm", description: "South Fork Campground and its access road were heavily and extensively damaged by floods and landslides from winter storms. The campground is closed until further notice. This small, primitive campground is in a remote area of the foothills on the South Fork of the Kaweah River, away from main park highways and features. The dirt road to this area is completely impassable to vehicles due to extensive damage from winter storms. There is no potable water here. Trailers and RVs are not permitted.", latitude: 36.34998442726772, longitude: -118.76546982357763},
  {name: "South Lake Desor Campground", city: "Houghton", totalsites: "10", firstcome: "7", reservable: "3", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "South Lake Desor Campground is located inland on the south side of Lake Desor off of the Greenstone Ridge Trail. There is no canoe/kayak/boat access. Stay Limit: 2 nights effective from June 1 – Labor Day. Access: Foot", latitude: 47.970686098959334, longitude: -88.97153792382892},
  {name: "South Rim Campground", city: "Montrose", totalsites: "88", firstcome: "14", reservable: "74", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/blca/planyourvisit/camp_southrim.htm", description: "The South Rim Campground is located one mile from the South Rim Visitor Center. Reservations may be made on recreation.gov for mid May to mid September, and it is first-come, first served after that. Sites are fairly close together, and are partially shaded by gambel oak and serviceberry bushes. Only Loop B offers 20 and 30 amp electric hookups. During the warm season, drinking water is available, but there are no showers or dish cleaning stations, and no water for RV tanks.", latitude: 38.54381032355281, longitude: -107.689424061934},
  {name: "Southwest Walk-In Campground", city: "Mineral", totalsites: "21", firstcome: "21", reservable: "0", fees: "16.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/southwest-walk-in-campground.htm", description: "The Southwest Walk-in Campground is located on the east side of the Kohm Yah-mah-nee Visitor Center parking area. A short walk along a paved walkway provides easy access to each site. When snow is on the ground (approximately Nov-May), the location of the Southwest Campground changes to an over-the-snow area south of the Kohm Yah-mah-nee Visitor Center parking area. Winter camping registration is located at the fee booth located south of the parking area.", latitude: 40.4366004217, longitude: -121.532640953},
  {name: "Split Mountain Group Campground", city: "Jensen", totalsites: "4", firstcome: "0", reservable: "4", fees: "40.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/dino/planyourvisit/splitmountaincampground.htm", description: "The Split Mountain Group Campground is located along the banks of the Green River at an elevation of 4800 feet (1463 m) near the foot of Split Mountain. The campground is 5 miles (8 km) from the dinosaur quarry, where you can see 150 million year old dinosaur bones encased in the rock. Beside the campground is the Split Mountain Boat Ramp where rafters and boaters come off the Green River. During the off season, when the Green River Campground is closed, the Split Mountain Campground is open to all campers.", latitude: 40.4405279561, longitude: -109.252312611},
  {name: "Sprague Creek Campground", city: "West Glacier", totalsites: "25", firstcome: "0", reservable: "25", fees: "23.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Sprague Creek is a small campground located on the northeast shore of Lake McDonald, just off Going-to-the-Sun Road. Towed units (vehicle plus unit) and RVs exceeding 21 feet are not permitted in this campground. The campground is located within trees, providing shade during warm summers. Some sites near the shore have unobstructed views of Lake McDonald. Reservations are required to camp at Sprague Creek Campground. Click the reservation button below for details.", latitude: 48.6056174161, longitude: -113.885413396},
  {name: "Spring Canyon Campground", city: "Grand Coulee", totalsites: "68", firstcome: "0", reservable: "68", fees: "23.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-springcanyon.htm", description: "Spring Canyon Campground stair-steps down the hillside to Lake Roosevelt, offering sweeping views of the lake and surrounding landscape. The open desert environment also means little shade and extremely hot temperatures in the summer. Spring Canyon is conveniently located near the towns of Grand Coulee and Coulee Dam, as well as the Grand Coulee Dam. The campground offers a Day Use Area, group sites, and a nearby boat launch.", latitude: 47.933040826786495, longitude: -118.94004952739897},
  {name: "Spring Canyon Group Sites", city: "Grand Coulee", totalsites: "2", firstcome: "0", reservable: "2", fees: "55.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/laro/planyourvisit/cg-group-springcanyon.htm", description: "The Spring Canyon group sites are located at the base of Spring Canyon Campground, offering sweeping views of the lake and surrounding landscape. The open desert landscape also means little shade and extremely hot temperatures in the summer. Spring Canyon has two group sites and each can hold up to 25 people. Spring Canyon is conveniently located near the town of Grand Coulee and the Grand Coulee Dam.", latitude: 47.934625, longitude: -118.94087},
  {name: "Spring Creek Campground", city: "Harriet", totalsites: "12", firstcome: "12", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/camping.htm", description: "Spring Creek Campground is a first come, first serve campground near Harriet, AR. Spring Creek has 12 tent only sites with no electrical amenities, no flush restrooms, and no water.", latitude: 36.0299778999, longitude: -92.5844677011},
  {name: "Spring Gap Campground", city: "f", totalsites: "14", firstcome: "0", reservable: "14", fees: "40.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - seasonal", url: "https://www.recreation.gov/camping/campgrounds/252967", description: "Mile 173.3 (Allegany County, MD) - Spring Gap Campground offers 12 single drive-in campsites and two drive-in group sites. Campsites 1, 2, 8, 11 and 13 can fit a single vehicle (i.e., car/pickup/van). Campsites 3-7, 12, 14 can fit a small 20 ft. or less RV/trailer/pop-up. Single sites can accommodate up to eight people. Each single has a picnic table, fire pit and grill. Campsites 9 and 10 are group sites capable of holding 30 people each. The group site has room for two vehicles.", latitude: 39.564844337461764, longitude: -78.71788171349304},
  {name: "Spud Rock Spring", city: "Tucson", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "", description: "- 7,400 - 3 campsites (6 people max per site)", latitude: 32.204179, longitude: -110.532126},
  {name: "Spur 406 Campground", city: "f", totalsites: "6", firstcome: "6", reservable: "0", fees: "6.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/amis/planyourvisit/spur-406-campground.htm", description: "Spur 406 Campground is generally open year-round and rarely fills up, except possibly on holiday weekends. It may close on occasion due to high water events. A boat launch ramp off the campground may be available depending on lake levels. This campground offers 6 primitive sites, each with a covered picnic table and a BBQ grill. No ground fires are allowed. The sites are suitable for tents or RVs. Vault toilets are available. No drinking water or hookups are available.", latitude: 29.550551581798967, longitude: -101.02043723006028},
  {name: "St. Mary", city: "West Glacier", totalsites: "148", firstcome: "0", reservable: "148", fees: "10.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "St. Mary campground is the largest campground on the east side of Glacier National Park and is open year round. Activities such as interpretive programs, book sales, and shuttle service tours are located at the nearby St. Mary Visitor Center. St. Mary is one of the campgrounds in the park that take reservations for part of the summer. Though shade may be sparse, aspen trees grace St. Mary campground with soothing sounds from spring and summer breezes, and colorful splashes of yellow late in the season.", latitude: 48.751679033, longitude: -113.442405203},
  {name: "Stafford Beach Campground", city: "f", totalsites: "10", firstcome: "0", reservable: "10", fees: "12.00", showers: "Cold - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/cuis/planyourvisit/staffordbeach.htm", description: "Stafford Beach Campground requires a three and a half mile hike from Sea Camp. All sites are walk-to, tent sites and campers are responsible for transporting all gear. Carts of any kind or wheeled coolers cannot be used to haul gear. The site has flush toilets, cold showers, and water spigots with potable water. The sites include fire rings with grills. Food storage lockers (bear boxes) are available. Sites are $12 a night, good for up to 6 people.", latitude: 30.80667, longitude: -81.45139},
  {name: "Stair Falls Campsite", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "", description: "A primitive campsite located near the portage trail for Stair Falls. The campsite can be reached by a 2-mile hike from Haskell Gate or by paddling down the East Branch Penobscot River.", latitude: 46.0872701025, longitude: -68.7779046814},
  {name: "Staircase Campground", city: "Hoodsport", totalsites: "49", firstcome: "49", reservable: "49", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/olym/planyourvisit/staircase-area-brochure.htm", description: "Camp near the Skokomish River and enjoy old-growth forest at Staircase. Summer ranger programs and riverside campsites available.", latitude: 47.5153731343, longitude: -123.32966806},
  {name: "Stanton Creek Primitive Camping Area", city: "Lake Powell", totalsites: "0", firstcome: "0", reservable: "0", fees: "12.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/glca/planyourvisit/camping.htm", description: "Designated primitive camping areas that are accessible by vehicle and sometimes by vessel as well. First-come first served, no reservations. No designated sites. No potable water. When pit toilets are unavailable, campers must bring portable toilets for use and proper disposal into the sewer system.", latitude: 37.498985, longitude: -110.699117},
  {name: "Station Camp Campground", city: "Oneida", totalsites: "24", firstcome: "0", reservable: "28", fees: "28.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/biso/planyourvisit/horsecamps.htm", description: "This campground contains 24 developed campsites. Each site offers water and electricity, tables, grills, tie-outs for 4 horses, access to modern restrooms with hot showers, a dump station and access miles of horse trails. Station Camp Campground is located on station Camp Road approximately 4.5 miles from the intersection of Coopertown Road and Leatherwood Road . The fee rate per night is $28.00 (half price for holders of the Interagency Access/Senior Passes).", latitude: 36.5458497459104, longitude: -84.6352172739667},
  {name: "Steel Creek Campground", city: "Ponca", totalsites: "40", firstcome: "21", reservable: "19", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/buff/planyourvisit/camping.htm", description: "Steel Creek has 26 tent only sites and 14 sites for campers with horses/stock animals (six people and four stock animals allowed per site). Tent sites 1 -13 and horse sites 27 - 32 are available for reservation at www.recreation.gov or by calling 1-877-444-6777. Reservations can be made 5 days to six months in advance. There are no electrical amenities or water hookups, but flush restrooms and water spigots are available from March 15 through November 14.", latitude: 36.0400288995, longitude: -93.3463188996},
  {name: "Stevens Creek Campground", city: "f", totalsites: "53", firstcome: "54", reservable: "0", fees: "16.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/cure/planyourvisit/camp_stevens_creek.htm", description: "The campground is located 12 miles west of Gunnison on U.S. Highway 50 on the shore of Blue Mesa Reservoir. Tents to large RVs can be accommodated. The 53 sites are surrounded by sage and rabbitbrush. There is no natural shade. All sites are first-come first-served. Elevation: 7540 feet", latitude: 38.48692337619382, longitude: -107.09112479936186},
  {name: "Stickpile Hill Hiker-Biker Campsite", city: "Little Orleans", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 149.4 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.583598999999985, longitude: -78.397693},
  {name: "Stone Cliff", city: "Thurmond", totalsites: "7", firstcome: "7", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - year round", phone: "Yes - year round", url: "", description: "This tent only campground located near the historic railroad town of Thurmond offers 7 walk-in sites, 5 on the sandy beach of the New River and 2 in the nearby forest. There are no drive-in sites at this location. RV, camper, or car camping is prohibited. The Stone Cliff Trailhead also starts at the campground.", latitude: 37.93304470057407, longitude: -81.0629900700327},
  {name: "Stovepipe Wells Campground", city: "Stovepipe Wells", totalsites: "190", firstcome: "190", reservable: "0", fees: "14.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/deva/planyourvisit/stovepipe-wells-campground.htm", description: "Open seasonally starting October 15th at noon. First come, first served campground located at sea level. The Stovepipe Wells campground has views of Death Valley proper and of the Mesquite Flat Sand Dunes. It is adjacent to the Stovepipe Wells general store, ranger station, and a privately operated RV park. Fee is paid at the pay station at the front of the campground.", latitude: 36.6066434019, longitude: -117.147762489},
  {name: "Summit Lake North Campground", city: "Mineral", totalsites: "46", firstcome: "0", reservable: "46", fees: "24.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/summit-lake-campground.htm", description: "Summit Lake North Campground is located on the north side of Summit Lake at about the middle point of the 30-mile park highway. The campground includes loop A and B. Additional campsites are located in three loops on the south side of the lake in Summit Lake South Campground. A picnic area is located on the shore and features a swimming area, picnic tables, and day use parking.", latitude: 40.4946207961, longitude: -121.424949424},
  {name: "Summit Lake South Campground", city: "Mineral", totalsites: "49", firstcome: "0", reservable: "49", fees: "22.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/summit-lake-campground.htm", description: "Summit Lake South Campground is located on the south side of Summit Lake at about the middle point of the 30-mile park highway. The campground contains loops C, D, and E (tents only). Additional campsites are located in two loops on the north side of the lake in Summit Lake North Campground.", latitude: 40.4904121913623, longitude: -121.424648255834},
  {name: "Sunset Campground", city: "Furnace Creek", totalsites: "230", firstcome: "230", reservable: "0", fees: "14.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/deva/planyourvisit/sunset-campground.htm", description: "Open seasonally starting October 15th at noon. First come, first served, large campground that rarely fills. This location has little to no vegetation and is comprised of desert gravels. Car and tent camping is permitted however each site DOES NOT offer a firegrate or picnic table. Campfires are not allowed in Sunset except at a few designated public areas within the campground. Fee is paid at the pay station in the campground.", latitude: 36.4572874697, longitude: -116.861919944},
  {name: "Sunset Campground", city: "Grant Grove", totalsites: "158", firstcome: "0", reservable: "158", fees: "28.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/seki/planyourvisit/sunset.htm", description: "All sites are reservation only. 𝗢𝗽𝗲𝗻𝗶𝗻𝗴 𝗗𝗮𝘁𝗲: June 16, 2023 (subject to change) Individual site reservations are on a one-month rolling basis. Group site reservations are on a four-month rolling basis. Sunset Campground is located 3 miles (5 km) from Kings Canyon Park entrance. It is located near Grant Grove Village in an open stand of evergreens. Services can be found in Grant Grove Village. A park amphitheater is located here and occasionally offers park programs.", latitude: 36.737368435773625, longitude: -118.96518028946252},
  {name: "Sunset Campground", city: "Bryce", totalsites: "100", firstcome: "98", reservable: "3", fees: "20.00", showers: "None", toilets: "Portable Toilets - seasonal", phone: "No", url: "https://www.nps.gov/brca/planyourvisit/sunsetcampground.htm", description: "Located west of Sunset Point, approximately 1.5 miles south of the Bryce Canyon Visitor Center, and is comprised of 99 sites in 3 loops; Loop A is primarily for RVs; Loops B & C are tent-only. There are no sewer, water or electrical hook-ups available. A dump station is available in summer months near North Campground; use is included with campsite fee. Potable water is available near the dump station. Sunset Campground is close to Sunset Point and has a shuttle stop at its entrance.", latitude: 37.6222868282, longitude: -112.174839007},
  {name: "Sunset View Campground", city: "Shonto", totalsites: "0", firstcome: "31", reservable: "0", fees: "f", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "", description: "Sunset View Campground has 31 sites and open all year round.", latitude: 36.6725214798, longitude: -110.542427983},
  {name: "Swains Lock Hiker-Biker Campsite", city: "Potomac", totalsites: "5", firstcome: "5", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 16.6 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.03100200000001, longitude: -77.244579},
  {name: "Tamarack Flat Campground", city: "f", totalsites: "52", firstcome: "0", reservable: "52", fees: "24.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yose/planyourvisit/tamarack.htm", description: "Tamarack Flat Campground is located off the Tioga Road just east of Crane Flat. The campground is approximately 20 miles (45 minutes) from Yosemite Valley and is tucked away in the forest 3 miles off Tioga Road. RVs and Trailers are not recommended for this harder to access, and more primitive campground. At an elevation of 6,300 feet (1,900 m) elevation, the campground is near Tamarack Creek, which is the only water source (must be filtered, treated, or boiled). There is a gas station nearby at Crane Flat.", latitude: 37.7517175326, longitude: -119.736234028},
  {name: "Tejas Wilderness Campground", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/ts_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. The extra distance to the Tejas Wilderness Campground is worth the time for those who wish to stay in a more densely forested surrounding. The tall trees provide deep shade in the morning and late afternoon and protection from high winds aloft. Centrally located, the Tejas Campground is 5.5 miles from Pine Springs Trailhead or 6.2 miles from Dog Canyon.", latitude: 31.9368597503931, longitude: -104.850488488205},
  {name: "Teklanika River", city: "Denali Park", totalsites: "53", firstcome: "0", reservable: "53", fees: "37.50", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/dena/planyourvisit/campground-tek.htm", description: "Open in summer only (May 20 to mid-September). Though nearly all private vehicles must turn around at mile 15, Savage River, Tek campers are an exception to that rule. If you wish, you may drive your vehicle / RV to Tek. The trade-off is that you must make, at minimum, a three-night stay, and your vehicle must stay in your campsite for the duration of your stay, while you use transit buses to sightsee. Tent campers using the park bus system to reach Tek are not subject to the 3-night minimum stay.", latitude: 63.6704983524783, longitude: -149.580973783265},
  {name: "Temple Bar Campground (Lake Mead)", city: "Boulder City", totalsites: "71", firstcome: "0", reservable: "0", fees: "20.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.recreation.gov/camping/campgrounds/10085626", description: "Enjoy the Temple Bar area by land or water. Hike through the desert along the Temple View Trail to observe the crystal blue waters of Lake Mead and the natural temple that changes colors throughout the day. Launch your own boat or rent one from the marina. You can also dine at the seasonal cafe, or grab a variety of snacks, drinks and charcoal from the store.", latitude: 36.03031828993541, longitude: -114.32485227007857},
  {name: "Temple Bar RV Park (Lake Mead)", city: "f", totalsites: "0", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://templebarlakemead.com/rv-park-campground/", description: "Just 75 miles away from Las Vegas, Temple Bar Marina is located in the Temple Basin on the Arizona side of eastern Lake Mead. It’s the closest marina for Arizonans who want to get away from the more populated Boulder Basin area of Lake Mead. The RV Park at Temple Bar Marina is a perfect location for enjoying the lake and all its recreational activities.", latitude: 36.03413715620238, longitude: -114.32279284224819},
  {name: "Texas Springs Campground", city: "Furnace Creek", totalsites: "92", firstcome: "92", reservable: "0", fees: "16.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/deva/planyourvisit/texas-springs-campground.htm", description: "Open seasonally starting October 15th at noon. First come, first served campground located in the hills above Furnace Creek. Great views and some trees. No generators allowed. Each site has a firegrate and picnic table. Fee is paid at pay station at the front of the campground.", latitude: 36.4600746963, longitude: -116.857302043},
  {name: "Thayer Campground", city: "Thurmond", totalsites: "4", firstcome: "4", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "", description: "This tent only campground is located near the community of Thayer along the banks of the New River. It has 4 walk-in sites. There are no drive-in sites at this location. RV, camper, or car camping is prohibited. Alcohol is prohibited at this campground.", latitude: 37.90172933750409, longitude: -81.03301777594444},
  {name: "The Colonies RV and Travel Park", city: "Fort Monroe", totalsites: "47", firstcome: "5", reservable: "47", fees: "f", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "http://www.thecoloniesrvtravelpark.com/", description: "With 47 campsites, 22 with full hookups, the Colonies RV and Travel Park is a great place to experience all Fort Monroe and the surrounding area have to offer. Amenities include a country store, clubhouse with kitchen, game room, and TV room.", latitude: 37.022068, longitude: -76.298918},
  {name: "Woolum", city: "St. Joe", totalsites: "0", firstcome: "0", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "Woolum Campground is a large open field at the Woolum access to the Buffalo River near St. Joe AR. There are no amenities at Woolum. There are no designated sites. A vault toilet is available. Fees are not charged at Woolum. Woolum serves as overflow when Tyler Bend campground is full.", latitude: 35.9703114997, longitude: -92.8805197992},
  {name: "The Needles Campground", city: "Moab", totalsites: "29", firstcome: "14", reservable: "11", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "http://www.nps.gov/cany/planyourvisit/camp-need.htm", description: "The Needles Campground has 26 individual sites, plus 3 group sites in different locations around The Needles district. Nightly camping fee for an individual site is $20. You can reserve some individual sites spring through fall. Other times of the year, individual sites are first-come, first-served. You can also reserve group sites for nights between mid-March and mid-November. There are toilets, picnic tables, and fire rings in the campground.", latitude: 38.1465958314, longitude: -109.801216334},
  {name: "The Point Campground", city: "f", totalsites: "55", firstcome: "38", reservable: "17", fees: "16.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/chic/planyourvisit/campgrounds.htm", description: "The Point Campground is open year round. It has a combination of sites with and without RV hook-ups. The Upper Loop is available by reservation only. The Lower Loop is first come, first served. Located near the shor of Lake of the Arbuckles and The Point Boat Launch.", latitude: 34.4595144058, longitude: -97.019805796},
  {name: "Thorndike Campground (Primitive)", city: "Death Valley", totalsites: "6", firstcome: "6", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/deva/planyourvisit/thorndike-campground.htm", description: "First come / first served primitive, forested campground with no additional fee located at 7,400' elevation. Accessible to high clearance vehicles (no longer than 25'), and 4x4 may be necessary.", latitude: 36.236917, longitude: -117.072172},
  {name: "Three Mile Campground", city: "Houghton", totalsites: "15", firstcome: "12", reservable: "3", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "Three Mile Campground is located on Rock Harbor Channel off of the Rock Harbor and Mount Franklin Trails. Boats can overnight at dock. Stay Limit: 1 night effective June 1 – September 17 annually Shelters: 8 Depth at dock (normal conditions): 9’ Access: Foot/canoe/kayak/private boat", latitude: 48.12385213393805, longitude: -88.52961948526877},
  {name: "Timber Creek Campground", city: "Grand Lake", totalsites: "98", firstcome: "0", reservable: "98", fees: "35.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/romo/planyourvisit/tccg.htm", description: "Timber Creek Campground is closed. This campground is a seasonal campground open during the summer season.", latitude: 40.379225136469785, longitude: -105.85275135737629},
  {name: "Todd Harbor Campground", city: "Houghton", totalsites: "9", firstcome: "6", reservable: "3", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Todd Harbor Campground is located in Todd Harbor on the north shore of Isle Royale National Park, off of the Minong Ridge Trail. Boats can overnight at dock. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 1 Depth at dock (normal conditions): 2’. Access: Foot/canoe/kayak/private boat", latitude: 48.05080649877427, longitude: -88.82111715996166},
  {name: "Tomales Bay Boat-in Camping", city: "Point Reyes Station", totalsites: "20", firstcome: "0", reservable: "20", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/pore/planyourvisit/kayak_libc.htm", description: "Tomales Bay boat-in camping is allowed on west-side National Park beaches north of Tomales Bay State Park's northern border (i.e., north of Indian Beach and Duck Cove). These beaches are tidally influenced and generally are small sandy coves backed against steep cliffs. Campers on Tomales Bay beaches must arrive by boat and may not hike, bike, or ride horses to the beaches. Twenty permits are available each day—9 for parties of 1 to 6 people, 8 for parties of 7 to 14, and 3 for parties of 15 to 25.", latitude: 38.163318, longitude: -122.915392},
  {name: "Tookers Island Campground", city: "Houghton", totalsites: "2", firstcome: "2", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/boating-guide.htm", description: "Tookers Island Campground is located on Tookers Island in Rock Harbor Channel. Boats can overnight at dock. Stay Limit: 3 nights from June 1 – Labor Day annually. Shelters: 2 Depth at dock (normal conditions): 7’ Access: Canoe/kayak/private boat", latitude: 48.12939102951485, longitude: -88.5032933491924},
  {name: "Tower Fall Campground", city: "Yellowstone National Park", totalsites: "31", firstcome: "0", reservable: "31", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yell/planyourvisit/towerfallcg.htm", description: "2023 DATES TO BE DETERMINED. Tower Fall Campground—elevation 6,600 feet (2012 m)—is located on the north side of the steep, winding, road to Dunraven Pass. The campground is near the Tower General Store and Tower Fall. The Lamar Valley, with spectacular wildlife viewing opportunities, is located nearby as are several hiking trails. Roosevelt Lodge, a short drive from the campground, offers dining and horseback riding. This is a reservable campground run by the National Park Service.", latitude: 44.889549848348636, longitude: -110.3894758833328},
  {name: "Town Creek Hiker-Biker Campsite", city: "Hagerstown", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 162.1 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.52446099999999, longitude: -78.538643},
  {name: "Trailer Village RV Park - South Rim", city: "Grand Canyon", totalsites: "123", firstcome: "0", reservable: "123", fees: "71.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.visitgrandcanyon.com/trailer-village-rv-park", description: "Trailer Village is the only in-park RV campground with full hookups (sewage, water, and electrical with 30 amp and 50 amp sites available) Open year-round, Trailer Village features paved pull-through sites which can accommodate vehicles up to 50 feet long. Trailer Village is operated by Delaware North. Reservations can be made up to 13 months in advance. If visiting during the busy season (May through October) making reservations 1 year in advance is recommend. Visit their website to make reservations.", latitude: 36.052812, longitude: -112.115001},
  {name: "Trappers Lake Group Backpacking Site", city: "f", totalsites: "1", firstcome: "0", reservable: "1", fees: "5.00", showers: nil, toilets: nil, phone: "", url: "", description: "Trappers Lake Group Campground is a little over a mile inland from the North Country Trail and 0.2 miles east of Trappers Lake Campground. It is on the north shore of Trappers Lake in the Beaver Basin Wilderness. One shared fire ring and pit toilet. Water source is Trappers Lake. This is a hike-in campground only; kayakers cannot access this campground.", latitude: 46.5876435919, longitude: -86.3154888155},
  {name: "Trappers Lake Individual Backpacking Sites", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "5.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/backcountry.htm", description: "Trappers Lake Campground is in the heart of the Beaver Basin Wilderness. The five campsites are on the north shore of Trappers Lake. This campground is a quiet hideaway located about one mile inland from the North Country Trail. One shared fire ring and pit toilet. Water source is Trappers Lake. This is a hike-in campground only; kayakers cannot access this campground.", latitude: 46.586517, longitude: -86.316522},
  {name: "Tuolumne Meadows Campground", city: "f", totalsites: "304", firstcome: "156", reservable: "148", fees: "36.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - seasonal", url: "https://www.nps.gov/yose/planyourvisit/tmcamp.htm", description: "Tuolumne Meadows Campground is Yosemite’s largest, and is located along the Tioga Road, with some areas located close to the Tuolumne River. At 8,000 feet (2,600 m) this campground is open seasonally and has wonderful summer access to many hikes, lakes, and prominent viewpoints. The campground is located approximately 1 hour and 30 minutes from Yosemite Valley but a small store, grill, and post office are located nearby in Tuolumne Meadows.", latitude: 37.8744752916, longitude: -119.353650884},
  {name: "Turkey Run Ridge Group Campground", city: "Triangle", totalsites: "9", firstcome: "0", reservable: "9", fees: "65.00", showers: "None", toilets: "Flush Toilets - year round", phone: "No", url: "https://www.nps.gov/prwi/planyourvisit/turkeyrunridge.htm", description: "Turkey Run Ridge Group Campground (tents only) is designed for groups of people. Families and individuals are encouraged to use Oak Ridge Campground instead. Turkey Run is a 9-site campground with restrooms (no showers), grated fire pits, and picnic tables. Three sites hold a maximum of 40 people. Six sites hold a maximum of 25 people. Parking is available. Alcohol is prohibited at Turkey Run Ridge Group Campground. You can reserve your spot at on www.recreation.gov or call 877-444-6777.", latitude: 38.58160140298197, longitude: -77.3727473440166},
  {name: "Turtle Run Hiker-Biker Campsite", city: "Poolesville", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 34.4 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use. Water is turned off from mid-November to mid-April each year.", latitude: 39.13928799999999, longitude: -77.51567299999999},
  {name: "Twelvemile Beach Campground", city: "Grand Marais", totalsites: "36", firstcome: "0", reservable: "36", fees: "25.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/piro/planyourvisit/camping.htm", description: "The campground's 36 sites are located on a high sandy plateau above Twelvemile Beach on Lake Superior. Some sites have a lake view. There are two disabled-accessible campsites. Generator use is prohibited in the campground's west loop. There are three sets of stairs to the beach. The North Country National Scenic Trail treks through the campground. The 2-mile White Birch Nature Trail begins and ends here. The campground is open by reservation only, mid-May through mid-October. Leashed pets allowed.", latitude: 46.6408, longitude: -86.21583},
  {name: "Twin Peaks Campground", city: "Ajo", totalsites: "208", firstcome: "0", reservable: "208", fees: "20.00", showers: "Free - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/orpi/planyourvisit/twin-peaks.htm", description: "Twin Peaks Campground is the main, developed campground at Organ Pipe Cactus National Monument, located 1.5 miles from the Kris Eggle Visitor Center and Highway 85. Twin Peaks Campground has 34 tent-only sites and 174 sites for RVs, and several sites can accommodate RVs up to 45 feet in length. Restrooms have running water and some have solar-heated showers. Hookups for electricity, water, or sewer are not available. A dump station, with potable water is located past the last row of campsites.", latitude: 31.940681081217072, longitude: -112.81094174286494},
  {name: "Two Medicine", city: "West Glacier", totalsites: "100", firstcome: "0", reservable: "101", fees: "20.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "Two Medicine is approximately 13 miles from East Glacier. Sites within the campground at are generally shaded by trees, and offer some privacy from other campers. Boat tours and Red Bus tours can be found at Two Medicine. There are numerous day hiking opportunities available, including a handicap accessible trail to Running Eagle Falls. The campground remains open in late-September for primitive camping with no running water or flush toilets. Advanced reservations are required for all sites.", latitude: 48.492114971, longitude: -113.36407124},
  {name: "Two Rivers Campground", city: "Eminence", totalsites: "22", firstcome: "20", reservable: "2", fees: "20.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/ozar/planyourvisit/two-rivers-campground.htm", description: "Two Rivers Campground is located at the confluence of the Jacks Fork and Current Rivers, at the end of V Highway off State Highway 106 in Shannon County, MO.", latitude: 37.189929, longitude: -91.276297},
  {name: "Tyler Bend Campground", city: "St. Joe", totalsites: "42", firstcome: "20", reservable: "22", fees: "20.00", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/buff/planyourvisit/tyler-bend-campground.htm", description: "Tyler Bend Campground is a developed campground in the middle district of the park. It has 28 drive-in sites, 10 walk-in sites, 5 group sites and a pavilion. The campground has flush restrooms, showers, dump station, potable water, but no electrical amenities. Reservations are available at www.recreation.gov (1-877-444-6777) for some sites. Fees are charged March 15 through November 14. It is free to camp November 15 through March 14, but limited amenities are available during that time.", latitude: 35.9868835993, longitude: -92.7595278001},
  {name: "Upper East Branch Campsite", city: "Patten", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "", description: "Walk-in campsite along the East Branch of the Penobscot River.", latitude: 46.113820001804086, longitude: -68.79790999965547},
  {name: "Upper Goodell Creek Group Campsite", city: "Sedro-Woolley", totalsites: "1", firstcome: "0", reservable: "1", fees: "75.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.recreation.gov/camping/campgrounds/234089", description: "Upper Goodell Group Campground is a convenient campground on the banks of Goodell Creek. The group site has tent pads, a pavilion with picnic tables, and a fire grate. Maximum capacity is 30 people (10 vehicles). Upper Goodell cannot accommodate RV's. Vault toilets and garbage service is provided. There are no hook-ups or potable water.", latitude: 48.683276868130136, longitude: -121.27077652544762},
  {name: "Upper Lehman Campground", city: "f", totalsites: "23", firstcome: "0", reservable: "23", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grba/planyourvisit/campgrounds.htm", description: "Whether you prefer the rich smell of summer mahogany riding air currents blended with the vanilla of ponderosa pine, or the sound of a clear mountain stream babbling beneath a symphony of swaying white fir, you won't regret your stay at Upper Lehman Campground. At 7500 feet in elevation and 3 miles up the Wheeler Peak Scenic Drive a stay at Upper Lehman is just a brief cruise from high alpine trails that will take you into the heart of the Snake Range.", latitude: 39.0136, longitude: -114.24776},
  {name: "Upper Pines Campground", city: "Yosemite", totalsites: "235", firstcome: "0", reservable: "235", fees: "36.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/yose/planyourvisit/pinescampgrounds.htm", description: "Upper Pines is located near the Merced River in Yosemite Valley. Yosemite Valley is centrally located in the park and boasts some of Yosemite’s most iconic features. This large campground is located at 4,000 feet (1,219 m) elevation and can be accessed from all park roads. Upper Pines is within biking and walking distance of many services and trailheads in Yosemite Valley and is located on the free shuttle route. There are food and grocery services nearby at Curry Village and Yosemite Village.", latitude: 37.7384294727, longitude: -119.564411225},
  {name: "Upper Scorpion Campground", city: "f", totalsites: "12", firstcome: "10", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/gicl/planyourvisit/camping.htm", description: "Upper Scorpion Campground is a partially developed campground within walking distance of the West Fork of the Gila River. The campground is located less than one mile from the Gila Cliff Dwellings National Monument just off of NM State Highway 15. Drinking water is available, tables and grills are provided at select sites. Vault toilets are available on site. Camping is free of charge and first-come, first-served.", latitude: 33.23049878845756, longitude: -108.2609959291612},
  {name: "Valley View Group Campground", city: "Bushkill", totalsites: "5", firstcome: "0", reservable: "5", fees: "f", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/dewa/planyourvisit/group-camping.htm", description: "Primitive group campsites in an open area with some trees next to the river; drinking water available; minimum of five people required to reserve site. Only Site One is handicap accessible.", latitude: 41.1156191003, longitude: -74.9637757011},
  {name: "Wahweap Campground & RV Park", city: "Page", totalsites: "266", firstcome: "36", reservable: "251", fees: "f", showers: "Hot - Year Round", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.lakepowell.com/rv-camping/wahweap-rv-campground/", description: "112 dry campsites (no hook-ups), 90 full hook-ups, and 6 group camping sites. Facilities include restrooms, laundry, showers, store, phones, dump station and potable water. The amphitheater, picnic area and swim beach are nearby. To make reservations for full hook-ups, group or dry camping please visit www.lakepowell.com or call 800-528-6154. The Wahweap camping store is 928-645-1059. Fees vary.", latitude: 36.9967066553, longitude: -111.499386112},
  {name: "War Ridge/Backus Mountain Campground", city: "Prince", totalsites: "8", firstcome: "8", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "", description: "This is the only campground in the park that is not along the river. It is located on a ridgetop near Backus Mountain. There are 8 drive-in sites for tents, RVs, or campers. Only small (less than 25 feet) RVs or campers are recommended. Camp only at designated sites. RV, camper, or car camping in non-designated sites or any parking area is strictly prohibited.", latitude: 37.84526058608828, longitude: -80.98518203383988},
  {name: "Warner Valley Campground", city: "Chester", totalsites: "17", firstcome: "9", reservable: "8", fees: "21.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/lavo/planyourvisit/warner-valley-campground.htm", description: "The campground is located one mile north of Warner Valley Ranger Station via a gravel road and 17 miles north of Chester. Not recommended for trailers. Numerous trailheads are located nearby.", latitude: 40.4428300456, longitude: -121.394565261},
  {name: "Washington Creek Campground", city: "Houghton", totalsites: "19", firstcome: "15", reservable: "4", fees: "0.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/windigo.htm", description: "Washington Creek Campground is located along Washington Creek near Windigo on the southwest end of Isle Royale, off of the Feldtmann Lake, Minong, and Greenstone Ridge Trails. The Windigo Visitor Center, Windigo Store, and Windigo Comfort Station are all accessible from the campground. Boats can stay at Windigo Dock (average depth at dock 4'-20') or stay and walk .3 miles to campground. Stay Limit: 3 nights from June 1 – September 17 annually Shelters: 10 Access: Foot/canoe/kayak/private boat", latitude: 47.91621695055476, longitude: -89.15457402986016},
  {name: "Wassataquoik Campsite", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "A beautiful primitive backcountry campsite next to Katahdin Brook along the International Appalachian Trail. The campsite is near the Wassataquoik Lean-to and is 0.9 miles from the trailhead at Wassataquoik Gate or 1.4 miles from the Barnard Mountain Trailhead.", latitude: 45.9329028831785, longitude: -68.7146921403937},
  {name: "Wassataquoik Lean-to", city: "f", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/kaww/planyourvisit/camping.htm", description: "A rustic backcountry Lean-to along the International Appalachian Trail near the Wassataquoik Stream. The site is 0.9 miles from the Wassataquoik Gate or 1.4 miles from the Barnard Mountain Trailhead.", latitude: 45.9328416267, longitude: -68.7155631045},
  {name: "Watch Hill Family Campground", city: "f", totalsites: "28", firstcome: "0", reservable: "28", fees: "f", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "https://www.nps.gov/fiis/planyourvisit/campgrounds.htm", description: "The family campground at Watch Hill provides a camping experience like no other.", latitude: 40.6909163, longitude: -72.9866},
  {name: "Watchman Campground", city: "f", totalsites: "176", firstcome: "0", reservable: "176", fees: "30.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/zion/planyourvisit/watchman-campground.htm", description: "Watchman Campground is located next to the Zion Canyon Visitor Center, ¼ mile away from the South Entrance in Springdale, Utah. Tent and electric campsites are available year-round and group campsites are available from March through November. All sites include a place for a tent or RV, a picnic table, and access to a fire ring. All campsites in Watchman Campground require reservations year-round. Reservations may be made six (6) months in advance.", latitude: 37.1975852245451, longitude: -112.987306895859},
  {name: "Wawona Campground", city: "Yosemite", totalsites: "95", firstcome: "0", reservable: "93", fees: "36.00", showers: "None", toilets: "Flush Toilets - year round", phone: "Yes - year round", url: "https://www.nps.gov/yose/planyourvisit/wawonacamp.htm", description: "The Wawona Campground is located along the South Fork Merced River close to historic Wawona. At an elevation of 4,000 feet (1,219 m), the campground is located in the southern end of the park, 27 miles and approximately 45 minutes from Yosemite Valley. The majestic Mariposa Grove of Giant Sequoias is just a short drive away and the Yosemite History Center is in nearby Wawona where you can see some of the park’s oldest structures. Wawona offers a visitor center and a market.", latitude: 37.54472065874548, longitude: -119.67274646199724},
  {name: "Wawona Horse Camp", city: "f", totalsites: "2", firstcome: "0", reservable: "2", fees: "50.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "Yes - year round", url: "", description: "The Wawona Horse Camp is located along the South Fork Merced River in the heart of historic Wawona. At an elevation of 4,000 feet (1,219 m), the campground is located in the southern end of the park, 27 miles and approximately 45 minutes from Yosemite Valley. The majestic Mariposa Grove of Giant Sequoias is just a short drive away and the Yosemite History Center is adjacent to the camp where you can see some of the park’s oldest structures. Wawona offers a visitor center and a market.", latitude: 37.53948952463516, longitude: -119.65328856424183},
  {name: "Weather Station Campground - South Manitou Island", city: "Empire", totalsites: "23", firstcome: "20", reservable: "3", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/slbe/planyourvisit/smiweatherstcamp.htm", description: "The Weather Station Campground overlooks Lake Michigan from the bluffs on the south side of the island. Camp sites are well secluded from each other and have fire pits for open fires. Follow the wooded trail past the lighthouse about 1.3 miles to the campground. Bring water filtration equipment since there is no source of purified drinking water. There are 20 individual sites and 3 group sites.", latitude: 45.003301, longitude: -86.110347},
  {name: "West Chickenbone Campground", city: "Houghton", totalsites: "9", firstcome: "6", reservable: "3", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/camping.htm", description: "West Chickenbone Campground is located on the west end of Chickenbone Lake, off of the Indian Portage Trail This inland campground has no boater access. Canoe/kayak access from the 1.2 mile portage from McCargoe Cove or the 0.2 mile portage from Lake Livermore. Stay Limit: 2 nights from June 1 – Labor Day annually. Access: Foot/canoe/kayak", latitude: 48.063272054903905, longitude: -88.72467586394845},
  {name: "Wheeler Peak Campground", city: "f", totalsites: "37", firstcome: "0", reservable: "37", fees: "20.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/grba/planyourvisit/campgrounds.htm", description: "Located at 9500', Wheeler Peak campground offer a great experience camping at high elevation. Nestled in aspen groves in the shadow of Wheeler Peak, temperatures can be cold even during the summer. Potable water is not available.", latitude: 39.01091, longitude: -114.3044},
  {name: "White Pine Backcountry Campground", city: "Honor", totalsites: "6", firstcome: "6", reservable: "0", fees: "10.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/slbe/planyourvisit/whitepinesmap.htm", description: "This camp has 6 sites and is located about 2 miles from the Trail's End Road trailhead on the Platte Plains hiking trail south of Empire just off of M-22 and is about 1/2 mile from the Lake Michigan shore where you can obtain fresh water. There is no well water at White Pine, so if you use water from Lake Michigan, it must be treated before drinking it.", latitude: 44.737174, longitude: -86.08701},
  {name: "White Rock Hiker-Biker Campsite", city: "Hancock", totalsites: "1", firstcome: "1", reservable: "0", fees: "0.00", showers: "None", toilets: "Portable Toilets - year round", phone: "Yes - year round", url: "", description: "Mile 126.4 - Hiker-biker campgrounds are located along the towpath approximately every 5-7 miles. No fee or reservation required. Overnight stays are limited to one night. All sites include a chemical toilet, picnic table, grill, and water. Please Note: Water at hiker-biker campsites is non-potable. Water is not treated and not for drinking or cooking. If you use this water, you are advised to filter, chemically treat, or boil before use.", latitude: 39.67753999999999, longitude: -78.20979399999999},
  {name: "White Tank Campground", city: "Twentynine Palms", totalsites: "15", firstcome: "15", reservable: "0", fees: "15.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/jotr/planyourvisit/white-tank-campground.htm", description: "Located off Pinto Basin Road, White Tank Campground is nestled among immense granite boulders. First-come, first-served. RVs and trailers may not exceed a combined maximum length of 25 feet. There is no water available. White Tank has excellent night sky viewing and is located near the darkest section of the park. After occupying an open, vacant campsite, proceed to an entrance station to complete registration and pay. Payment can be delayed until morning if arriving after entrance stations close.", latitude: 33.985738, longitude: -116.0166743},
  {name: "White Wolf Campground", city: "f", totalsites: "74", firstcome: "0", reservable: "74", fees: "30.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yose/planyourvisit/wwcamp.htm", description: "White Wolf Campground is located off the Tioga Road between Tuolumne Meadows and Crane Flat, approximately an hour from Yosemite Valley at 8,000 feet (2,400 m). The campground is tucked into the forest about a mile from Tioga Road near White Wolf Lodge. Trailheads nearby lead to Lukens and Harden Lakes and the area is popular for those accessing other wilderness destinations. Minimal services may be available at White Wolf Lodge (if open). There are no other visitor services close to the campground.", latitude: 37.870349714937376, longitude: -119.64834665998012},
  {name: "Wildcat Campground", city: "Point Reyes Station", totalsites: "8", firstcome: "1", reservable: "7", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/pore/planyourvisit/wildcat-campground.htm", description: "Wildcat Campground is located in an open meadow on a bluff overlooking the ocean with a short walk to the beach. One may access Wildcat Campground by foot, bicycle, or horse; not by boat, auto, or RV. It is a 6.3-mile hike from Bear Valley Trailhead or a 5.5-mile hike from the Palomarin Trailhead. The only bicycling route is via a strenuous 6.7-mile ride along the Stewart Trail from the Five Brooks Trailhead. There are five regular (1- to 6-person) sites and three group (7- to 25-person) sites.", latitude: 37.97015310679054, longitude: -122.79068822618683},
  {name: "Wilderness Cabins", city: "Kula", totalsites: "3", firstcome: "0", reservable: "3", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/hale/planyourvisit/wilderness-cabins.htm", description: "Located on the island of Maui, Haleakalā National Park maintains three wilderness cabins for visitor use-- Hōlua, Palikū, and Kapalaoa. All cabins are accessible only by trail from the summit area and require you to hike a minimum of 3.7 mi (5.9 km).", latitude: 20.717921296462112, longitude: -156.14203614585165},
  {name: "Wilderness Ridge Wilderness Campground", city: "f", totalsites: "5", firstcome: "0", reservable: "5", fees: "6.00", showers: "None", toilets: "No Toilets", phone: "No", url: "https://www.nps.gov/gumo/planyourvisit/wr_wcg.htm", description: "A Wilderness Use Permit is required for all use of these campsites. An interesting hike along the Permian Reef Trail meanders up 2,000 feet to Wilderness Ridge where the sudden transition from rock to trees is refreshing. Once on top, the trail is level through forested and open areas and takes you to the edge of the escarpment where the view is outstanding. Wilderness Ridge Campground is in the trees, and worth the extra distance to save the resource from unnecessary damage.", latitude: 31.99784003435671, longitude: -104.77031431515502},
  {name: "Wilderness Road Campground", city: "Ewing", totalsites: "160", firstcome: "0", reservable: "154", fees: "24.00", showers: "Hot - Seasonal", toilets: "Flush Toilets - seasonal", phone: "No", url: "", description: "This 160 site campground is located approximately 3 miles from the park visitor center off of Highway 58 in Virginia. 20, 30 and 50 amp electrical hookups are available at 41 of the sites. Hot showers and potable water are located in the comfort stations. Camping rigs up to 50 feet can be accomodated. Sites are accessed through paved loop roads. Most of the sites require backing in with the exception of one pull through site. Campsites are available on a first-come, first-serve basis", latitude: 36.604102064574086, longitude: -83.63260934580455},
  {name: "Wilderness Tent Sites", city: "Kula", totalsites: "10", firstcome: "0", reservable: "10", fees: "30.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/hale/planyourvisit/wilderness-camping.htm", description: "There are two primitive wilderness tent areas which are accessible only by trail, Hōlua and Palikū. These sites are only reservable on recreation.gov. No first come-first serve sites are available.", latitude: 20.739517202433813, longitude: -156.21861978744005},
  {name: "Wildrose Campground", city: "Death Valley", totalsites: "23", firstcome: "23", reservable: "0", fees: "0.00", showers: "None", toilets: "Vault Toilets - year round", phone: "No", url: "https://www.nps.gov/deva/planyourvisit/wildrose-campground.htm", description: "First come / first served campground with no additional fee high in the Panamint Mountains. Located at an elevation of 4100 ft / 1250 meters. No hook-ups available. Generators allowed 7am - 7pm. This area is prone to high winds. Sites are dirt and gravel surrounded by Mesquite bushes and rolling hills.", latitude: 36.2659441986, longitude: -117.190333865},
  {name: "Willow Beach Campground (Lake Mohave)", city: "f", totalsites: "9", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://willowbeachharbor.com/willow-beach-campground-and-rv-park/", description: "Willow Beach Campground offers 9 tent campground sites and is located just 30 minutes from Boulder City and 45 minutes from the Las Vegas strip. With its unique location, the park offers scenic views of the Colorado River, Black Canyon, as well as the mountain and desert landscapes in the distance. Enjoy a variety of water activities on Lake Mead and Lake Mohave, hike on the many trails in the recreation area, tour the Hoover Dam, or head to Las Vegas for some nightlife entertainment.", latitude: 35.87336335576787, longitude: -114.6521633431546},
  {name: "Willow Beach RV Park", city: "f", totalsites: "28", firstcome: "0", reservable: "0", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://willowbeachharbor.com/willow-beach-campground-and-rv-park/", description: "Willow Beach RV Park is your full-service RV Park and tent campground located just 30 minutes from Boulder City and 45 minutes from the Las Vegas strip. With its unique location, the park offers scenic views of the Colorado River, Black Canyon, as well as the mountain and desert landscapes in the distance. Enjoy a variety of water activities on Lake Mead and Lake Mohave, hike on the many trails in the recreation area, tour the Hoover Dam, or head to Las Vegas for some nightlife entertainment.", latitude: 35.87336335576787, longitude: -114.65216334315461},
  {name: "Wonder Lake", city: "Denali Park", totalsites: "28", firstcome: "0", reservable: "28", fees: "8.00", showers: "None", toilets: "Flush Toilets - seasonal", phone: "No", url: "https://www.nps.gov/dena/planyourvisit/campground-wonder.htm", description: "Wonder Lake Campground is closed until further notice. Wonder Lake Campground is at mile 85 on the Denali Park Road. It offers 28 tent-only sites. It is the closest campground to Denali. Though separated by nearly 26 miles, the sheer size of the mountain makes it loom impressively over Wonder Lake Campground. Cloudy skies can obscure views, and mosquitoes can be fierce, but Wonder Lake on a clear day offers views that will last a lifetime. Bear-proof food lockers are available throughout the campground.", latitude: 63.45398217678673, longitude: -150.86162174852063},
  {name: "Wood Lake Campground", city: "Houghton", totalsites: "3", firstcome: "3", reservable: "0", fees: "0.00", showers: "None", toilets: "Composting Toilets - seasonal", phone: "No", url: "https://www.nps.gov/isro/planyourvisit/canoe-and-kayak.htm", description: "Wood Lake Campground is located inland on the north shore of Wood Lake. Wood Lake can be accessed via a .6 mile portage from Lake Whittlesey or from Siskiwit Lake. Stay Limit: 2 nights from June 1 – Labor Day annually. Access: Canoe/kayak. No private boat access.", latitude: 48.00853512034862, longitude: -88.73960933467993},
  {name: "Worthington State Forest Campground", city: "Columbia", totalsites: "0", firstcome: "0", reservable: "78", fees: "f", showers: nil, toilets: nil, phone: "", url: "https://nj.gov/dep/parksandforests/parks/worthingtonstateforest.html", description: "The park has 78 tent and trailer sites along two miles of river frontage.  Each site contains a picnic table and a fire ring. Showers, modern toilet facilities and drinking water are available. Sites 1-23 are available for tent camping only and sites 29 -82 are available for tents, travel trailers and motorhomes.", latitude: 41.01296000001025, longitude: -75.08484440122811},
  {name: "Yankee Paradise Wilderness Campsite", city: "f", totalsites: "4", firstcome: "0", reservable: "4", fees: "9.00", showers: "None", toilets: "No Toilets", phone: "Yes - year round", url: "https://www.nps.gov/cuis/planyourvisit/yankeeparadise.htm", description: "Yankee Paradise is one of three campgrounds in Cumberland Island’s designated wilderness on the northern part of the island. The campsite is located 7.5 miles north of the Sea Camp dock. Wilderness sites have no amenities. Fires are prohibited, but camp stoves can be used for cooking. Campers should be prepared to hang all food and trash. Water is available from a nearby well and must be treated. Permits are $9 a night, good for up to 6 people.", latitude: 30.8492, longitude: -81.4478},
  {name: "Yosemite Creek Campground", city: "f", totalsites: "75", firstcome: "0", reservable: "75", fees: "24.00", showers: "None", toilets: "Vault Toilets - seasonal", phone: "No", url: "https://www.nps.gov/yose/planyourvisit/ycreekcamp.htm", description: "Yosemite Creek Campground is located nearly 5 miles off the Tioga Road (via a rough road) 26 miles west of Tuolumne Meadows, and a little over an hour from Yosemite Valley. Located in the forest at 7,700 feet (2,300 m) many campsites are close to Yosemite Creek, which is the only water source (must be filtered, treated, or boiled). RVs and Trailers are not recommended for this harder to access, and more primitive campground. There are no visitor services close to the campground.", latitude: 37.82629400382813, longitude: -119.59833337699251}
])
